import React from 'react';
import { MultiStageFormData, CloudInfrastructure, DatabasesStorage, FileStorageEmail } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CloudInfrastructurePage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.cloudInfrastructure;
  const dbData = formData.databasesStorage;
  const fileEmailData = formData.fileStorageEmail;

  const handleInputChange = (field: keyof CloudInfrastructure, value: string | string[]) => {
    updateFormData('cloudInfrastructure', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof CloudInfrastructure, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleDBInputChange = (field: keyof DatabasesStorage, value: string | string[]) => {
    updateFormData('databasesStorage', { [field]: value });
  };

  const handleDBCheckboxChange = (field: keyof DatabasesStorage, value: string, checked: boolean) => {
    const currentValues = (dbData[field] as string[]) || [];
    if (checked) {
      handleDBInputChange(field, [...currentValues, value]);
    } else {
      handleDBInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleFileEmailInputChange = (field: keyof FileStorageEmail, value: string) => {
    updateFormData('fileStorageEmail', { [field]: value });
  };

  const cloudHosting = [
    'AWS (Amazon Web Services)',
    'DigitalOcean',
    'Fly.io',
    'GCP (Google Cloud Platform)',
    'Heroku',
    'Microsoft Azure',
    'Netlify',
    'Railway',
    'Render',
    'Vercel'
  ];

  const cloudStorage = [
    'Amazon S3 (Simple Storage Service)',
    'Backblaze B2',
    'Cloudflare R2',
    'DigitalOcean Spaces',
    'Google Cloud Storage',
    'Microsoft Azure Blob Storage',
    'MinIO',
    'Wasabi'
  ];

  const serverlessPlatforms = [
    'AWS Lambda',
    'Azure Functions',
    'Cloudflare Workers',
    'Google Cloud Functions',
    'Netlify Functions',
    'Supabase Edge Functions',
    'Vercel Functions'
  ];

  const paasProviders = [
    'App Platform (DigitalOcean)',
    'Clever Cloud',
    'Fly.io',
    'Heroku',
    'Netlify',
    'Platform.sh',
    'Railway',
    'Render',
    'Scalingo',
    'Vercel'
  ];

  const containerPlatforms = [
    'AWS ECS',
    'Azure Container Instances',
    'DigitalOcean App Platform',
    'Docker',
    'Google Cloud Run',
    'Kubernetes (K8s)',
    'Railway',
    'Render'
  ];

  const iaasProviders = [
    'AWS EC2',
    'DigitalOcean Droplets',
    'Google Compute Engine',
    'Hetzner Cloud',
    'Linode',
    'Microsoft Azure Virtual Machines',
    'OVHcloud',
    'Vultr'
  ];

  const cloudDevelopmentEnvironments = [
    'GitHub Codespaces',
    'Gitpod',
    'StackBlitz',
    'CodeSandbox'
  ];

  const containerizationTools = [
    'Docker',
    'Vagrant',
    'Podman',
    'LXC'
  ];

  const databases = [
    'DynamoDB',
    'Elasticsearch',
    'MongoDB',
    'MySQL',
    'PostgreSQL',
    'Redis',
    'SQLite',
    'Supabase'
  ];

  const ormTools = [
    'Drizzle ORM',
    'Mongoose',
    'Prisma',
    'Sequelize',
    'TypeORM'
  ];

  const fileStorageOptions = [
    'Amazon S3 (Simple Storage Service)',
    'Cloudinary',
    'DigitalOcean Spaces',
    'Firebase Storage',
    'Google Cloud Storage',
    'Microsoft Azure Blob Storage',
    'MinIO',
    'Supabase Storage'
  ];

  const emailProviders = [
    'Amazon SES',
    'Brevo (formerly Sendinblue)',
    'ConvertKit',
    'Gmail',
    'Mailchimp',
    'Mailgun',
    'Postmark',
    'Resend',
    'SendGrid'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Cloud & Infrastructure</h2>
        <p className="text-gray-600">Choose your preferred cloud hosting and infrastructure services</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Cloud Hosting */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Cloud Hosting Providers</h3>
        <p className="text-gray-600 mb-4">Select all cloud hosting providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.cloudHosting || []).length === cloudHosting.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('cloudInfrastructure', { cloudHosting: [...cloudHosting] });
                } else {
                  updateFormData('cloudInfrastructure', { cloudHosting: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cloudHosting.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cloudHosting || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('cloudHosting', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Cloud Hosting Provider
          </label>
          <select
            value={data.defaultCloudHosting || ''}
            onChange={(e) => handleInputChange('defaultCloudHosting', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default cloud hosting provider</option>
            {cloudHosting.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Cloud Storage */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Cloud Storage Services</h3>
        <p className="text-gray-600 mb-4">Select all cloud storage services you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.cloudStorage || []).length === cloudStorage.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('cloudInfrastructure', { cloudStorage: [...cloudStorage] });
                } else {
                  updateFormData('cloudInfrastructure', { cloudStorage: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cloudStorage.map((service) => (
            <label key={service} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cloudStorage || []).includes(service)}
                onChange={(e) => handleCheckboxChange('cloudStorage', service, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{service}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Cloud Storage Service
          </label>
          <select
            value={data.defaultCloudStorage || ''}
            onChange={(e) => handleInputChange('defaultCloudStorage', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default cloud storage service</option>
            {cloudStorage.map((service) => (
              <option key={service} value={service}>{service}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Serverless Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Serverless Platforms</h3>
        <p className="text-gray-600 mb-4">Select all serverless platforms you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.serverlessPlatforms || []).length === serverlessPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('cloudInfrastructure', { serverlessPlatforms: [...serverlessPlatforms] });
                } else {
                  updateFormData('cloudInfrastructure', { serverlessPlatforms: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {serverlessPlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.serverlessPlatforms || []).includes(platform)}
                onChange={(e) => handleCheckboxChange('serverlessPlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{platform}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Serverless Platform
          </label>
          <select
            value={data.defaultServerless || ''}
            onChange={(e) => handleInputChange('defaultServerless', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default serverless platform</option>
            {serverlessPlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* PaaS Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Platform as a Service (PaaS) Providers</h3>
        <p className="text-gray-600 mb-4">Select all PaaS providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.paasProviders || []).length === paasProviders.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('cloudInfrastructure', { paasProviders: [...paasProviders] });
                } else {
                  updateFormData('cloudInfrastructure', { paasProviders: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {paasProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.paasProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('paasProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default PaaS Provider
          </label>
          <select
            value={data.defaultPaaS || ''}
            onChange={(e) => handleInputChange('defaultPaaS', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default PaaS provider</option>
            {paasProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Container Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Container Platforms</h3>
        <p className="text-gray-600 mb-4">Select all container platforms you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.containerPlatforms || []).length === containerPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('cloudInfrastructure', { containerPlatforms: [...containerPlatforms] });
                } else {
                  updateFormData('cloudInfrastructure', { containerPlatforms: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {containerPlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.containerPlatforms || []).includes(platform)}
                onChange={(e) => handleCheckboxChange('containerPlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{platform}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Container Platform
          </label>
          <select
            value={data.defaultContainer || ''}
            onChange={(e) => handleInputChange('defaultContainer', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default container platform</option>
            {containerPlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* IaaS Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Infrastructure as a Service (IaaS) Providers</h3>
        <p className="text-gray-600 mb-4">Select all IaaS providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.iaasProviders || []).length === iaasProviders.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('cloudInfrastructure', { iaasProviders: [...iaasProviders] });
                } else {
                  updateFormData('cloudInfrastructure', { iaasProviders: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {iaasProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.iaasProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('iaasProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default IaaS Provider
          </label>
          <select
            value={data.defaultIaaS || ''}
            onChange={(e) => handleInputChange('defaultIaaS', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default IaaS provider</option>
            {iaasProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Cloud Development Environments */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Cloud Development Environments</h3>
        <p className="text-gray-600 mb-4">Select all cloud development environments you&apos;re comfortable with:</p>

