import React from 'react';
import { MultiStageFormData, TechStack, BackendAPI } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function TechStackCombinedPage({ formData, updateFormData }: Props) {
  const techData = formData.techStack;
  const backendData = formData.backendAPI;

  const handleTechInputChange = (field: keyof TechStack, value: string | string[]) => {
    updateFormData('techStack', { [field]: value });
  };

  const handleTechCheckboxChange = (field: keyof TechStack, value: string, checked: boolean) => {
    const currentValues = (techData[field] as string[]) || [];
    if (checked) {
      handleTechInputChange(field, [...currentValues, value]);
    } else {
      handleTechInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleBackendInputChange = (field: keyof BackendAPI, value: string | string[]) => {
    updateFormData('backendAPI', { [field]: value });
  };

  const handleBackendCheckboxChange = (field: keyof BackendAPI, value: string, checked: boolean) => {
    const currentValues = (backendData[field] as string[]) || [];
    if (checked) {
      handleBackendInputChange(field, [...currentValues, value]);
    } else {
      handleBackendInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const programmingLanguages = [
    'C#',
    'Elixir',
    'Go (Golang)',
    'Java',
    'JavaScript (JS)',
    'Kotlin',
    'PHP',
    'Python',
    'Ruby',
    'Rust',
    'TypeScript (TS)'
  ];

  const runtimes = [
    'Bun',
    'Deno',
    'Node.js'
  ];

  const fullStackFrameworks = [
    'Astro',
    'Next.js',
    'Nuxt',
    'Remix',
    'SolidStart',
    'SvelteKit'
  ];

  const frontendFrameworks = [
    'Angular',
    'Preact',
    'React',
    'SolidJS',
    'Svelte',
    'Vue.js'
  ];

  const backendFrameworks = [
    'ASP.NET Core',
    'Django',
    'Echo',
    'Express.js',
    'FastAPI',
    'Fastify',
    'Fiber',
    'Flask',
    'Gin',
    'Laravel',
    'NestJS',
    'Phoenix',
    'Ruby on Rails',
    'Spring Boot'
  ];

  const apiStyles = [
    'GraphQL',
    'gRPC',
    'REST',
    'Server-Sent Events',
    'Webhook',
    'WebSocket'
  ];

  const apiClients = [
    'Apollo Client',
    'Axios',
    'Fetch API',
    'React Query',
    'RTK Query',
    'SWR'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Tech Stack</h2>
        <p className="text-gray-600">Choose your preferred programming languages, frameworks, and API design</p>
      </div>

      {/* Programming Languages */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Programming Languages</h3>
        <p className="text-gray-600 mb-4">Select all programming languages you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(techData.programmingLanguages || []).length === programmingLanguages.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { programmingLanguages: [...programmingLanguages] });
                } else {
                  updateFormData('techStack', { programmingLanguages: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {programmingLanguages.map((language) => (
            <label key={language} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(techData.programmingLanguages || []).includes(language)}
                onChange={(e) => handleTechCheckboxChange('programmingLanguages', language, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{language}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Programming Language
          </label>
          <select
            value={techData.defaultLanguage || ''}
            onChange={(e) => handleTechInputChange('defaultLanguage', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default language</option>
            {programmingLanguages.map((language) => (
              <option key={language} value={language}>{language}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Runtimes */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">JavaScript Runtimes</h3>
        <p className="text-gray-600 mb-4">Select all runtimes you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(techData.runtimes || []).length === runtimes.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { runtimes: [...runtimes] });
                } else {
                  updateFormData('techStack', { runtimes: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {runtimes.map((runtime) => (
            <label key={runtime} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(techData.runtimes || []).includes(runtime)}
                onChange={(e) => handleTechCheckboxChange('runtimes', runtime, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{runtime}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Runtime
          </label>
          <select
            value={techData.defaultRuntime || ''}
            onChange={(e) => handleTechInputChange('defaultRuntime', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default runtime</option>
            {runtimes.map((runtime) => (
              <option key={runtime} value={runtime}>{runtime}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Full-Stack Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Full-Stack & Meta Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all full-stack frameworks you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(techData.fullStackFrameworks || []).length === fullStackFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { fullStackFrameworks: [...fullStackFrameworks] });
                } else {
                  updateFormData('techStack', { fullStackFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {fullStackFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(techData.fullStackFrameworks || []).includes(framework)}
                onChange={(e) => handleTechCheckboxChange('fullStackFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Full-Stack Framework
          </label>
          <select
            value={techData.defaultFullStackFramework || ''}
            onChange={(e) => handleTechInputChange('defaultFullStackFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default framework</option>
            {fullStackFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Frontend Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Frontend Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all frontend frameworks you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(techData.frontendFrameworks || []).length === frontendFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { frontendFrameworks: [...frontendFrameworks] });
                } else {
                  updateFormData('techStack', { frontendFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {frontendFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(techData.frontendFrameworks || []).includes(framework)}
                onChange={(e) => handleTechCheckboxChange('frontendFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Frontend Framework
          </label>
          <select
            value={techData.defaultFrontend || ''}
            onChange={(e) => handleTechInputChange('defaultFrontend', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default framework</option>
            {frontendFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Backend Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Backend Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all backend frameworks you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(backendData.backendFrameworks || []).length === backendFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('backendAPI', { backendFrameworks: [...backendFrameworks] });
                } else {
                  updateFormData('backendAPI', { backendFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {backendFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(backendData.backendFrameworks || []).includes(framework)}
                onChange={(e) => handleBackendCheckboxChange('backendFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Backend Framework
          </label>
          <select
            value={backendData.defaultBackendFramework || ''}
            onChange={(e) => handleBackendInputChange('defaultBackendFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default backend framework</option>
            {backendFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* API Styles */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">API Design Patterns</h3>
        <p className="text-gray-600 mb-4">Select all API styles you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(backendData.apiStyles || []).length === apiStyles.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('backendAPI', { apiStyles: [...apiStyles] });
                } else {
                  updateFormData('backendAPI', { apiStyles: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {apiStyles.map((style) => (
            <label key={style} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(backendData.apiStyles || []).includes(style)}
                onChange={(e) => handleBackendCheckboxChange('apiStyles', style, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{style}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default API Style
          </label>
          <select
            value={backendData.defaultAPIStyle || ''}
            onChange={(e) => handleBackendInputChange('defaultAPIStyle', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default API style</option>
            {apiStyles.map((style) => (
              <option key={style} value={style}>{style}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* API Clients */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">API Client Libraries</h3>
        <p className="text-gray-600 mb-4">Select all API client libraries you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(backendData.apiClients || []).length === apiClients.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('backendAPI', { apiClients: [...apiClients] });
                } else {
                  updateFormData('backendAPI', { apiClients: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {apiClients.map((client) => (
            <label key={client} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(backendData.apiClients || []).includes(client)}
                onChange={(e) => handleBackendCheckboxChange('apiClients', client, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{client}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default API Client
          </label>
          <select
            value={backendData.defaultAPIClient || ''}
            onChange={(e) => handleBackendInputChange('defaultAPIClient', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default API client</option>
            {apiClients.map((client) => (
              <option key={client} value={client}>{client}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

    </div>
  );
}
