import React from 'react';
import { MultiStageFormData, StateManagement, HeadlessCMS, SearchPlatforms } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CMSPage({ formData, updateFormData }: Props) {
  const data = formData.stateManagement;
  const cmsData = formData.headlessCMS;
  const searchData = formData.searchPlatforms;

  const handleInputChange = (field: keyof StateManagement, value: string | string[]) => {
    updateFormData('stateManagement', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof StateManagement, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleCMSInputChange = (field: keyof HeadlessCMS, value: string | string[]) => {
    updateFormData('headlessCMS', { [field]: value });
  };

  const handleSearchInputChange = (field: keyof SearchPlatforms, value: string | string[]) => {
    updateFormData('searchPlatforms', { [field]: value });
  };

  const handleSearchCheckboxChange = (field: keyof SearchPlatforms, value: string, checked: boolean) => {
    const currentValues = (searchData[field] as string[]) || [];
    if (checked) {
      handleSearchInputChange(field, [...currentValues, value]);
    } else {
      handleSearchInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const clientSideState = [
    'Context API',
    'Jotai',
    'MobX',
    'Recoil',
    'Redux',
    'Valtio',
    'XState',
    'Zustand'
  ];

  const dataFetching = [
    'Apollo Client',
    'Axios',
    'Fetch API',
    'React Query',
    'RTK Query',
    'SWR',
    'TanStack Query'
  ];

  const cmsPlatforms = [
    'Contentful',
    'Sanity.io',
    'Storyblok',
    'Strapi'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">CMS</h2>
        <p className="text-gray-600">Choose your preferred content management and state management solutions</p>
      </div>

      {/* Client-Side State Management */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Client-Side State Management</h3>
        <p className="text-gray-600 mb-4">Select all state management libraries you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.clientSideState || []).length === clientSideState.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stateManagement', { clientSideState: [...clientSideState] });
                } else {
                  updateFormData('stateManagement', { clientSideState: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {clientSideState.map((library) => (
            <label key={library} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.clientSideState || []).includes(library)}
                onChange={(e) => handleCheckboxChange('clientSideState', library, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{library}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Client-Side State Library
          </label>
          <select
            value={data.defaultClientState || ''}
            onChange={(e) => handleInputChange('defaultClientState', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default state library</option>
            {clientSideState.map((library) => (
              <option key={library} value={library}>{library}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Data Fetching */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Data Fetching & API Management</h3>
        <p className="text-gray-600 mb-4">Select all data fetching libraries you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.dataFetching || []).length === dataFetching.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stateManagement', { dataFetching: [...dataFetching] });
                } else {
                  updateFormData('stateManagement', { dataFetching: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {dataFetching.map((library) => (
            <label key={library} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.dataFetching || []).includes(library)}
                onChange={(e) => handleCheckboxChange('dataFetching', library, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{library}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Data Fetching Library
          </label>
          <select
            value={data.defaultDataFetching || ''}
            onChange={(e) => handleInputChange('defaultDataFetching', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default data fetching library</option>
            {dataFetching.map((library) => (
              <option key={library} value={library}>{library}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* CMS Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">CMS Platforms</h3>
        <p className="text-gray-600 mb-4">Select all CMS platforms you&apos;re comfortable with:</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(cmsData.cmsPlatforms || []).length === cmsPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('headlessCMS', { cmsPlatforms: [...cmsPlatforms] });
                } else {
                  updateFormData('headlessCMS', { cmsPlatforms: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cmsPlatforms.map((cms) => (
            <label key={cms} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(cmsData.cmsPlatforms || []).includes(cms)}
                onChange={(e) => handleCMSInputChange('cmsPlatforms', [...(cmsData.cmsPlatforms || []).filter(v => v !== cms), ...(e.target.checked ? [cms] : [])])}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{cms}</span>
            </label>
          ))}
        </div>
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default CMS
          </label>
          <select
            value={cmsData.defaultCMS || ''}
            onChange={(e) => handleCMSInputChange('defaultCMS', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default CMS</option>
            {cmsPlatforms.map((cms) => (
              <option key={cms} value={cms}>{cms}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>
    </div>
  );
}
