import React from 'react';
import { MultiStageFormData, DnsConfiguration } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function DnsConfigurationPage({ formData, updateFormData }: Props) {
  const data = formData.dnsConfiguration;

  const handleInputChange = (field: keyof DnsConfiguration, value: string | string[]) => {
    updateFormData('dnsConfiguration', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof DnsConfiguration, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const dnsProviders = [
    'Amazon Route 53',
    'Azure DNS',
    'Cloudflare DNS',
    'DigitalOcean DNS',
    'DNSimple',
    'GoDaddy DNS',
    'Google Cloud DNS',
    'Hover DNS',
    'Namecheap DNS',
    'Porkbun DNS'
  ];

  const cdnProviders = [
    'Akamai',
    'AWS CloudFront',
    'Azure CDN',
    'Bunny CDN',
    'CDN77',
    'Cloudflare',
    'Fastly',
    'Google Cloud CDN',
    'KeyCDN',
    'StackPath'
  ];

  const sslProviders = [
    'Let\'s Encrypt',
    'DigiCert',
    'Sectigo',
    'GlobalSign',
    'Comodo SSL',
    'GeoTrust',
    'Thawte',
    'Entrust',
    'Network Solutions',
    'GoDaddy SSL'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">DNS, CDN & SSL</h2>
        <p className="text-gray-600">Choose your preferred domain, content delivery, and security services</p>
      </div>

      {/* DNS Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">DNS Providers</h3>
        <p className="text-gray-600 mb-4">Select all DNS providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {dnsProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.dnsProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('dnsProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default DNS Provider
          </label>
          <select
            value={data.defaultDNS || ''}
            onChange={(e) => handleInputChange('defaultDNS', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default DNS provider</option>
            {dnsProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* CDN Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">CDN Providers</h3>
        <p className="text-gray-600 mb-4">Select all CDN providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {cdnProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cdnProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('cdnProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default CDN Provider
          </label>
          <select
            value={data.defaultCDN || ''}
            onChange={(e) => handleInputChange('defaultCDN', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default CDN provider</option>
            {cdnProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* SSL Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">SSL Certificate Providers</h3>
        <p className="text-gray-600 mb-4">Select all SSL certificate providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {sslProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.sslProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('sslProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default SSL Provider
          </label>
          <select
            value={data.defaultSSL || ''}
            onChange={(e) => handleInputChange('defaultSSL', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default SSL provider</option>
            {sslProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Service Information */}
      <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
        <h3 className="text-lg font-semibold mb-4 text-blue-900">DNS, CDN & SSL Information</h3>
        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-blue-800">DNS (Domain Name System)</h4>
            <p className="text-blue-700 text-sm">Services that translate domain names into IP addresses, making your website accessible via human-readable URLs.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">CDN (Content Delivery Network)</h4>
            <p className="text-blue-700 text-sm">Networks of servers that deliver content to users based on their geographic location, improving website speed and performance.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">SSL Certificate Providers</h4>
            <p className="text-blue-700 text-sm">Certificate authorities that issue SSL/TLS certificates to enable secure HTTPS connections and protect user data.</p>
          </div>
        </div>
      </div>
    </div>
  );
}
