import React, { useRef } from 'react';
import { MultiStageFormData, AIIntegration } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function MCPServersPage({ formData, updateFormData, skipPage }: Props) {
  const aiData = formData.aiIntegration;
  const deSelectAllRef = useRef<HTMLInputElement>(null);

  const handleAIInputChange = (field: keyof AIIntegration, value: string | string[] | boolean) => {
    updateFormData('aiIntegration', { [field]: value });
  };

  const handleAICheckboxChange = (field: keyof AIIntegration, value: string, checked: boolean) => {
    const currentValues = (aiData[field] as string[]) || [];
    if (checked) {
      handleAIInputChange(field, [...currentValues, value]);
    } else {
      handleAIInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleDeSelectAll = () => {
    updateFormData('aiIntegration', { mcpServers: [] });
    if (deSelectAllRef.current) {
      deSelectAllRef.current.checked = false;
    }
  };

  const mcpServersByCategory = {
    'Core Infrastructure': [
      'Anthropic',
      'AWS',
      'Docker',
      'File System',
      'Git',
      'GitHub',
      'Google Cloud',
      'OpenAI'
    ],
    'Communication & Collaboration': [
      'Confluence',
      'Discord',
      'Mailchimp',
      'Microsoft Teams',
      'Notion',
      'SendGrid',
      'Slack',
      'Twilio',
      'Zoom'
    ],
    'CRM & Business Tools': [
      'HubSpot',
      'Salesforce',
      'WordPress'
    ],
    'Design & Creative': [
      'Figma'
    ],
    'Development & Cloud': [
      'Azure',
      'Cloudflare',
      'DigitalOcean',
      'Firebase',
      'Google Cloud',
      'Heroku',
      'MongoDB',
      'Netlify',
      'PlanetScale',
      'PostgreSQL',
      'Redis',
      'Supabase',
      'Vercel'
    ],
    'Payments & Accounting': [
      'Paddle',
      'Shopify',
      'Stripe'
    ],
    'Productivity & Storage': [
      'Airtable',
      'Dropbox',
      'Google Drive',
      'IFTTT',
      'N8N',
      'Zapier'
    ],
    'Project Management': [
      'Asana',
      'Jira',
      'Linear',
      'Monday.com',
      'Trello'
    ]
  };

  const allServers = Object.values(mcpServersByCategory).flat();

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">MCP Servers</h2>
        <p className="text-gray-600">Choose your preferred MCP servers for AI integration</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Quick Selection Options */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Quick Selection</h3>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.mcpServers || []).includes('Let the AI decide these for me')}
              onChange={(e) => handleAICheckboxChange('mcpServers', 'Let the AI decide these for me', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Let the AI decide these for me</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.mcpServers || []).length === allServers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { mcpServers: [...allServers] });
                } else {
                  updateFormData('aiIntegration', { mcpServers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Select All (50 servers)</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.mcpServers || []).length === 10}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { mcpServers: mcpServersByCategory['Core Infrastructure'] });
                } else {
                  updateFormData('aiIntegration', { mcpServers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Top 10 (Core Infrastructure)</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              ref={deSelectAllRef}
              onChange={handleDeSelectAll}
              className="rounded border-gray-300 text-red-600 focus:ring-red-500"
            />
            <span className="text-red-600 font-medium">De-Select All</span>
          </label>
        </div>
      </div>

      {/* MCP Servers by Category */}
      {Object.entries(mcpServersByCategory).map(([category, servers]) => (
        <div key={category} className="bg-white p-6 rounded-lg border">
          <h3 className="text-lg font-semibold mb-4 text-blue-600">{category}</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
            {servers.map((server, index) => (
              <label key={server} className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(aiData.mcpServers || []).includes(server)}
                  onChange={(e) => handleAICheckboxChange('mcpServers', server, e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">
                  {server}
                  {index < 3 && (
                    <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-green-100 text-green-800">
                      Popular
                    </span>
                  )}
                </span>
              </label>
            ))}
          </div>
        </div>
      ))}

    </div>
  );
}
