import React from 'react';
import { MultiStageFormData } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function EcommercePaymentsPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.ecommercePayments;

  return (
    <div className="space-y-6">
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
        <h3 className="font-semibold text-yellow-800 mb-2">E-commerce, Payments & Accounting</h3>
        <p className="text-yellow-700 text-sm mb-4">
          If you are not selling anything you can skip this page.
        </p>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-yellow-500 text-white rounded-md hover:bg-yellow-600 transition-colors"
        >
          Save and Skip Page
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <h4 className="font-medium text-gray-700 mb-3">E-Commerce Providers</h4>
          <div className="space-y-2">
            {['Shopify', 'WooCommerce', 'BigCommerce', 'Wix eCommerce', 'Squarespace Commerce'].map(provider => (
              <label key={provider} className="flex items-center">
                <input type="checkbox" className="mr-2" />
                {provider}
              </label>
            ))}
          </div>
        </div>

        <div>
          <h4 className="font-medium text-gray-700 mb-3">Payment Providers</h4>
          <div className="space-y-2">
            {['Stripe Payments', 'Paddle', 'PayPal', 'Lemon Squeezy', 'Braintree', 'Adyen'].map(provider => (
              <label key={provider} className="flex items-center">
                <input type="checkbox" className="mr-2" />
                {provider}
              </label>
            ))}
          </div>
        </div>
      </div>

      <div>
        <h4 className="font-medium text-gray-700 mb-3">Payment Plans</h4>
        <div className="space-y-2">
          {[
            'One-Time Sales (e.g., goods, services, digital products)',
            'Usage-Based (e.g., API credits, metered services)',
            'Subscriptions/Memberships (e.g., monthly, annual access)',
            'No'
          ].map(plan => (
            <label key={plan} className="flex items-center">
              <input type="radio" name="paymentPlan" className="mr-2" />
              {plan} {plan === 'No' && '(pre-selected)'}
            </label>
          ))}
        </div>
      </div>
    </div>
  );
}
