import React from 'react';
import { MultiStageFormData, CompanyInfo } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CompanyInfoPage({ formData, updateFormData }: Props) {
  const data = formData.companyInfo;
  // Pre-select English if not set
  if (!data.companyLanguage) {
    updateFormData('companyInfo', { companyLanguage: 'English' });
  }

  const handleInputChange = (field: keyof CompanyInfo, value: string | boolean | File) => {
    updateFormData('companyInfo', { [field]: value });
  };

  const countries = ['US', 'CA', 'UK', 'DE', 'FR', 'AU', 'JP', 'CN', 'IN', 'BR'];
  const timezones = ['UTC-8', 'UTC-7', 'UTC-6', 'UTC-5', 'UTC-4', 'UTC-3', 'UTC', 'UTC+1', 'UTC+2', 'UTC+8', 'UTC+9'];
  const languages = ['English', 'Spanish', 'German', 'Portuguese', 'Chinese', 'French'];
  const skillLevels = ['Beginner', 'Intermediate', 'Expert', 'Rather Not Say'];

  return (
    <div className="space-y-6">
      <div className="bg-blue-50 p-4 rounded-lg">
        <label className="flex items-center">
          <input
            type="checkbox"
            checked={data.usePersonalInfo || false}
            onChange={(e) => handleInputChange('usePersonalInfo', e.target.checked)}
            className="mr-3"
          />
          <span className="font-medium">Use my &apos;Developer Personal &amp; Account Information&apos; from previous page</span>
        </label>
      </div>

      {!data.usePersonalInfo && (
        <>
          <div className="border-t pt-6">
            <h3 className="text-lg font-semibold mb-4 text-gray-700">
              Company Information (if different from personal)
            </h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Company Name *
                </label>
                <input
                  type="text"
                  value={data.companyName || ''}
                  onChange={(e) => handleInputChange('companyName', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Your Company Name"
                  required={!data.usePersonalInfo}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Company Website *
                </label>
                <input
                  type="url"
                  value={data.companyWebsite || ''}
                  onChange={(e) => handleInputChange('companyWebsite', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="https://yourcompany.com"
                  required={!data.usePersonalInfo}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Company Email Address *
                </label>
                <input
                  type="email"
                  value={data.companyEmail || ''}
                  onChange={(e) => handleInputChange('companyEmail', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="contact@yourcompany.com"
                  required={!data.usePersonalInfo}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Company Phone Number *
                </label>
                <input
                  type="tel"
                  value={data.companyPhone || ''}
                  onChange={(e) => handleInputChange('companyPhone', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="+1-555-123-4567"
                  required={!data.usePersonalInfo}
                />
              </div>

              <div className="md:col-span-2">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Company Address *
                </label>
                <textarea
                  value={data.companyAddress || ''}
                  onChange={(e) => handleInputChange('companyAddress', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  rows={3}
                  placeholder="123 Business Ave, Corporate City, CC 12345, Country"
                  required={!data.usePersonalInfo}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Preferred Username *
                </label>
                <input
                  type="text"
                  value={data.companyUsername || ''}
                  onChange={(e) => handleInputChange('companyUsername', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="company_username"
                  required={!data.usePersonalInfo}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Timezone *
                </label>
                <select
                  value={data.companyTimezone || ''}
                  onChange={(e) => handleInputChange('companyTimezone', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required={!data.usePersonalInfo}
                >
                  <option value="">Select timezone</option>
                  {timezones.map(tz => (
                    <option key={tz} value={tz}>{tz}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Country *
                </label>
                <select
                  value={data.companyCountry || ''}
                  onChange={(e) => handleInputChange('companyCountry', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required={!data.usePersonalInfo}
                >
                  <option value="">Select country</option>
                  {countries.map(country => (
                    <option key={country} value={country}>{country}</option>
                  ))}
                </select>
              </div>
            </div>

            <div className="mt-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Language Preference *
              </label>
              <div className="space-y-2">
                {languages.map(lang => (
                  <label key={lang} className="flex items-center">
                    <input
                      type="radio"
                      name="companyLanguage"
                      value={lang}
                      checked={data.companyLanguage === lang || (!data.companyLanguage && lang === 'English')}
                      onChange={(e) => handleInputChange('companyLanguage', e.target.value)}
                      className="mr-2"
                    />
                    {lang}
                  </label>
                ))}
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  GitHub Username
                </label>
                <input
                  type="text"
                  value={data.companyGithub || ''}
                  onChange={(e) => handleInputChange('companyGithub', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="company-github"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  LinkedIn
                </label>
                <input
                  type="url"
                  value={data.companyLinkedin || ''}
                  onChange={(e) => handleInputChange('companyLinkedin', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="https://linkedin.com/company/yourcompany"
                />
              </div>
            </div>

            <div className="mt-6">
              <label className="block text-sm font-medium text-gray-700 mb-3">
                Company Skill Level *
              </label>
              <div className="space-y-2">
                {skillLevels.map(level => (
                  <label key={level} className="flex items-center">
                    <input
                      type="radio"
                      name="companySkillLevel"
                      value={level}
                      checked={data.companySkillLevel === level}
                      onChange={(e) => handleInputChange('companySkillLevel', e.target.value)}
                      className="mr-2"
                    />
                    {level}
                  </label>
                ))}
              </div>
            </div>
          </div>
        </>
      )}

      <div className="border-t pt-6">
        <h3 className="text-lg font-semibold mb-4 text-gray-700">File Uploads</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Company Logo / App Icon
            </label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const file = e.target.files?.[0];
                if (file) handleInputChange('companyLogo', file);
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <p className="text-xs text-gray-500 mt-1">Upload your company logo or app icon</p>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Profile Photo / Avatar
            </label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => {
                const file = e.target.files?.[0];
                if (file) handleInputChange('profilePhoto', file);
              }}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <p className="text-xs text-gray-500 mt-1">Upload a profile photo or avatar</p>
          </div>
        </div>
      </div>
    </div>
  );
}
