import React from 'react';
import { MultiStageFormData, DefaultPreferences } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function DefaultPreferencesPage({ formData, updateFormData }: Props) {
  const data = formData.defaultPreferences;

  const handleInputChange = (field: keyof DefaultPreferences, value: string | string[]) => {
    updateFormData('defaultPreferences', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof DefaultPreferences, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const siteTypes = [
    'AI and Generative Platforms',
    'API and Service Monitoring Platforms',
    'Booking and Reservation Systems',
    'Cloud Computing Platforms',
    'Code Hosting and Collaboration Platforms',
    'Content Management Systems (CMS)',
    'Dynamic Web Apps',
    'Educational and E-Learning Platforms',
    'E-commerce and Marketplace Applications',
    'Financial Services (FinTech) Applications',
    'Health, Fitness, and Telemedicine Platforms',
    'Media Entertainment and Streaming Platforms',
    'Multi-Page Applications (MPAs)',
    'On-Demand Service Applications',
    'Online Design and Editing Tools',
    'Productivity and Collaboration Tools',
    'Progressive Web Apps (PWAs)',
    'Single Page Applications (SPAs)',
    'Social Networking and Media Applications',
    'Software as a Service (SaaS) Platforms',
    'Static Web Apps',
    'Web3 and Decentralized Applications (dApps)'
  ];

  const deviceTypes = ['Mobile', 'Desktops/Laptops', 'Tablets', 'Smart-TVs', 'ALL OF THE ABOVE'];
  const domainRegistrars = ['Cloudflare', 'GoDaddy', 'IONOS', 'Namecheap', 'NO PREFERENCE - all are ok'];
  const cicdTools = ['Azure DevOps', 'CircleCI', 'GitHub Actions', 'GitLab CI/CD', 'Jenkins'];
  const cicdToolsWithNoPreference = ['Azure DevOps', 'CircleCI', 'GitHub Actions', 'GitLab CI/CD', 'Jenkins', 'NO PREFERENCE - all are ok'];

  return (
    <div className="space-y-6">
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Preferred Contact Email * (the email of the App's 'Contact@the-new-app-you-are-developing.com')
        </label>
        <input
          type="email"
          value={data.contactEmail || ''}
          onChange={(e) => handleInputChange('contactEmail', e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="contact@example.com"
          required
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Default App Type - If you make a certain type of app all the time. *
        </label>
        <div className="space-y-2">
          {siteTypes.map(type => (
            <label key={type} className="flex items-center">
              <input
                type="radio"
                name="defaultSiteType"
                value={type}
                checked={data.defaultSiteType === type}
                onChange={(e) => handleInputChange('defaultSiteType', e.target.value)}
                className="mr-2"
              />
              <span className="text-sm">{type}</span>
            </label>
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Device Types Your Apps Should Work On *
        </label>
        <div className="space-y-2">
          {deviceTypes.map(device => (
            <label key={device} className="flex items-center">
              <input
                type="checkbox"
                checked={(data.deviceTypes || []).includes(device)}
                onChange={(e) => handleCheckboxChange('deviceTypes', device, e.target.checked)}
                className="mr-2"
              />
              {device}
            </label>
          ))}
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Hosting Region
          </label>
          <select
            value={data.hostingRegion || ''}
            onChange={(e) => handleInputChange('hostingRegion', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="">Select region</option>
            <option value="us-east-1">US East (N. Virginia)</option>
            <option value="us-west-2">US West (Oregon)</option>
            <option value="eu-west-1">Europe (Ireland)</option>
            <option value="ap-southeast-1">Asia Pacific (Singapore)</option>
            <option value="global">Global</option>
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Starter Repo URL
          </label>
          <input
            type="url"
            value={data.starterRepo || ''}
            onChange={(e) => handleInputChange('starterRepo', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="https://github.com/user/starter-template"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Docker Image
          </label>
          <input
            type="text"
            value={data.dockerImage || ''}
            onChange={(e) => handleInputChange('dockerImage', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="node:18-alpine"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Starter Folder
          </label>
          <input
            type="text"
            value={data.starterFolder || ''}
            onChange={(e) => handleInputChange('starterFolder', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="/home/user/projects/new-app"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Default Domain Registrar *
        </label>
        <div className="space-y-2">
          {domainRegistrars.map(registrar => (
            <label key={registrar} className="flex items-center">
              <input
                type="radio"
                name="domainRegistrar"
                value={registrar}
                checked={data.domainRegistrar === registrar || (!data.domainRegistrar && registrar === 'NO PREFERENCE - all are ok')}
                onChange={(e) => handleInputChange('domainRegistrar', e.target.value)}
                className="mr-2"
              />
              {registrar}
            </label>
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          CI/CD Tools - Choose all you are ok with
        </label>
        <div className="space-y-2">
          {cicdToolsWithNoPreference.map(tool => (
            <label key={tool} className="flex items-center">
              <input
                type="checkbox"
                checked={(data.cicdTools || []).includes(tool) || (!data.cicdTools && tool === 'NO PREFERENCE - all are ok')}
                onChange={(e) => handleCheckboxChange('cicdTools', tool, e.target.checked)}
                className="mr-2"
              />
              {tool}
            </label>
          ))}
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Default CI/CD Tool *
        </label>
        <select
          value={data.defaultCicdTool || ''}
          onChange={(e) => handleInputChange('defaultCicdTool', e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          required
        >
          <option value="">Select CI/CD tool</option>
          {cicdTools.map(tool => (
            <option key={tool} value={tool}>
              {tool}
            </option>
          ))}
        </select>
      </div>
    </div>
  );
}
