import React from 'react';
import { MultiStageFormData, IOTStack } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function IOTStackPage({ formData, updateFormData, skipPage }: Props) {
  const iotData = formData.iotStack;

  const handleIOTInputChange = (field: keyof IOTStack, value: string | string[]) => {
    updateFormData('iotStack', { [field]: value });
  };

  const handleIOTCheckboxChange = (field: keyof IOTStack, value: string, checked: boolean) => {
    const currentValues = (iotData[field] as string[]) || [];
    if (checked) {
      handleIOTInputChange(field, [...currentValues, value]);
    } else {
      handleIOTInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  // Cloud IoT Platforms
  const cloudIOTPlatforms = [
    'AWS IoT Stack',
    'Azure IoT Stack',
    'Google Cloud IoT Stack',
    'IBM Watson IoT Platform',
    'Oracle IoT Cloud Stack',
    'PTC ThingWorx Stack',
    'Siemens Insights Hub (MindSphere)',
    'Particle Platform Stack',
    'Blynk Platform Stack',
    'ThingsBoard Platform Stack'
  ];

  // Edge Computing Platforms
  const edgeComputingPlatforms = [
    'AWS IoT Greengrass',
    'Azure IoT Edge',
    'Google Distributed Cloud Edge',
    'KubeEdge',
    'NVIDIA Jetson',
    'Edge Computing Stack (Generic)',
    'Home Assistant / Open Source Smart Home'
  ];

  // IoT Protocols
  const iotProtocols = [
    'MQTT',
    'CoAP',
    'AMQP',
    'HTTPS',
    'LoRaWAN',
    'Zigbee',
    'Bluetooth Low Energy (BLE)',
    'OPC-UA',
    'HTTP',
    'WebSockets'
  ];

  // Embedded Languages
  const embeddedLanguages = [
    'C/C++',
    'Python/MicroPython',
    'Rust',
    'Java',
    'JavaScript/Node.js',
    'Assembly',
    'FreeRTOS',
    'Zephyr RTOS'
  ];

  // IoT Hardware Platforms
  const iotHardwarePlatforms = [
    'Raspberry Pi',
    'ESP32',
    'STM32',
    'Nordic nRF series',
    'Arduino',
    'Particle Boron/Argon',
    'NVIDIA Jetson',
    'Custom Microcontrollers'
  ];

  // IoT Frameworks
  const iotFrameworks = [
    'Python-Centric Stack (Raspberry Pi/MicroPython)',
    'MQTT + Node.js/JavaScript Stack',
    'Embedded C/C++ Stack (with RTOS)',
    'Java-Centric Stack',
    'Platform-Agnostic (MQTT/CoAP + Open Source)',
    'Rust-Based Embedded Stack',
    'Zigbee/BLE Mesh Stack'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">IOT Stack</h2>
        <p className="text-gray-600">Please choose your preferred IoT platforms, protocols, and frameworks.</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col space-y-3">
        <span className="text-yellow-700 text-sm">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full"
        >
          Save & Skip
        </button>
      </div>

      {/* Patent Information */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Patent Information</h3>
        <p className="text-gray-600 mb-4">If your IoT project was inspired by a specific patent, please provide the patent number:</p>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Patent that inspired you
          </label>
          <input
            type="text"
            value={iotData.patentNumber || ''}
            onChange={(e) => handleIOTInputChange('patentNumber', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
            placeholder="US8354030B1"
          />
        </div>
      </div>

      {/* Cloud IoT Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Cloud IoT Platforms</h3>
        <p className="text-gray-600 mb-4">Select all cloud IoT platforms you're comfortable with:</p>

        <div className="grid grid-cols-1 gap-3 mb-4">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(iotData.cloudIOTPlatforms || []).length === cloudIOTPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  handleIOTInputChange('cloudIOTPlatforms', [...cloudIOTPlatforms]);
                } else {
                  handleIOTInputChange('cloudIOTPlatforms', []);
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cloudIOTPlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(iotData.cloudIOTPlatforms || []).includes(platform)}
                onChange={(e) => handleIOTCheckboxChange('cloudIOTPlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{platform}</span>
            </label>
          ))}
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Cloud IoT Platform
          </label>
          <select
            value={iotData.defaultCloudIOTPlatform || ''}
            onChange={(e) => handleIOTInputChange('defaultCloudIOTPlatform', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default cloud IoT platform</option>
            {cloudIOTPlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Edge Computing Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Edge Computing Platforms</h3>
        <p className="text-gray-600 mb-4">Select all edge computing platforms you plan to use:</p>

        <div className="grid grid-cols-1 gap-3 mb-4">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(iotData.edgeComputingPlatforms || []).length === edgeComputingPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  handleIOTInputChange('edgeComputingPlatforms', [...edgeComputingPlatforms]);
                } else {
                  handleIOTInputChange('edgeComputingPlatforms', []);
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {edgeComputingPlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(iotData.edgeComputingPlatforms || []).includes(platform)}
                onChange={(e) => handleIOTCheckboxChange('edgeComputingPlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{platform}</span>
            </label>
          ))}
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Edge Computing Platform
          </label>
          <select
            value={iotData.defaultEdgeComputingPlatform || ''}
            onChange={(e) => handleIOTInputChange('defaultEdgeComputingPlatform', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default edge computing platform</option>
            {edgeComputingPlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
          </select>
        </div>
      </div>

      {/* IoT Protocols */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">IoT Protocols</h3>
        <p className="text-gray-600 mb-4">Select all IoT communication protocols you plan to use:</p>

        <div className="grid grid-cols-1 gap-3 mb-4">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(iotData.iotProtocols || []).length === iotProtocols.length}
              onChange={(e) => {
                if (e.target.checked) {
                  handleIOTInputChange('iotProtocols', [...iotProtocols]);
                } else {
                  handleIOTInputChange('iotProtocols', []);
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {iotProtocols.map((protocol) => (
            <label key={protocol} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(iotData.iotProtocols || []).includes(protocol)}
                onChange={(e) => handleIOTCheckboxChange('iotProtocols', protocol, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{protocol}</span>
            </label>
          ))}
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default IoT Protocol
          </label>
          <select
            value={iotData.defaultIOTProtocol || ''}
            onChange={(e) => handleIOTInputChange('defaultIOTProtocol', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default IoT protocol</option>
            {iotProtocols.map((protocol) => (
              <option key={protocol} value={protocol}>{protocol}</option>
            ))}
          </select>
        </div>
      </div>

      {/* Embedded Languages */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Embedded Languages</h3>
        <p className="text-gray-600 mb-4">Select all embedded programming languages you're comfortable with:</p>

        <div className="grid grid-cols-1 gap-3 mb-4">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(iotData.embeddedLanguages || []).length === embeddedLanguages.length}
              onChange={(e) => {
                if (e.target.checked) {
                  handleIOTInputChange('embeddedLanguages', [...embeddedLanguages]);
                } else {
                  handleIOTInputChange('embeddedLanguages', []);
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {embeddedLanguages.map((language) => (
            <label key={language} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(iotData.embeddedLanguages || []).includes(language)}
                onChange={(e) => handleIOTCheckboxChange('embeddedLanguages', language, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{language}</span>
            </label>
          ))}
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Embedded Language
          </label>
          <select
            value={iotData.defaultEmbeddedLanguage || ''}
            onChange={(e) => handleIOTInputChange('defaultEmbeddedLanguage', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default embedded language</option>
            {embeddedLanguages.map((language) => (
              <option key={language} value={language}>{language}</option>
            ))}
          </select>
        </div>
      </div>

      {/* IoT Hardware Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">IoT Hardware Platforms</h3>
        <p className="text-gray-600 mb-4">Select all IoT hardware platforms you plan to use:</p>

        <div className="grid grid-cols-1 gap-3 mb-4">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(iotData.iotHardwarePlatforms || []).length === iotHardwarePlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  handleIOTInputChange('iotHardwarePlatforms', [...iotHardwarePlatforms]);
                } else {
                  handleIOTInputChange('iotHardwarePlatforms', []);
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {iotHardwarePlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(iotData.iotHardwarePlatforms || []).includes(platform)}
                onChange={(e) => handleIOTCheckboxChange('iotHardwarePlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{platform}</span>
            </label>
          ))}
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default IoT Hardware Platform
          </label>
          <select
            value={iotData.defaultIOTHardwarePlatform || ''}
            onChange={(e) => handleIOTInputChange('defaultIOTHardwarePlatform', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default IoT hardware platform</option>
            {iotHardwarePlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
          </select>
        </div>
      </div>

      {/* IoT Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">IoT Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all IoT frameworks and stack approaches you're comfortable with:</p>

        <div className="grid grid-cols-1 gap-3 mb-4">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(iotData.iotFrameworks || []).length === iotFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  handleIOTInputChange('iotFrameworks', [...iotFrameworks]);
                } else {
                  handleIOTInputChange('iotFrameworks', []);
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {iotFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(iotData.iotFrameworks || []).includes(framework)}
                onChange={(e) => handleIOTCheckboxChange('iotFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default IoT Framework
          </label>
          <select
            value={iotData.defaultIOTFramework || ''}
            onChange={(e) => handleIOTInputChange('defaultIOTFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default IoT framework</option>
            {iotFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
          </select>
        </div>
      </div>

    </div>
  );
}
