import React from 'react';
import { MultiStageFormData, SecurityAuth } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function SecurityAuthPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.securityAuth;

  const handleInputChange = (field: keyof SecurityAuth, value: string | string[]) => {
    updateFormData('securityAuth', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof SecurityAuth, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const twoFactorAuthOptions = [
    'No 2FA Required',
    'Authenticator App (TOTP)',
    'Biometric Authentication',
    'Email-based 2FA',
    'Hardware Security Keys',
    'SMS-based 2FA'
  ];

  const authMethods = [
    'API Key Authentication',
    'Certificate-based Authentication',
    'JWT (JSON Web Token)',
    'OAuth 2.0',
    'OpenID Connect',
    'Session-based Authentication'
  ];

  const socialLogins = [
    'Apple Sign In',
    'Discord OAuth',
    'Facebook Login',
    'GitHub OAuth',
    'Google OAuth',
    'LinkedIn OAuth',
    'Microsoft OAuth',
    'Slack OAuth',
    'Twitter OAuth'
  ];

  const enterpriseSSO = [
    'Auth0',
    'Azure AD',
    'Keycloak',
    'LDAP/Active Directory',
    'Okta',
    'OneLogin',
    'Ping Identity',
    'SAML 2.0'
  ];

  const sslProviders = [
    'Comodo SSL',
    'DigiCert',
    'Entrust',
    'GeoTrust',
    'GlobalSign',
    'GoDaddy SSL',
    'Let\'s Encrypt',
    'Network Solutions',
    'Sectigo',
    'Thawte'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Security & Authentication</h2>
        <p className="text-gray-600">Choose your preferred authentication and security methods</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Two-Factor Authentication */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Two-Factor Authentication</h3>
        <p className="text-gray-600 mb-4">Select your preferred 2FA method:</p>

        <div className="space-y-3">
          {twoFactorAuthOptions.map((option) => (
            <label key={option} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="twoFactorAuth"
                value={option}
                checked={data.twoFactorAuth === option || (!data.twoFactorAuth && option === 'No 2FA Required')}
                onChange={(e) => handleInputChange('twoFactorAuth', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{option}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Authentication Methods */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Authentication Methods</h3>
        <p className="text-gray-600 mb-4">Select your preferred authentication method:</p>

        <div className="space-y-3">
          {authMethods.map((method) => (
            <label key={method} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="authMethod"
                value={method}
                checked={data.authMethod === method}
                onChange={(e) => handleInputChange('authMethod', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{method}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Social Logins */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Social Login Options</h3>
        <p className="text-gray-600 mb-4">Select all social login providers you want to support:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.socialLogins || []).length === socialLogins.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('securityAuth', { socialLogins: [...socialLogins] });
                } else {
                  updateFormData('securityAuth', { socialLogins: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {socialLogins.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.socialLogins || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('socialLogins', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{provider}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Enterprise SSO */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Enterprise Single Sign-On (SSO)</h3>
        <p className="text-gray-600 mb-4">Select your preferred enterprise SSO solution:</p>

        <div className="space-y-3">
          {enterpriseSSO.map((sso) => (
            <label key={sso} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="enterpriseSSO"
                value={sso}
                checked={data.enterpriseSSO === sso}
                onChange={(e) => handleInputChange('enterpriseSSO', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{sso}</span>
            </label>
          ))}
        </div>
      </div>

      {/* SSL Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">SSL Certificate Providers</h3>
        <p className="text-gray-600 mb-4">Select all SSL certificate providers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.sslProviders || []).length === sslProviders.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('securityAuth', { sslProviders: [...sslProviders] });
                } else {
                  updateFormData('securityAuth', { sslProviders: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {sslProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.sslProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('sslProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default SSL Provider
          </label>
          <select
            value={data.defaultSSL || ''}
            onChange={(e) => handleInputChange('defaultSSL', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default SSL provider</option>
            {sslProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>


    </div>
  );
}
