import React from 'react';
import { MultiStageFormData, CloudInfrastructure, SecurityAuth, DatabasesStorage, FileStorageEmail } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
  goToPage: (pageNumber: number) => void;
}

// Helper to remove AI value if user selects a non-AI value
function removeAIDecision(current: string[] = []) {
  return current.filter(v => v !== 'Let the AI decide');
}

export default function CloudInfrastructurePage({ formData, updateFormData, skipPage, goToPage }: Props) {
  const cloudData = formData.cloudInfrastructure || {};
  const securityData = formData.securityAuth || {};
  const dbData = formData.databasesStorage || {};
  const fileEmailData = formData.fileStorageEmail || {};

  // Update handleCloudInputChange to remove AI if user selects non-AI value
  const handleCloudInputChange = (field: keyof CloudInfrastructure, value: string | string[]) => {
    if (typeof value === 'string' && value !== 'Let the AI decide') {
      updateFormData('cloudInfrastructure', { [field]: value });
    } else if (Array.isArray(value)) {
      updateFormData('cloudInfrastructure', { [field]: value });
    } else {
      updateFormData('cloudInfrastructure', { [field]: value });
    }
  };

  // Update handleSecurityInputChange to remove AI if user selects non-AI value
  const handleSecurityInputChange = (field: keyof SecurityAuth, value: string | string[]) => {
    if (typeof value === 'string' && value !== 'Let the AI decide') {
      updateFormData('securityAuth', { [field]: value });
    } else if (Array.isArray(value)) {
      updateFormData('securityAuth', { [field]: value });
    } else {
      updateFormData('securityAuth', { [field]: value });
    }
  };

  const handleDBCheckboxChange = (field: keyof DatabasesStorage, value: string, checked: boolean) => {
    const currentValues = (dbData[field] as string[]) || [];
    if (checked) {
      // Remove 'Let the AI decide' if user selects a non-AI option
      const filteredValues = currentValues.filter(v => v !== 'Let the AI decide');
      updateFormData('databasesStorage', { [field]: [...filteredValues, value] });
    } else {
      updateFormData('databasesStorage', { [field]: currentValues.filter(v => v !== value) });
    }
  };

  // Update handleFileEmailInputChange to remove AI if user selects non-AI value
  const handleFileEmailInputChange = (field: keyof FileStorageEmail, value: string) => {
    updateFormData('fileStorageEmail', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof CloudInfrastructure, value: string, checked: boolean) => {
    const currentValues = (cloudData[field] as string[]) || [];
    if (checked) {
      // Remove 'Let the AI decide' if user selects a non-AI option
      const filteredValues = currentValues.filter(v => v !== 'Let the AI decide');
      updateFormData('cloudInfrastructure', { [field]: [...filteredValues, value] });
    } else {
      updateFormData('cloudInfrastructure', { [field]: currentValues.filter(v => v !== value) });
    }
  };

  const handleSecurityCheckboxChange = (field: keyof SecurityAuth, value: string, checked: boolean) => {
    const currentValues = (securityData[field] as string[]) || [];
    if (checked) {
      // Remove 'Let the AI decide' if user selects a non-AI option
      const filteredValues = currentValues.filter(v => v !== 'Let the AI decide');
      updateFormData('securityAuth', { [field]: [...filteredValues, value] });
    } else {
      updateFormData('securityAuth', { [field]: currentValues.filter(v => v !== value) });
    }
  };

  // AI Decision handlers for each section
  const handleAICloudCheckboxChange = (field: keyof CloudInfrastructure, value: string, checked: boolean) => {
    if (checked) {
      // When "Let the AI decide" is checked, clear all other selections and only keep "Let the AI decide"
      handleCloudInputChange(field, ['Let the AI decide']);
    } else {
      // When unchecked, clear the "Let the AI decide" option
      handleCloudInputChange(field, []);
    }
  };

  const handleAISecurityCheckboxChange = (field: keyof SecurityAuth, value: string, checked: boolean) => {
    if (checked) {
      // When "Let the AI decide" is checked, clear all other selections and only keep "Let the AI decide"
      updateFormData('securityAuth', { [field]: ['Let the AI decide'] });
    } else {
      // When unchecked, clear the "Let the AI decide" option
      updateFormData('securityAuth', { [field]: [] });
    }
  };

  const handleAIAuthMethodChange = (checked: boolean) => {
    if (checked) {
      handleSecurityInputChange('authMethod', 'Let the AI decide');
    } else {
      handleSecurityInputChange('authMethod', '');
    }
  };

  const handleAIDBCheckboxChange = (field: keyof DatabasesStorage, value: string, checked: boolean) => {
    if (checked) {
      // When "Let the AI decide" is checked, clear all other selections and only keep "Let the AI decide"
      updateFormData('databasesStorage', { [field]: ['Let the AI decide'] });
    } else {
      // When unchecked, clear the "Let the AI decide" option
      updateFormData('databasesStorage', { [field]: [] });
    }
  };

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Cloud & Security</h2>
        <p className="text-gray-600">Configure your cloud infrastructure, hosting, and security preferences</p>
      </div>

      {/* Skip Option */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 flex flex-col space-y-3">
        <span className="text-blue-700 text-sm">This section is optional. If it does not apply, you can continue to the next one.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-gray-100 text-gray-700 border border-gray-300 rounded-md hover:bg-gray-200 transition-colors w-full"
        >
          Skip Section
        </button>
        <div className="h-2"></div>
        <button
          onClick={() => goToPage(16)}
          className="px-4 py-2 bg-green-100 text-green-700 border border-green-300 rounded-md hover:bg-green-200 transition-colors w-full"
        >
          Skip to Review & Submit Page
        </button>
      </div>

      {/* Section 1: Hosting & Deployment Model */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Hosting & Deployment Model</h3>
        <p className="text-gray-600 mb-6">Select your preferred hosting and deployment models</p>

        {/* Hosting & Deployment Model */}
        <div className="mb-6">

          {/* IaaS */}
          <div className="mb-8">
            <h4 className="text-md font-semibold text-blue-600 mb-3">IaaS (Virtual Machines)</h4>

            {/* AI Decision Checkbox with separation */}
            <div className="border-b border-gray-200 pb-4 mb-4">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={cloudData.defaultIaaS === 'Let the AI decide'}
                  onChange={(e) => handleCloudInputChange('defaultIaaS', e.target.checked ? 'Let the AI decide' : '')}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-purple-600 font-medium">Let the AI decide</span>
              </label>
            </div>

            <div className="space-y-2">
              {['All are OK', 'AWS EC2', 'Google Compute Engine', 'Azure VMs', 'DigitalOcean Droplets', 'Linode', 'Vultr'].map((option) => (
