import MobileDesktopPage from './pages/MobileDesktopPage';

const FORM_PAGES: FormPageConfig[] = [
  { id: 1, title: '🧑‍💻 Developer Information', description: 'Personal developer details', required: true, canSkip: false },
  { id: 2, title: '🏢 Company Information', description: 'Business or organization details', required: true, canSkip: false },
  { id: 3, title: '📱 App Configuration', description: 'App-specific settings and target audience', required: true, canSkip: false },
  { id: 4, title: '🎨 Styling & Design', description: 'UI/UX and design preferences', required: true, canSkip: false },
  { id: 5, title: '⚙️ Tech Stack', description: 'Programming Languages and Frameworks', required: true, canSkip: false },
  { id: 6, title: '🔒 Security & Authentication', description: 'Authentication and security preferences', required: true, canSkip: false },
  { id: 7, title: '💳 E-commerce & Payments', description: 'Payment and commerce setup', required: false, canSkip: true },
  { id: 8, title: '📁 File Storage & Email', description: 'Storage and communication services', required: true, canSkip: false },
  { id: 9, title: '📦 Packages & Builds', description: 'Package management and build tools', required: true, canSkip: false },

  { id: 10, title: '📡 APIs', description: 'Communication and integration services', required: false, canSkip: true },
  { id: 11, title: '📝 CMS & Search', description: 'Content management, state management, and search solutions', required: false, canSkip: true },

  { id: 12, title: '🗄️ Databases & Storage', description: 'Data storage and management', required: true, canSkip: false },
  { id: 13, title: '☁️ Cloud & Infrastructure', description: 'Hosting and deployment preferences', required: true, canSkip: false },
  { id: 14, title: '✅ Testing & Code Quality', description: 'Testing frameworks and quality tools', required: true, canSkip: false },
  { id: 15, title: '📊 Analytics & Monitoring', description: 'Performance and user analytics', required: false, canSkip: true },
  { id: 16, title: '💰 Financial APIs', description: 'Banking and investment services', required: false, canSkip: true },
  { id: 17, title: '🤖 AI & MCPs', description: 'AI services and tools setup', required: false, canSkip: true },
  { id: 18, title: '📱 Mobile & Desktop', description: 'Cross-platform development', required: false, canSkip: true }
];

const STORAGE_KEY = 'multiStageFormData';

export default function MultiStageForm() {
  const scrollContainerRef = useRef<HTMLDivElement>(null);
  const [formData, setFormData] = useState<MultiStageFormData>(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        return {
          ...parsed,
          completedPages: new Set(parsed.completedPages || []),
          appConfiguration: {
            targetAgeRanges: ['All Ages'],
            targetGender: ['All Genders'],
            targetAudience: ['Everyone'],
            ...parsed.appConfiguration
          },
          backendAPI: {
            backendFrameworks: [],
            defaultBackendFramework: '',
            apiStyles: [],
            defaultAPIStyle: '',
            apiClients: [],
            defaultAPIClient: ''
          }
        };
      } catch (error) {
        console.error('Error parsing saved form data:', error);
      }
    }

    return {
      currentPage: 1,
      completedPages: new Set<number>(),
      developerInfo: {
        fullName: '',
        email: '',
        streetAddress: '',
        city: '',
        stateProvince: '',
        postalCode: '',
        country: '',
        phoneNumber: '',
        countryCode: '+1',
        username: '',
        timezone: '',
        language: 'English',
        skillLevel: 'Rather Not Say'
      },
      companyInfo: {},
      defaultPreferences: {},
      versionControl: {},
      developmentEnvironment: {},
      packageManagement: {},
      buildBundling: {},
      ecommercePayments: {},
      fileStorageEmail: {},
      mapsGeolocation: {},
      securityAuth: {
        twoFactorAuth: 'No 2FA Required'
      },
      communicationAPIs: {},
      dnsConfiguration: {},
      techStack: {},
      stateManagement: {},
      backendAPI: {
        backendFrameworks: [],
        defaultBackendFramework: '',
        apiStyles: [],
        defaultAPIStyle: '',
        apiClients: [],
        defaultAPIClient: ''
      },
      headlessCMS: {},
      searchPlatforms: {},
      databasesStorage: {},
      cloudInfrastructure: {},
      stylingDesign: {},
      testingCodeQuality: {},
      deploymentCICD: {},
      analyticsMonitoring: {},
      financialAPIs: {},
      aiIntegration: {},
      mobileDesktop: {},
                appConfiguration: {
            targetAgeRanges: ['All Ages'],
            targetGender: ['All Genders'],
            targetAudience: ['Everyone']
          }
    };
  });

  // Add state for showing the clear confirmation modal
  const [showClearConfirm, setShowClearConfirm] = useState(false);

  useEffect(() => {
    const dataToSave = {
      ...formData,
      completedPages: Array.from(formData.completedPages)
    };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(dataToSave));
  }, [formData]);

  const updateFormData = <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => {
    setFormData(prev => ({
      ...prev,
      [section]: { ...(prev[section] as object), ...data }
    }));
  };

  const goToPage = (pageNumber: number) => {
    if (pageNumber >= 1 && pageNumber <= FORM_PAGES.length) {
      setFormData(prev => ({ ...prev, currentPage: pageNumber }));
    }
  };

  // Function to center the current page button in the scroll container
  const centerCurrentPage = () => {
    if (scrollContainerRef.current) {
      const container = scrollContainerRef.current;
      const currentPageButton = container.querySelector(`[data-page="${formData.currentPage}"]`) as HTMLElement;

      if (currentPageButton) {
        const containerWidth = container.offsetWidth;
        const buttonLeft = currentPageButton.offsetLeft;
        const buttonWidth = currentPageButton.offsetWidth;

        // Calculate the scroll position to center the button
        const scrollLeft = buttonLeft - (containerWidth / 2) + (buttonWidth / 2);

        container.scrollTo({
          left: Math.max(0, scrollLeft),
          behavior: 'smooth'
        });
      }
    }
  };

  // Center the current page when it changes
  useEffect(() => {
    centerCurrentPage();
  }, [formData.currentPage]);

  const nextPage = () => {
    setFormData(prev => {
      const newCompletedPages = new Set(prev.completedPages);
      newCompletedPages.add(prev.currentPage);
      return {
        ...prev,
        completedPages: newCompletedPages,
        currentPage: Math.min(prev.currentPage + 1, FORM_PAGES.length)
      };
    });
  };

  const previousPage = () => {
    setFormData(prev => ({
      ...prev,
      currentPage: Math.max(prev.currentPage - 1, 1)
    }));
  };

  const skipPage = () => {
    setFormData(prev => {
      const newCompletedPages = new Set(prev.completedPages);
      newCompletedPages.add(prev.currentPage);
      return {
        ...prev,
        completedPages: newCompletedPages,
        currentPage: Math.min(prev.currentPage + 1, FORM_PAGES.length)
      };
    });
  };

  const validateRequiredFields = () => {
    // Basic validation for required fields
    const { developerInfo } = formData;
    const missingFields = [];

    if (!developerInfo.fullName) missingFields.push('Full Name');
    if (!developerInfo.email) missingFields.push('Email');
    if (!developerInfo.streetAddress) missingFields.push('Street Address');
    if (!developerInfo.city) missingFields.push('City');
    if (!developerInfo.stateProvince) missingFields.push('State/Province');
    if (!developerInfo.postalCode) missingFields.push('Postal Code');
    if (!developerInfo.country) missingFields.push('Country');
    if (!developerInfo.phoneNumber) missingFields.push('Phone Number');
    if (!developerInfo.username) missingFields.push('Username');
    if (!developerInfo.timezone) missingFields.push('Timezone');
    if (!developerInfo.country) missingFields.push('Country');
    if (!developerInfo.language) missingFields.push('Language');
    if (!developerInfo.skillLevel) missingFields.push('Skill Level');

    return missingFields;
  };

  const completeSetup = () => {
    // Validate required fields
    const missingFields = validateRequiredFields();
    if (missingFields.length > 0) {
      alert(`⚠️ Please complete the following required fields before submitting:\n\n${missingFields.join('\n')}\n\nGo to Page 1 (Developer Info) to complete these fields.`);
      return;
    }

    // Mark final page as completed
    setFormData(prev => {
      const newCompletedPages = new Set(prev.completedPages);
      newCompletedPages.add(prev.currentPage);
      return {
        ...prev,
        completedPages: newCompletedPages
      };
    });

    // Prepare configuration data for webhook
    const configData = {
      generatedAt: new Date().toISOString(),
      formVersion: '1.0.0',
      totalPages: FORM_PAGES.length,
      completedPages: Array.from(formData.completedPages).length + 1,
      configuration: {
        developerInfo: formData.developerInfo,
        companyInfo: formData.companyInfo,
        defaultPreferences: formData.defaultPreferences,
        versionControl: formData.versionControl,
        developmentEnvironment: formData.developmentEnvironment,
        packageManagement: formData.packageManagement,
        buildBundling: formData.buildBundling,
        ecommercePayments: formData.ecommercePayments,
        fileStorageEmail: formData.fileStorageEmail,
        mapsGeolocation: formData.mapsGeolocation,
        securityAuth: formData.securityAuth,
        communicationAPIs: formData.communicationAPIs,
        techStack: formData.techStack,
        stateManagement: formData.stateManagement,
        backendAPI: formData.backendAPI,
        headlessCMS: formData.headlessCMS,
        searchPlatforms: formData.searchPlatforms,
        databasesStorage: formData.databasesStorage,
        cloudInfrastructure: formData.cloudInfrastructure,
        stylingDesign: formData.stylingDesign,
        testingCodeQuality: formData.testingCodeQuality,
        deploymentCICD: formData.deploymentCICD,
        analyticsMonitoring: formData.analyticsMonitoring,
        financialAPIs: formData.financialAPIs,
        aiIntegration: formData.aiIntegration,
        mobileDesktop: formData.mobileDesktop,
        appConfiguration: formData.appConfiguration
      }
    };

    // Submit to webhook
    const submitToWebhook = async () => {
      try {
        const response = await fetch('https://encinitaseyes.app.n8n.cloud/form/0615e0eb-d7cf-4ddb-8ee4-f31e7698be22', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(configData)
        });

        if (response.ok) {
          const result = await response.json();
          console.log('Webhook response:', result);

          // Show success message with summary
          const completedCount = Array.from(formData.completedPages).length + 1;
          const summary = `🎉 Setup Complete!\n\n` +
            `✅ Completed ${completedCount} of ${FORM_PAGES.length} pages\n` +
            `📡 Configuration successfully sent to webhook\n` +
            `🚀 Your project setup data has been processed!\n\n` +
            `Server Response: ${result.message || 'Configuration received successfully'}`;

          alert(summary);
        } else {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
      } catch (error) {
        console.error('Webhook submission failed:', error);

        const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
        alert('Webhook submission failed: ' + errorMessage);
      }
    };

    submitToWebhook();
  };

  const renderPageContent = () => {
    const currentPageConfig = FORM_PAGES.find(page => page.id === formData.currentPage);
    if (!currentPageConfig) return null;

    switch (currentPageConfig.id) {
      case 1:
        return <DeveloperInfoPage formData={formData} updateFormData={updateFormData} />;
      case 2:
        return <CompanyInfoPage formData={formData} updateFormData={updateFormData} />;
      case 3:
        return <AppConfigurationPage formData={formData} updateFormData={updateFormData} />;
      case 4:
        return <StylingDesignPage formData={formData} updateFormData={updateFormData} />;
      case 5:
        return <TechStackCombinedPage formData={formData} updateFormData={updateFormData} />;
      case 6:
        return <SecurityAuthPage formData={formData} updateFormData={updateFormData} />;
      case 7:
        return <EcommercePaymentsPage formData={formData} updateFormData={updateFormData} />;
      case 8:
        return <FileStorageEmailPage formData={formData} updateFormData={updateFormData} />;
      case 9:
        return <PackagesBuildsPage formData={formData} updateFormData={updateFormData} />;
      case 10:
        return <CommunicationAPIsPage formData={formData} updateFormData={updateFormData} />;
      case 11:
        return <CMSPage formData={formData} updateFormData={updateFormData} />;
      case 12:
        return <DatabasesStoragePage formData={formData} updateFormData={updateFormData} />;
      case 13:
        return <CloudInfrastructurePage formData={formData} updateFormData={updateFormData} />;
      case 14:
        return <TestingCodeQualityPage formData={formData} updateFormData={updateFormData} />;
      case 15:
        return <AnalyticsMonitoringPage formData={formData} updateFormData={updateFormData} />;
      case 16:
        return <FinancialAPIsPage formData={formData} updateFormData={updateFormData} />;
      case 17:
        return <AIIntegrationPage formData={formData} updateFormData={updateFormData} />;
      case 18:
        return <MobileDesktopPage formData={formData} updateFormData={updateFormData} />;
      default:
        return null;
    }
  };

  return (
    <div className="multi-stage-form-container">
      <div className="form-header">
        <h1>Project Setup Wizard</h1>
        <p>Complete the following steps to configure your project.</p>
      </div>

      <div className="form-progress-bar">
        {FORM_PAGES.map(page => (
          <div
            key={page.id}
            data-page={page.id}
            className={`progress-step ${formData.completedPages.has(page.id) ? 'completed' : ''}`}
            onClick={() => goToPage(page.id)}
          >
            <span>{page.title}</span>
            {page.required && !formData.completedPages.has(page.id) && (
              <span className="required-indicator">*</span>
            )}
          </div>
        ))}
      </div>

      <div className="form-content" ref={scrollContainerRef}>
        {renderPageContent()}
      </div>

      <div className="form-navigation">
        <button onClick={previousPage} disabled={formData.currentPage === 1}>Previous</button>
        <button onClick={nextPage} disabled={formData.currentPage === FORM_PAGES.length}>
          {formData.currentPage === FORM_PAGES.length ? 'Complete Setup' : 'Next'}
        </button>
        {formData.currentPage < FORM_PAGES.length && (
          <button onClick={skipPage} disabled={formData.completedPages.has(formData.currentPage)}>
            Skip
          </button>
        )}
      </div>

      {showClearConfirm && (
        <div className="clear-confirmation-modal">
          <p>Are you sure you want to clear all your progress and start over?</p>
          <button onClick={() => {
            localStorage.removeItem(STORAGE_KEY);
            setFormData({
              currentPage: 1,
              completedPages: new Set<number>(),
              developerInfo: {
                fullName: '',
                email: '',
                streetAddress: '',
                city: '',
                stateProvince: '',
                postalCode: '',
                country: '',
                phoneNumber: '',
                countryCode: '+1',
                username: '',
                timezone: '',
                language: 'English',
                skillLevel: 'Rather Not Say'
              },
              companyInfo: {},
              defaultPreferences: {},
              versionControl: {},
              developmentEnvironment: {},
              packageManagement: {},
              buildBundling: {},
              ecommercePayments: {},
              fileStorageEmail: {},
              mapsGeolocation: {},
              securityAuth: {
                twoFactorAuth: 'No 2FA Required'
              },
              communicationAPIs: {},
              dnsConfiguration: {},
              techStack: {},
              stateManagement: {},
              backendAPI: {
                backendFrameworks: [],
                defaultBackendFramework: '',
                apiStyles: [],
                defaultAPIStyle: '',
                apiClients: [],
                defaultAPIClient: ''
              },
              headlessCMS: {},
              searchPlatforms: {},
              databasesStorage: {},
              cloudInfrastructure: {},
              stylingDesign: {},
              testingCodeQuality: {},
              deploymentCICD: {},
              analyticsMonitoring: {},
              financialAPIs: {},
              aiIntegration: {},
              mobileDesktop: {},
              appConfiguration: {
                targetAgeRanges: ['All Ages'],
                targetGender: ['All Genders'],
                targetAudience: ['Everyone']
              }
            });
            setShowClearConfirm(false);
          }}>Clear All Progress</button>
          <button onClick={() => setShowClearConfirm(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
}
