import React from 'react';
import { MultiStageFormData, MobileDesktop } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function MobileDesktopPage({ formData, updateFormData }: Props) {
  const data = formData.mobileDesktop;

  const handleInputChange = (field: keyof MobileDesktop, value: string | string[]) => {
    updateFormData('mobileDesktop', { [field]: value });
  };

  const mobileFrameworks = [
    'Capacitor',
    'Ionic',
    'Progressive Web App (PWA)',
    'React Native'
  ];

  const desktopFrameworks = [
    'Electron',
    'Flutter',
    'Tauri'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Mobile / Desktop</h2>
        <p className="text-gray-600">Choose your preferred cross-platform development frameworks</p>
      </div>
      {/* Mobile Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Mobile Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all mobile frameworks you&apos;re comfortable with:</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.mobileFrameworks || []).length === mobileFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('mobileDesktop', { mobileFrameworks: [...mobileFrameworks] });
                } else {
                  updateFormData('mobileDesktop', { mobileFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {mobileFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.mobileFrameworks || []).includes(framework)}
                onChange={(e) => handleInputChange('mobileFrameworks', [...(data.mobileFrameworks || []).filter(v => v !== framework), ...(e.target.checked ? [framework] : [])])}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{framework}</span>
            </label>
          ))}
        </div>
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Mobile Framework
          </label>
          <select
            value={data.defaultMobileFramework || ''}
            onChange={(e) => handleInputChange('defaultMobileFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default mobile framework</option>
            {mobileFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>
      {/* Desktop Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Desktop Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all desktop frameworks you&apos;re comfortable with:</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.desktopFrameworks || []).length === desktopFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('mobileDesktop', { desktopFrameworks: [...desktopFrameworks] });
                } else {
                  updateFormData('mobileDesktop', { desktopFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {desktopFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.desktopFrameworks || []).includes(framework)}
                onChange={(e) => handleInputChange('desktopFrameworks', [...(data.desktopFrameworks || []).filter(v => v !== framework), ...(e.target.checked ? [framework] : [])])}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{framework}</span>
            </label>
          ))}
        </div>
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Desktop Framework
          </label>
          <select
            value={data.defaultDesktopFramework || ''}
            onChange={(e) => handleInputChange('defaultDesktopFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default desktop framework</option>
            {desktopFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>
    </div>
  );
}
