import React from 'react';
import { MultiStageFormData, BuildBundling } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function BuildBundlingPage({ formData, updateFormData }: Props) {
  const data = formData.buildBundling;

  const handleInputChange = (field: keyof BuildBundling, value: string | string[]) => {
    updateFormData('buildBundling', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof BuildBundling, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const bundlers = [
    'esbuild',
    'Parcel',
    'Rollup',
    'SWC',
    'Turbopack',
    'Vite',
    'Webpack'
  ];

  const transpilers = [
    'Babel',
    'esbuild',
    'SWC',
    'TypeScript Compiler'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Build & Compile</h2>
        <p className="text-gray-600">Choose your preferred build tools and transpilers</p>
      </div>

      {/* Bundlers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Build Tools & Bundlers</h3>
        <p className="text-gray-600 mb-4">Select all bundlers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.bundlers || []).length === bundlers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('buildBundling', { bundlers: [...bundlers] });
                } else {
                  updateFormData('buildBundling', { bundlers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {bundlers.map((bundler) => (
            <label key={bundler} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.bundlers || []).includes(bundler)}
                onChange={(e) => handleCheckboxChange('bundlers', bundler, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{bundler}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Bundler
          </label>
          <select
            value={data.defaultBundler || ''}
            onChange={(e) => handleInputChange('defaultBundler', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default bundler</option>
            {bundlers.map((bundler) => (
              <option key={bundler} value={bundler}>{bundler}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Transpilers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Transpilers & Compilers</h3>
        <p className="text-gray-600 mb-4">Select all transpilers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.transpilers || []).length === transpilers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('buildBundling', { transpilers: [...transpilers] });
                } else {
                  updateFormData('buildBundling', { transpilers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {transpilers.map((transpiler) => (
            <label key={transpiler} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.transpilers || []).includes(transpiler)}
                onChange={(e) => handleCheckboxChange('transpilers', transpiler, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{transpiler}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Transpiler
          </label>
          <select
            value={data.defaultTranspiler || ''}
            onChange={(e) => handleInputChange('defaultTranspiler', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default transpiler</option>
            {transpilers.map((transpiler) => (
              <option key={transpiler} value={transpiler}>{transpiler}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>


    </div>
  );
}
