import React from 'react';
import { MultiStageFormData, AppConfiguration } from '../../types/FormTypes';
import naicsFlat from '../../data/naics_flat.json';
import { useState } from 'react';

// Type for NAICS JSON
interface NationalIndustry {
  name: string;
}
interface NaicsIndustry {
  name: string;
  nationalIndustries: Record<string, NationalIndustry>;
}
interface Industry {
  name: string;
  naicsIndustries: Record<string, NaicsIndustry>;
}
interface Subsector {
  name: string;
  industries: Record<string, Industry>;
}
interface Sector {
  name: string;
  subsectors: Record<string, Subsector>;
}
interface NaicsData {
  [sector: string]: Sector;
}
const naicsData = naicsDataRaw as NaicsData;

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function AppConfigurationPage({ formData, updateFormData }: Props) {
  const data = formData.appConfiguration;

  const handleInputChange = (field: keyof AppConfiguration, value: any) => {
    updateFormData('appConfiguration', { [field]: value });
  };

  const handleArrayChange = (field: keyof AppConfiguration, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const targetAudience = [
    'General Public',
    'Businesses/Enterprise',
    'Developers',
    'Students',
    'Educators',
    'Healthcare Professionals',
    'Financial Services',
    'E-commerce',
    'Gaming',
    'Social Media',
    'Content Creators',
    'Non-profit Organizations'
  ];

  const targetAgeRanges = [
    'Children (0-12)',
    'Teenagers (13-17)',
    'Young Adults (18-24)',
    'Adults (25-34)',
    'Adults (35-44)',
    'Adults (45-54)',
    'Adults (55-64)',
    'Seniors (65+)',
    'All Ages'
  ];

  const targetGender = [
    'Male',
    'Female',
    'Non-binary',
    'All Genders'
  ];

  const languagesServed = [
    'English',
    'Spanish',
    'French',
    'German',
    'Italian',
    'Portuguese',
    'Russian',
    'Chinese (Simplified)',
    'Chinese (Traditional)',
    'Japanese',
    'Korean',
    'Arabic',
    'Hindi',
    'Other'
  ];

  const techSavviness = [
    'Beginner',
    'Intermediate',
    'Advanced',
    'Expert',
    'Mixed Levels'
  ];

  const educationLevel = [
    'High School',
    'Some College',
    'Bachelor\'s Degree',
    'Master\'s Degree',
    'PhD/Doctorate',
    'Technical Certification',
    'Self-taught',
    'Mixed Levels'
  ];

  // Single NAICS dropdown
  const handleNaicsChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    updateFormData('appConfiguration', { sector: e.target.value });
  };

  const occupations = [
    'Software Developer',
    'Designer',
    'Product Manager',
    'Business Analyst',
    'Student',
    'Entrepreneur',
    'Healthcare Professional',
    'Educator',
    'Marketing Professional',
    'Sales Professional',
    'Executive/Manager',
    'Other'
  ];

  const incomeRange = [
    'Under $25,000',
    '$25,000 - $50,000',
    '$50,000 - $75,000',
    '$75,000 - $100,000',
    '$100,000 - $150,000',
    '$150,000 - $200,000',
    'Over $200,000',
    'Mixed Ranges'
  ];

  const companySize = [
    '1-10 employees',
    '11-50 employees',
    '51-200 employees',
    '201-500 employees',
    '501-1000 employees',
    '1000+ employees',
    'Mixed Sizes'
  ];

  const projectBudget = [
    'Under $5,000',
    '$5,000 - $10,000',
    '$10,000 - $25,000',
    '$25,000 - $50,000',
    '$50,000 - $100,000',
    '$100,000 - $250,000',
    'Over $250,000',
    'Not specified'
  ];

  const licenseType = [
    'Open Source (MIT)',
    'Open Source (GPL)',
    'Open Source (Apache)',
    'Proprietary',
    'Freemium',
    'Subscription',
    'One-time Purchase',
    'Not specified'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">App Configuration</h2>
        <p className="text-gray-600">Configure your application settings and target audience</p>
      </div>

      {/* Basic App Information */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Basic App Information</h3>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App Name
            </label>
            <input
              type="text"
              value={data.appName || ''}
              onChange={(e) => handleInputChange('appName', e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              placeholder="Enter your app name"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App Description
            </label>
            <textarea
              value={data.appDescription || ''}
              onChange={(e) => handleInputChange('appDescription', e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              placeholder="Describe your app, its features, and purpose..."
              rows={4}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App or URL that inspired you
            </label>
            <input
              type="url"
              value={data.inspirationUrl || ''}
              onChange={(e) => handleInputChange('inspirationUrl', e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              placeholder="https://example.com/inspiration"
            />
          </div>
        </div>
      </div>

      {/* Target Audience */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Target Audience</h3>
        <p className="text-gray-600 mb-4">Select all target audiences for your app:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {targetAudience.map((audience) => (
            <label key={audience} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.targetAudience || []).includes(audience)}
                onChange={(e) => handleArrayChange('targetAudience', audience, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{audience}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Demographics */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Demographics</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h4 className="font-medium text-gray-800 mb-3">Target Age Ranges</h4>
            <div className="space-y-2">
              {targetAgeRanges.map((range) => (
                <label key={range} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(data.targetAgeRanges || []).includes(range)}
                    onChange={(e) => handleArrayChange('targetAgeRanges', range, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{range}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <h4 className="font-medium text-gray-800 mb-3">Target Gender</h4>
            <div className="space-y-2">
              {targetGender.map((gender) => (
                <label key={gender} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(data.targetGender || []).includes(gender)}
                    onChange={(e) => handleArrayChange('targetGender', gender, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{gender}</span>
                </label>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Geographic & Language */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Geographic & Language Settings</h3>

        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-gray-800 mb-3">Languages Served</h4>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
              {languagesServed.map((language) => (
                <label key={language} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(data.languagesServed || []).includes(language)}
                    onChange={(e) => handleArrayChange('languagesServed', language, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{language}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <h4 className="font-medium text-gray-800 mb-3">Geographic Coverage</h4>
            <div className="space-y-3">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={data.locationsServed?.worldwide || false}
                  onChange={(e) => handleInputChange('locationsServed', { 
                    ...data.locationsServed, 
                    worldwide: e.target.checked 
                  })}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700">Worldwide</span>
              </label>

              {!data.locationsServed?.worldwide && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">City</label>
                    <input
                      type="text"
                      value={data.locationsServed?.city || ''}
                      onChange={(e) => handleInputChange('locationsServed', { 
                        ...data.locationsServed, 
                        city: e.target.value 
                      })}
