import React from 'react';
import { MultiStageFormData, ColorsFonts } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}




export default function ColorsFontsPage({ formData, updateFormData, skipPage }: Props) {
  const colorsData = formData.colorsFonts || {};

  const handleInputChange = (field: keyof ColorsFonts, value: string | boolean) => {
    updateFormData('colorsFonts', { [field]: value });
  };

  const handleCustomColorChange = (colorType: 'primaryBackground' | 'secondaryBackground' | 'accent' | 'text' | 'heading', value: string) => {
    const currentCustomColors = colorsData.customColors || {};
    updateFormData('colorsFonts', {
      customColors: {
        ...currentCustomColors,
        [colorType]: value
      }
    });
  };



  const getColorValue = (colorType: 'primaryBackground' | 'secondaryBackground' | 'accent' | 'text' | 'heading') => {
    const customColors = colorsData.customColors || {};
    if (customColors[colorType]) {
      return customColors[colorType];
    }

    // Default colors if no custom colors or theme selected
    const defaultColors = {
      primaryBackground: '#FFFFFF',
      secondaryBackground: '#F8F9FA',
      accent: '#0D6EFD',
      text: '#6C757D',
      heading: '#212529'
    };

    return defaultColors[colorType];
  };

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Colors & Fonts</h2>
        <p className="text-gray-600">Choose your preferred color palette and typography theme</p>
      </div>

      {/* AI Decide Option */}
      <div className="bg-white p-6 rounded-lg border">
        <div className="flex items-center space-x-3">
          <input
            type="checkbox"
            id="aiDecide"
            checked={colorsData.aiDecide || false}
            onChange={(e) => handleInputChange('aiDecide', e.target.checked)}
            className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
          />
          <label htmlFor="aiDecide" className="text-lg font-medium text-gray-900">
            Let the AI decide these for me
          </label>
        </div>
        <p className="text-gray-600 mt-2 ml-6">
          Our AI will analyze your project requirements and select the most suitable color palette and typography theme.
        </p>
      </div>



      {/* Custom Color Inputs */}
      {!colorsData.aiDecide && (
        <div className="bg-white p-6 rounded-lg border">
          <h3 className="text-lg font-semibold mb-4">Customize Colors</h3>
          <p className="text-gray-600 mb-6">
            You can customize the colors for your selected theme. Use the color picker or type in hex values.
          </p>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Primary Background */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Primary Background
              </label>
              <div className="flex space-x-2">
                <input
                  type="color"
                  value={getColorValue('primaryBackground')}
                  onChange={(e) => handleCustomColorChange('primaryBackground', e.target.value)}
                  className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                />
                <input
                  type="text"
                  value={getColorValue('primaryBackground')}
                  onChange={(e) => handleCustomColorChange('primaryBackground', e.target.value)}
                  placeholder="#FFFFFF"
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                />
              </div>
            </div>

            {/* Secondary Background */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Secondary Background
              </label>
              <div className="flex space-x-2">
                <input
                  type="color"
                  value={getColorValue('secondaryBackground')}
                  onChange={(e) => handleCustomColorChange('secondaryBackground', e.target.value)}
                  className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                />
                <input
                  type="text"
                  value={getColorValue('secondaryBackground')}
                  onChange={(e) => handleCustomColorChange('secondaryBackground', e.target.value)}
                  placeholder="#F8F9FA"
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                />
              </div>
            </div>

            {/* Accent Color */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Accent Color
              </label>
              <div className="flex space-x-2">
                <input
                  type="color"
                  value={getColorValue('accent')}
                  onChange={(e) => handleCustomColorChange('accent', e.target.value)}
                  className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                />
                <input
                  type="text"
                  value={getColorValue('accent')}
                  onChange={(e) => handleCustomColorChange('accent', e.target.value)}
                  placeholder="#0D6EFD"
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                />
              </div>
            </div>

            {/* Text Color */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Text Color
              </label>
              <div className="flex space-x-2">
                <input
                  type="color"
                  value={getColorValue('text')}
                  onChange={(e) => handleCustomColorChange('text', e.target.value)}
                  className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                />
                <input
                  type="text"
                  value={getColorValue('text')}
                  onChange={(e) => handleCustomColorChange('text', e.target.value)}
                  placeholder="#6C757D"
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                />
              </div>
            </div>

            {/* Heading Color */}
            <div className="md:col-span-2">
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Heading Color
              </label>
              <div className="flex space-x-2">
                <input
                  type="color"
                  value={getColorValue('heading')}
                  onChange={(e) => handleCustomColorChange('heading', e.target.value)}
                  className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                />
                <input
                  type="text"
                  value={getColorValue('heading')}
                  onChange={(e) => handleCustomColorChange('heading', e.target.value)}
                  placeholder="#212529"
                  className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                />
              </div>
            </div>
          </div>

          {/* Color Preview */}
          <div className="mt-6 p-4 border border-gray-200 rounded-lg">
            <h4 className="text-sm font-medium text-gray-700 mb-3">Color Preview</h4>
            <div className="grid grid-cols-5 gap-2">
              <div className="text-center">
                <div
                  className="w-full h-16 rounded border border-gray-300 mb-2"
                  style={{ backgroundColor: getColorValue('primaryBackground') }}
                />
                <span className="text-xs text-gray-600">Primary</span>
              </div>
              <div className="text-center">
                <div
                  className="w-full h-16 rounded border border-gray-300 mb-2"
                  style={{ backgroundColor: getColorValue('secondaryBackground') }}
                />
                <span className="text-xs text-gray-600">Secondary</span>
              </div>
              <div className="text-center">
                <div
                  className="w-full h-16 rounded border border-gray-300 mb-2"
                  style={{ backgroundColor: getColorValue('accent') }}
                />
                <span className="text-xs text-gray-600">Accent</span>
              </div>
              <div className="text-center">
                <div
                  className="w-full h-16 rounded border border-gray-300 mb-2"
                  style={{ backgroundColor: getColorValue('text') }}
                />
                <span className="text-xs text-gray-600">Text</span>
              </div>
              <div className="text-center">
                <div
                  className="w-full h-16 rounded border border-gray-300 mb-2"
                  style={{ backgroundColor: getColorValue('heading') }}
                />
                <span className="text-xs text-gray-600">Heading</span>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Skip Option */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply, you can skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>
    </div>
  );
}
