import React from 'react';
import { MultiStageFormData, PackageManagement, BuildBundling } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function PackagesBuildsPage({ formData, updateFormData, skipPage }: Props) {
  const packageData = formData.packageManagement;
  const buildData = formData.buildBundling;

  const handlePackageInputChange = (field: keyof PackageManagement, value: string | string[]) => {
    updateFormData('packageManagement', { [field]: value });
  };

  const handlePackageCheckboxChange = (field: keyof PackageManagement, value: string, checked: boolean) => {
    const currentValues = (packageData[field] as string[]) || [];
    if (checked) {
      handlePackageInputChange(field, [...currentValues, value]);
    } else {
      handlePackageInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleBuildInputChange = (field: keyof BuildBundling, value: string | string[]) => {
    updateFormData('buildBundling', { [field]: value });
  };

  const handleBuildCheckboxChange = (field: keyof BuildBundling, value: string, checked: boolean) => {
    const currentValues = (buildData[field] as string[]) || [];
    if (checked) {
      handleBuildInputChange(field, [...currentValues, value]);
    } else {
      handleBuildInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const packageManagers = [
    'Cargo',
    'Composer',
    'Go Modules',
    'Gradle',
    'Maven',
    'npm',
    'NuGet',
    'pip',
    'pnpm',
    'RubyGems',
    'Yarn'
  ];

  const bundlers = [
    'esbuild',
    'Parcel',
    'Rollup',
    'SWC',
    'Turbopack',
    'Vite',
    'Webpack'
  ];

  const transpilers = [
    'Babel',
    'esbuild',
    'SWC',
    'TypeScript Compiler'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Packages & Builds</h2>
        <p className="text-gray-600">Choose your preferred package management and build tools</p>
      </div>

      {/* Package Managers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Package Managers</h3>
        <p className="text-gray-600 mb-4">Select all package managers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(packageData.packageManagers || []).length === packageManagers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('packageManagement', { packageManagers: [...packageManagers] });
                } else {
                  updateFormData('packageManagement', { packageManagers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {packageManagers.map((manager) => (
            <label key={manager} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(packageData.packageManagers || []).includes(manager)}
                onChange={(e) => handlePackageCheckboxChange('packageManagers', manager, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{manager}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Package Manager
          </label>
          <select
            value={packageData.defaultPackageManager || ''}
            onChange={(e) => handlePackageInputChange('defaultPackageManager', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default package manager</option>
            {packageManagers.map((manager) => (
              <option key={manager} value={manager}>{manager}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Bundlers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Build Tools & Bundlers</h3>
        <p className="text-gray-600 mb-4">Select all bundlers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(buildData.bundlers || []).length === bundlers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('buildBundling', { bundlers: [...bundlers] });
                } else {
                  updateFormData('buildBundling', { bundlers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {bundlers.map((bundler) => (
            <label key={bundler} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(buildData.bundlers || []).includes(bundler)}
                onChange={(e) => handleBuildCheckboxChange('bundlers', bundler, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{bundler}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Bundler
          </label>
          <select
            value={buildData.defaultBundler || ''}
            onChange={(e) => handleBuildInputChange('defaultBundler', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default bundler</option>
            {bundlers.map((bundler) => (
              <option key={bundler} value={bundler}>{bundler}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Transpilers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Transpilers & Compilers</h3>
        <p className="text-gray-600 mb-4">Select all transpilers you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(buildData.transpilers || []).length === transpilers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('buildBundling', { transpilers: [...transpilers] });
                } else {
                  updateFormData('buildBundling', { transpilers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {transpilers.map((transpiler) => (
            <label key={transpiler} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(buildData.transpilers || []).includes(transpiler)}
                onChange={(e) => handleBuildCheckboxChange('transpilers', transpiler, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{transpiler}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Transpiler
          </label>
          <select
            value={buildData.defaultTranspiler || ''}
            onChange={(e) => handleBuildInputChange('defaultTranspiler', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default transpiler</option>
            {transpilers.map((transpiler) => (
              <option key={transpiler} value={transpiler}>{transpiler}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

    </div>
  );
}
