import React from 'react';
import { MultiStageFormData, CommunicationAPIs } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CommunicationAPIsPage({ formData, updateFormData }: Props) {
  const data = formData.communicationAPIs;

  const handleInputChange = (field: keyof CommunicationAPIs, value: string[]) => {
    updateFormData('communicationAPIs', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof CommunicationAPIs, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const communicationAPIs = [
    'Twilio',
    'SendGrid',
    'Mailgun',
    'Pusher',
    'Ably',
    'Slack API',
    'WhatsApp Business Platform',
    'Discord API',
    'Telegram Bot API',
    'Microsoft Teams API'
  ];

  const contentAPIs = [
    'OpenAI API',
    'Google AI Platform (Vertex AI)',
    'YouTube IFrame Player API',
    'Spotify Web API',
    'OpenWeatherMap API',
    'Yelp Fusion API',
    'NewsAPI',
    'Unsplash API',
    'Pexels API',
    'Giphy API'
  ];

  const productivityAPIs = [
    'Google Workspace APIs (Drive, Sheets, Docs)',
    'Microsoft Graph API (OneDrive, Outlook)',
    'Salesforce APIs',
    'HubSpot API',
    'Zoom API',
    'Notion API',
    'Airtable API',
    'Trello API',
    'Asana API',
    'Monday.com API'
  ];

  const calendarOptions = [
    'Calendly API',
    'Cal.com API',
    'Google Calendar API',
    'Microsoft Outlook Calendar API',
    'Apple Calendar',
    'iCal Integration',
    'Outlook Web Add-ins',
    'Google Workspace Add-ons'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Communication & APIs</h2>
        <p className="text-gray-600">Choose your preferred communication and integration services</p>
      </div>

      {/* Communication APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Communication & Messaging APIs</h3>
        <p className="text-gray-600 mb-4">Select all communication APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {communicationAPIs.map((api) => (
            <label key={api} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.communicationAPIs || []).includes(api)}
                onChange={(e) => handleCheckboxChange('communicationAPIs', api, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{api}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Content & Data APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Content, Data & AI APIs</h3>
        <p className="text-gray-600 mb-4">Select all content and data APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {contentAPIs.map((api) => (
            <label key={api} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.contentAPIs || []).includes(api)}
                onChange={(e) => handleCheckboxChange('contentAPIs', api, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{api}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Productivity APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Productivity & Business APIs</h3>
        <p className="text-gray-600 mb-4">Select all productivity APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {productivityAPIs.map((api) => (
            <label key={api} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.productivityAPIs || []).includes(api)}
                onChange={(e) => handleCheckboxChange('productivityAPIs', api, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{api}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Calendar APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Scheduling & Calendar APIs</h3>
        <p className="text-gray-600 mb-4">Select all calendar and scheduling APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {calendarOptions.map((option) => (
            <label key={option} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.calendarOptions || []).includes(option)}
                onChange={(e) => handleCheckboxChange('calendarOptions', option, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{option}</span>
            </label>
          ))}
        </div>
      </div>

      {/* API Information */}
      <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
        <h3 className="text-lg font-semibold mb-4 text-blue-900">API Integration Information</h3>
        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-blue-800">Communication & Messaging APIs</h4>
            <p className="text-blue-700 text-sm">APIs for sending emails, SMS, push notifications, and real-time messaging features.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">Content, Data & AI APIs</h4>
            <p className="text-blue-700 text-sm">APIs for AI services, media content, weather data, and external information sources.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">Productivity & Business APIs</h4>
            <p className="text-blue-700 text-sm">APIs for integrating with business tools, document management, and productivity platforms.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">Scheduling & Calendar APIs</h4>
            <p className="text-blue-700 text-sm">APIs for calendar integration, appointment scheduling, and time management features.</p>
          </div>
        </div>
      </div>
    </div>
  );
}
