import React from 'react';
import { MultiStageFormData, CommunicationAPIs } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CommunicationAPIsPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.communicationAPIs;

  const handleInputChange = (field: keyof CommunicationAPIs, value: string[]) => {
    updateFormData('communicationAPIs', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof CommunicationAPIs, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const llmAgents = [
    'Anthropic Claude',
    'AutoGPT',
    'BabyAGI',
    'Cohere',
    'Google Gemini',
    'Hugging Face',
    'LangChain',
    'Meta Llama',
    'Mistral AI',
    'OpenAI GPT-4'
  ];

  const videoGeneration = [
    'AnimateDiff',
    'Gen-2 (Runway)',
    'Imagen Video (Google)',
    'Make-A-Video (Meta)',
    'Pika Labs',
    'Runway ML',
    'Stable Video Diffusion',
    'Text2Video-Zero'
  ];

  const audioGeneration = [
    'Amazon Polly',
    'Azure Speech Service',
    'Coqui TTS',
    'Descript',
    'ElevenLabs',
    'Google Cloud Text-to-Speech',
    'Murf AI',
    'Play.ht',
    'Resemble AI',
    'Synthesia'
  ];

  const ocr = [
    'ABBYY FineReader',
    'Adobe Acrobat OCR',
    'AWS Textract',
    'Azure Computer Vision',
    'Google Cloud Vision API',
    'OCR.space',
    'PaddleOCR',
    'Tesseract OCR'
  ];

  const mcpServers = [
    'Anthropic MCP Server',
    'Custom MCP Server',
    'Database MCP Server',
    'File System MCP Server',
    'Google MCP Server',
    'OpenAI MCP Server'
  ];

  const communicationAPIs = [
    'Ably',
    'Discord',
    'Mailgun',
    'Microsoft Teams',
    'Pusher',
    'SendGrid',
    'Slack',
    'Telegram Bot',
    'Twilio',
    'WhatsApp Business Platform'
  ];

  const contentAPIs = [
    'Giphy',
    'Google AI Platform (Vertex AI)',
    'NewsAPI',
    'OpenAI',
    'OpenWeatherMap',
    'Pexels',
    'Spotify Web',
    'Unsplash',
    'Yelp Fusion',
    'YouTube IFrame Player'
  ];

  const productivityAPIs = [
    'Airtable',
    'Asana',
    'Google Workspace (Drive, Sheets, Docs)',
    'HubSpot',
    'Microsoft Graph (OneDrive, Outlook)',
    'Monday.com',
    'Notion',
    'Salesforce',
    'Trello',
    'Zoom'
  ];

  const calendarOptions = [
    'Apple Calendar',
    'Cal.com',
    'Calendly',
    'Google Calendar',
    'Google Workspace Add-ons',
    'iCal Integration',
    'Microsoft Outlook Calendar',
    'Outlook Web Add-ins'
  ];

  const mappingProviders = [
    'Apple Maps (iOS)',
    'Bing Maps',
    'Carto',
    'Google Maps Platform',
    'HERE Maps',
    'MapTiler',
    'Mapbox',
    'OpenStreetMap',
    'TomTom'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">APIs</h2>
        <p className="text-gray-600">Choose your preferred communication and integration services</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Communication APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Communication & Messaging</h3>
        <p className="text-gray-600 mb-4">Select all communication APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.communicationAPIs || []).length === communicationAPIs.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('communicationAPIs', { communicationAPIs: [...communicationAPIs] });
                } else {
                  updateFormData('communicationAPIs', { communicationAPIs: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {communicationAPIs.map((api) => (
            <label key={api} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.communicationAPIs || []).includes(api)}
                onChange={(e) => handleCheckboxChange('communicationAPIs', api, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{api}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Content & Data APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Content, Data & AI</h3>
        <p className="text-gray-600 mb-4">Select all content and data APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.contentAPIs || []).length === contentAPIs.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('communicationAPIs', { contentAPIs: [...contentAPIs] });
                } else {
                  updateFormData('communicationAPIs', { contentAPIs: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {contentAPIs.map((api) => (
            <label key={api} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.contentAPIs || []).includes(api)}
                onChange={(e) => handleCheckboxChange('contentAPIs', api, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{api}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Productivity APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Productivity & Business</h3>
        <p className="text-gray-600 mb-4">Select all productivity APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.productivityAPIs || []).length === productivityAPIs.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('communicationAPIs', { productivityAPIs: [...productivityAPIs] });
                } else {
                  updateFormData('communicationAPIs', { productivityAPIs: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {productivityAPIs.map((api) => (
            <label key={api} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.productivityAPIs || []).includes(api)}
                onChange={(e) => handleCheckboxChange('productivityAPIs', api, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{api}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Calendar APIs */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Scheduling & Calendar</h3>
        <p className="text-gray-600 mb-4">Select all calendar and scheduling APIs you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.calendarOptions || []).length === calendarOptions.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('communicationAPIs', { calendarOptions: [...calendarOptions] });
                } else {
                  updateFormData('communicationAPIs', { calendarOptions: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {calendarOptions.map((option) => (
            <label key={option} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.calendarOptions || []).includes(option)}
                onChange={(e) => handleCheckboxChange('calendarOptions', option, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{option}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Mapping Provider */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Mapping Provider</h3>
        <p className="text-gray-600 mb-4">Select your preferred mapping and geolocation service:</p>

        <div className="space-y-3">
          {mappingProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="mappingProvider"
                value={provider}
                checked={mapsData.mappingProvider === provider}
                onChange={(e) => handleMapsInputChange('mappingProvider', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{provider}</span>
            </label>
          ))}
        </div>
      </div>

      {/* LLM Agents */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">LLM Agents & Language Models</h3>
        <p className="text-gray-600 mb-4">Select all LLM agents and language models you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.llmAgents || []).length === llmAgents.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { llmAgents: [...llmAgents] });
                } else {
                  updateFormData('aiIntegration', { llmAgents: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {llmAgents.map((agent) => (
            <label key={agent} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(aiData.llmAgents || []).includes(agent)}
                onChange={(e) => handleAICheckboxChange('llmAgents', agent, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{agent}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Video Generation */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Video Generation</h3>
        <p className="text-gray-600 mb-4">Select all video generation tools you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.videoGeneration || []).length === videoGeneration.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { videoGeneration: [...videoGeneration] });
                } else {
                  updateFormData('aiIntegration', { videoGeneration: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {videoGeneration.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(aiData.videoGeneration || []).includes(tool)}
                onChange={(e) => handleAICheckboxChange('videoGeneration', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{tool}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Audio Generation */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Audio Generation</h3>
        <p className="text-gray-600 mb-4">Select all audio generation tools you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.audioGeneration || []).length === audioGeneration.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { audioGeneration: [...audioGeneration] });
                } else {
                  updateFormData('aiIntegration', { audioGeneration: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {audioGeneration.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(aiData.audioGeneration || []).includes(tool)}
                onChange={(e) => handleAICheckboxChange('audioGeneration', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{tool}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Web Technologies */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Web Technologies</h3>
        <p className="text-gray-600 mb-4">Select the web technologies you want to use:</p>

        <div className="space-y-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={aiData.canvas2D || false}
              onChange={(e) => handleAIInputChange('canvas2D', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 text-sm">Canvas 2D (for graphics and animations)</span>
          </label>

          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={aiData.webGL || false}
              onChange={(e) => handleAIInputChange('webGL', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 text-sm">WebGL (for 3D graphics and GPU acceleration)</span>
          </label>

          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={aiData.webAudio || false}
              onChange={(e) => handleAIInputChange('webAudio', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 text-sm">Web Audio API (for audio processing and synthesis)</span>
          </label>
        </div>
      </div>

      {/* OCR Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">OCR (Optical Character Recognition)</h3>
        <p className="text-gray-600 mb-4">Select all OCR tools you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.ocr || []).length === ocr.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { ocr: [...ocr] });
                } else {
                  updateFormData('aiIntegration', { ocr: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {ocr.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(aiData.ocr || []).includes(tool)}
                onChange={(e) => handleAICheckboxChange('ocr', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{tool}</span>
            </label>
          ))}
        </div>
      </div>

    </div>
  );
}
