import React from 'react';
import { MultiStageFormData, TestingCodeQuality, AnalyticsMonitoring } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function TestingCodeQualityPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.testingCodeQuality;
  const analyticsData = formData.analyticsMonitoring;

  const handleInputChange = (field: keyof TestingCodeQuality, value: string | string[]) => {
    updateFormData('testingCodeQuality', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof TestingCodeQuality, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleAnalyticsInputChange = (field: keyof AnalyticsMonitoring, value: string | string[]) => {
    updateFormData('analyticsMonitoring', { [field]: value });
  };

  const handleAnalyticsCheckboxChange = (field: keyof AnalyticsMonitoring, value: string, checked: boolean) => {
    const currentValues = (analyticsData[field] as string[]) || [];
    if (checked) {
      handleAnalyticsInputChange(field, [...currentValues, value]);
    } else {
      handleAnalyticsInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const testingFrameworks = [
    'Cypress',
    'Jest',
    'Playwright',
    'Pytest',
    'React Testing Library',
    'Selenium',
    'Supertest',
    'Vitest'
  ];

  const lintingTools = [
    'Black',
    'ESLint',
    'Prettier',
    'Ruff',
    'Stylelint'
  ];

  const formattingTools = [
    'Black',
    'Prettier',
    'Ruff'
  ];

  const gitHooks = [
    'Husky',
    'lint-staged'
  ];

  const analyticsTools = [
    'Fathom Analytics',
    'Google Analytics',
    'Hotjar',
    'Mixpanel',
    'Plausible'
  ];

  const performanceMonitoring = [
    'DataDog',
    'Lighthouse',
    'LogRocket',
    'New Relic',
    'Sentry'
  ];

  const loggingServices = [
    'AWS CloudWatch',
    'Datadog',
    'Logtail (by BetterStack)'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Testing & Analytics</h2>
        <p className="text-gray-600">Choose your preferred testing frameworks and quality tools</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Testing Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Testing Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all testing frameworks you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.testingFrameworks || []).length === testingFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('testingCodeQuality', { testingFrameworks: [...testingFrameworks] });
                } else {
                  updateFormData('testingCodeQuality', { testingFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {testingFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.testingFrameworks || []).includes(framework)}
                onChange={(e) => handleCheckboxChange('testingFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Testing Framework
          </label>
          <select
            value={data.defaultTestingFramework || ''}
            onChange={(e) => handleInputChange('defaultTestingFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default testing framework</option>
            {testingFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Linting Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Linting & Code Analysis</h3>
        <p className="text-gray-600 mb-4">Select all linting tools you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.lintingTools || []).length === lintingTools.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('testingCodeQuality', { lintingTools: [...lintingTools] });
                } else {
                  updateFormData('testingCodeQuality', { lintingTools: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {lintingTools.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.lintingTools || []).includes(tool)}
                onChange={(e) => handleCheckboxChange('lintingTools', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{tool}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Linting Tool
          </label>
          <select
            value={data.defaultLinting || ''}
            onChange={(e) => handleInputChange('defaultLinting', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default linting tool</option>
            {lintingTools.map((tool) => (
              <option key={tool} value={tool}>{tool}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Formatting Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Code Formatting</h3>
        <p className="text-gray-600 mb-4">Select all formatting tools you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.formattingTools || []).length === formattingTools.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('testingCodeQuality', { formattingTools: [...formattingTools] });
                } else {
                  updateFormData('testingCodeQuality', { formattingTools: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {formattingTools.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.formattingTools || []).includes(tool)}
                onChange={(e) => handleCheckboxChange('formattingTools', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{tool}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Formatting Tool
          </label>
          <select
            value={data.defaultFormatting || ''}
            onChange={(e) => handleInputChange('defaultFormatting', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default formatting tool</option>
            {formattingTools.map((tool) => (
              <option key={tool} value={tool}>{tool}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Git Hooks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Git Hooks & Commit Standards</h3>
        <p className="text-gray-600 mb-4">Select all git hooks you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.gitHooks || []).length === gitHooks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('testingCodeQuality', { gitHooks: [...gitHooks] });
                } else {
                  updateFormData('testingCodeQuality', { gitHooks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {gitHooks.map((hook) => (
            <label key={hook} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.gitHooks || []).includes(hook)}
                onChange={(e) => handleCheckboxChange('gitHooks', hook, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{hook}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Git Hooks Tool
          </label>
          <select
            value={data.defaultGitHooks || ''}
            onChange={(e) => handleInputChange('defaultGitHooks', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default git hooks tool</option>
            {gitHooks.map((hook) => (
              <option key={hook} value={hook}>{hook}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Analytics Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Analytics Tools</h3>
        <p className="text-gray-600 mb-4">Select all analytics tools you&apos;re comfortable with:</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(analyticsData.analyticsTools || []).length === analyticsTools.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('analyticsMonitoring', { analyticsTools: [...analyticsTools] });
                } else {
                  updateFormData('analyticsMonitoring', { analyticsTools: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {analyticsTools.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(analyticsData.analyticsTools || []).includes(tool)}
                onChange={(e) => handleAnalyticsCheckboxChange('analyticsTools', tool, e.target.checked)}
