import React from 'react';
import { MultiStageFormData, ProjectManagement } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function ProjectManagementPage({ formData, updateFormData }: Props) {
  const data = formData.projectManagement;

  const handleInputChange = (field: keyof ProjectManagement, value: string | string[]) => {
    updateFormData('projectManagement', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof ProjectManagement, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const projectManagementTools = [
    'Asana',
    'Jira',
    'Linear', 
    'Trello'
  ];





  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Project Management</h2>
        <p className="text-gray-600">Choose your preferred workflow and documentation tools</p>
      </div>

      {/* Project Management Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Project Management Tools</h3>
        <p className="text-gray-600 mb-4">Select all project management tools you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.projectManagementTools || []).length === projectManagementTools.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('projectManagement', { projectManagementTools: [...projectManagementTools] });
                } else {
                  updateFormData('projectManagement', { projectManagementTools: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {projectManagementTools.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.projectManagementTools || []).includes(tool)}
                onChange={(e) => handleCheckboxChange('projectManagementTools', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{tool}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Project Management Tool
          </label>
          <select
            value={data.defaultProjectManagement || ''}
            onChange={(e) => handleInputChange('defaultProjectManagement', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default tool</option>
            {projectManagementTools.map((tool) => (
              <option key={tool} value={tool}>{tool}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

    </div>
  );
}
