import React from 'react';
import { MultiStageFormData, EcommercePayments } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function EcommercePaymentsPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.ecommercePayments;

  const handleInputChange = (field: keyof EcommercePayments, value: string | string[]) => {
    updateFormData('ecommercePayments', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof EcommercePayments, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const ecommerceProviders = [
    'Shopify',
    'WooCommerce', 
    'BigCommerce',
    'Wix eCommerce',
    'Squarespace Commerce'
  ];

  const paymentProviders = [
    'Stripe Payments',
    'Paddle',
    'PayPal',
    'Lemon Squeezy',
    'Braintree',
    'Adyen'
  ];

  const paymentPlans = [
    'One-Time Sales (e.g., goods, services, digital products)',
    'Usage-Based (e.g., API credits, metered services)',
    'Subscriptions/Memberships (e.g., monthly, annual access)'
  ];

  const accountingPlatforms = [
    'Bench',
    'FreeAgent',
    'FreshBooks',
    'GoDaddy Bookkeeping',
    'Kashoo',
    'QuickBooks',
    'Sage',
    'Wave',
    'Xero',
    'Zoho Books'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Payments & Accounting</h2>
        <p className="text-gray-600">Choose your preferred e-commerce and payment solutions</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* E-Commerce Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">E-Commerce Providers</h3>
        <p className="text-gray-600 mb-4">Select all e-commerce platforms you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.ecommerceProviders || []).includes('Let the AI decide these for me')}
              onChange={(e) => handleCheckboxChange('ecommerceProviders', 'Let the AI decide these for me', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Let the AI decide these for me</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.ecommerceProviders || []).length === ecommerceProviders.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('ecommercePayments', { ecommerceProviders: [...ecommerceProviders] });
                } else {
                  updateFormData('ecommercePayments', { ecommerceProviders: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>
          {ecommerceProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.ecommerceProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('ecommerceProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{provider}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Payment Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Payment Providers</h3>
        <p className="text-gray-600 mb-4">Select all payment processors you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.paymentProviders || []).includes('Let the AI decide these for me')}
              onChange={(e) => handleCheckboxChange('paymentProviders', 'Let the AI decide these for me', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Let the AI decide these for me</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.paymentProviders || []).length === paymentProviders.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('ecommercePayments', { paymentProviders: [...paymentProviders] });
                } else {
                  updateFormData('ecommercePayments', { paymentProviders: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>
          {paymentProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.paymentProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('paymentProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{provider}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Payment Plans */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Payment Plans</h3>
        <p className="text-gray-600 mb-4">Select all payment models you plan to use:</p>

        <div className="space-y-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.paymentPlans || []).includes('Let the AI decide these for me')}
              onChange={(e) => handleCheckboxChange('paymentPlans', 'Let the AI decide these for me', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Let the AI decide these for me</span>
          </label>
          {paymentPlans.map((plan) => (
            <label key={plan} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.paymentPlans || []).includes(plan)}
                onChange={(e) => handleCheckboxChange('paymentPlans', plan, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{plan}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Accounting Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Accounting Platforms</h3>
        <p className="text-gray-600 mb-4">Select all accounting platforms you want to integrate:</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.accountingPlatforms || []).includes('Let the AI decide these for me')}
              onChange={(e) => handleCheckboxChange('accountingPlatforms', 'Let the AI decide these for me', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Let the AI decide these for me</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.accountingPlatforms || []).length === accountingPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('ecommercePayments', { accountingPlatforms: [...accountingPlatforms] });
                } else {
                  updateFormData('ecommercePayments', { accountingPlatforms: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>
          {accountingPlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.accountingPlatforms || []).includes(platform)}
                onChange={(e) => handleCheckboxChange('accountingPlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{platform}</span>
            </label>
          ))}
        </div>
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Accounting Platform
          </label>
          <select
            value={data.defaultAccountingPlatform || ''}
            onChange={(e) => handleInputChange('defaultAccountingPlatform', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default accounting platform</option>
            {accountingPlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>
    </div>
  );
}
