import React, { useState, useEffect, useRef } from 'react';
import { MultiStageFormData, FormPageConfig } from '../types/FormTypes';
import DeveloperInfoPage from './pages/DeveloperInfoPage';
import CompanyInfoPage from './pages/CompanyInfoPage';

import VersionControlPage from './pages/VersionControlPage';
import EcommercePaymentsPage from './pages/EcommercePaymentsPage';
import FileStorageEmailPage from './pages/FileStorageEmailPage';
import MapsGeolocationPage from './pages/MapsGeolocationPage';
import SecurityAuthPage from './pages/SecurityAuthPage';
import CommunicationAPIsPage from './pages/CommunicationAPIsPage';

import TechStackPage from './pages/TechStackPage';
import CloudInfrastructurePage from './pages/CloudInfrastructurePage';
import StylingDesignPage from './pages/StylingDesignPage';
import FinancialAPIsPage from './pages/FinancialAPIsPage';
import AIIntegrationPage from './pages/AIIntegrationPage';
import AppConfigurationPage from './pages/AppConfigurationPage';
import DevelopmentEnvironmentPage from './pages/DevelopmentEnvironmentPage';
import PackageManagementPage from './pages/PackageManagementPage';
import BuildBundlingPage from './pages/BuildBundlingPage';
import StateManagementPage from './pages/StateManagementPage';
import BackendAPIPage from './pages/BackendAPIPage';
import HeadlessCMSPage from './pages/HeadlessCMSPage';
import SearchPlatformsPage from './pages/SearchPlatformsPage';
import DatabasesStoragePage from './pages/DatabasesStoragePage';
import TestingCodeQualityPage from './pages/TestingCodeQualityPage';
import DeploymentCICDPage from './pages/DeploymentCICDPage';
import AnalyticsMonitoringPage from './pages/AnalyticsMonitoringPage';
import MobileDesktopPage from './pages/MobileDesktopPage';

const FORM_PAGES: FormPageConfig[] = [
  { id: 1, title: '🧑‍💻 Developer Information', description: 'Personal developer details', required: true, canSkip: false },
  { id: 2, title: '🏢 Company Information', description: 'Business or organization details', required: true, canSkip: false },
  { id: 3, title: '📱 App Configuration', description: 'App-specific settings and target audience', required: true, canSkip: false },
  { id: 4, title: '🔒 Security & Authentication', description: 'Authentication and security preferences', required: true, canSkip: false },
  { id: 5, title: '💻 Development Environment', description: 'IDE and development tools', required: false, canSkip: true },
  { id: 6, title: '📦 Package Management', description: 'Dependency and monorepo management', required: true, canSkip: false },
  { id: 7, title: ' Build & Compile', description: 'Build tools and transpilers', required: true, canSkip: false },
  { id: 8, title: '💳 E-commerce & Payments', description: 'Payment and commerce setup', required: false, canSkip: true },
  { id: 9, title: '📁 File Storage & Email', description: 'Storage and communication services', required: true, canSkip: false },
  { id: 10, title: '🗺️ Maps & Geolocation', description: 'Location-based services', required: false, canSkip: true },
  { id: 11, title: '📡 Communication & APIs', description: 'Communication and integration services', required: false, canSkip: true },
  { id: 12, title: '⚙️ Tech Stack & Languages', description: 'Programming languages and frameworks', required: true, canSkip: false },
  { id: 13, title: '💾 State Management & Data Fetching', description: 'Client-side state and API data', required: false, canSkip: true },
  { id: 14, title: '🌐 Backend & API', description: 'Server-side frameworks and API design', required: true, canSkip: false },
  { id: 15, title: '📝 Headless CMS', description: 'Content management systems', required: false, canSkip: true },
  { id: 16, title: '🔍 Search Platforms', description: 'Search and discovery services', required: false, canSkip: true },
  { id: 17, title: '🗄️ Databases & Storage', description: 'Data storage and management', required: true, canSkip: false },
  { id: 18, title: '☁️ Cloud & Infrastructure', description: 'Hosting and deployment preferences', required: true, canSkip: false },
  { id: 19, title: '🎨 Styling & Design', description: 'UI/UX and design preferences', required: true, canSkip: false },
  { id: 20, title: '✅ Testing & Code Quality', description: 'Testing frameworks and quality tools', required: true, canSkip: false },
  { id: 21, title: '🚀 Deployment & CI/CD', description: 'Deployment and continuous integration', required: true, canSkip: false },
  { id: 22, title: '📊 Analytics & Monitoring', description: 'Performance and user analytics', required: false, canSkip: true },
  { id: 23, title: '💰 Financial APIs', description: 'Banking and investment services', required: false, canSkip: true },
  { id: 24, title: '🤖 AI & MCPs', description: 'AI services and tools setup', required: false, canSkip: true },
  { id: 25, title: '📱 Mobile & Desktop', description: 'Cross-platform development', required: false, canSkip: true }
];

const STORAGE_KEY = 'multiStageFormData';

export default function MultiStageForm() {
  const scrollContainerRef = useRef<HTMLDivElement>(null);
  const [formData, setFormData] = useState<MultiStageFormData>(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        return {
          ...parsed,
          completedPages: new Set(parsed.completedPages || []),
          appConfiguration: {
            targetAgeRanges: ['All Ages'],
            targetGender: ['All Genders'],
            targetAudience: ['Everyone'],
            ...parsed.appConfiguration
          },
          backendAPI: {
            backendFrameworks: [],
            defaultBackendFramework: '',
            apiStyles: [],
            defaultAPIStyle: '',
            apiClients: [],
            defaultAPIClient: ''
          }
        };
      } catch (error) {
        console.error('Error parsing saved form data:', error);
      }
    }

    return {
      currentPage: 1,
      completedPages: new Set<number>(),
      developerInfo: {
        fullName: '',
        email: '',
        streetAddress: '',
        city: '',
        stateProvince: '',
        postalCode: '',
        country: '',
        phoneNumber: '',
        countryCode: '+1',
        username: '',
        timezone: '',
        language: 'English',
        skillLevel: 'Rather Not Say'
      },
      companyInfo: {},
      defaultPreferences: {},
      versionControl: {},
      developmentEnvironment: {},
      packageManagement: {},
      buildBundling: {},
      ecommercePayments: {},
      fileStorageEmail: {},
      mapsGeolocation: {},
      securityAuth: {
        twoFactorAuth: 'No 2FA Required'
      },
      communicationAPIs: {},
      dnsConfiguration: {},
      techStack: {},
      stateManagement: {},
      backendAPI: {
        backendFrameworks: [],
        defaultBackendFramework: '',
        apiStyles: [],
        defaultAPIStyle: '',
        apiClients: [],
        defaultAPIClient: ''
      },
      headlessCMS: {},
      searchPlatforms: {},
      databasesStorage: {},
      cloudInfrastructure: {},
      stylingDesign: {},
      testingCodeQuality: {},
      deploymentCICD: {},
      analyticsMonitoring: {},
      financialAPIs: {},
      aiIntegration: {},
      mobileDesktop: {},
                appConfiguration: {
            targetAgeRanges: ['All Ages'],
            targetGender: ['All Genders'],
            targetAudience: ['Everyone']
          }
    };
  });

  // Add state for showing the clear confirmation modal
  const [showClearConfirm, setShowClearConfirm] = useState(false);

  useEffect(() => {
    const dataToSave = {
      ...formData,
      completedPages: Array.from(formData.completedPages)
    };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(dataToSave));
  }, [formData]);

  const updateFormData = <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => {
    setFormData(prev => ({
      ...prev,
      [section]: { ...(prev[section] as object), ...data }
    }));
  };

  const goToPage = (pageNumber: number) => {
    if (pageNumber >= 1 && pageNumber <= FORM_PAGES.length) {
      setFormData(prev => ({ ...prev, currentPage: pageNumber }));
    }
  };

  // Function to center the current page button in the scroll container
  const centerCurrentPage = () => {
    if (scrollContainerRef.current) {
      const container = scrollContainerRef.current;
      const currentPageButton = container.querySelector(`[data-page="${formData.currentPage}"]`) as HTMLElement;

      if (currentPageButton) {
        const containerWidth = container.offsetWidth;
        const buttonLeft = currentPageButton.offsetLeft;
        const buttonWidth = currentPageButton.offsetWidth;

        // Calculate the scroll position to center the button
        const scrollLeft = buttonLeft - (containerWidth / 2) + (buttonWidth / 2);

        container.scrollTo({
          left: Math.max(0, scrollLeft),
          behavior: 'smooth'
        });
      }
    }
  };

  // Center the current page when it changes
  useEffect(() => {
    centerCurrentPage();
  }, [formData.currentPage]);

  const nextPage = () => {
    setFormData(prev => {
      const newCompletedPages = new Set(prev.completedPages);
      newCompletedPages.add(prev.currentPage);
      return {
        ...prev,
        completedPages: newCompletedPages,
        currentPage: Math.min(prev.currentPage + 1, FORM_PAGES.length)
      };
    });
  };

  const previousPage = () => {
    setFormData(prev => ({
      ...prev,
      currentPage: Math.max(prev.currentPage - 1, 1)
    }));
  };

  const skipPage = () => {
    const currentPageConfig = FORM_PAGES[formData.currentPage - 1];
    if (currentPageConfig.canSkip) {
      nextPage();
    }
  };

  const validateRequiredFields = () => {
    // Basic validation for required fields
    const { developerInfo } = formData;
    const missingFields = [];

    if (!developerInfo.fullName) missingFields.push('Full Name');
    if (!developerInfo.email) missingFields.push('Email');
    if (!developerInfo.streetAddress) missingFields.push('Street Address');
    if (!developerInfo.city) missingFields.push('City');
    if (!developerInfo.stateProvince) missingFields.push('State/Province');
    if (!developerInfo.postalCode) missingFields.push('Postal Code');
    if (!developerInfo.country) missingFields.push('Country');
    if (!developerInfo.phoneNumber) missingFields.push('Phone Number');
    if (!developerInfo.username) missingFields.push('Username');
    if (!developerInfo.timezone) missingFields.push('Timezone');
    if (!developerInfo.country) missingFields.push('Country');
    if (!developerInfo.language) missingFields.push('Language');
    if (!developerInfo.skillLevel) missingFields.push('Skill Level');

    return missingFields;
  };

  const completeSetup = () => {
    // Validate required fields
    const missingFields = validateRequiredFields();
    if (missingFields.length > 0) {
      alert(`⚠️ Please complete the following required fields before submitting:\n\n${missingFields.join('\n')}\n\nGo to Page 1 (Developer Info) to complete these fields.`);
      return;
    }

    // Mark final page as completed
    setFormData(prev => {
      const newCompletedPages = new Set(prev.completedPages);
      newCompletedPages.add(prev.currentPage);
      return {
        ...prev,
        completedPages: newCompletedPages
      };
    });

    // Prepare configuration data for webhook
    const configData = {
      generatedAt: new Date().toISOString(),
      formVersion: '1.0.0',
      totalPages: FORM_PAGES.length,
      completedPages: Array.from(formData.completedPages).length + 1,
      configuration: {
        developerInfo: formData.developerInfo,
        companyInfo: formData.companyInfo,
        defaultPreferences: formData.defaultPreferences,
        versionControl: formData.versionControl,
        developmentEnvironment: formData.developmentEnvironment,
        packageManagement: formData.packageManagement,
        buildBundling: formData.buildBundling,
        ecommercePayments: formData.ecommercePayments,
        fileStorageEmail: formData.fileStorageEmail,
        mapsGeolocation: formData.mapsGeolocation,
        securityAuth: formData.securityAuth,
        communicationAPIs: formData.communicationAPIs,
        dnsConfiguration: formData.dnsConfiguration,
        techStack: formData.techStack,
        stateManagement: formData.stateManagement,
        backendAPI: formData.backendAPI,
        headlessCMS: formData.headlessCMS,
        searchPlatforms: formData.searchPlatforms,
        databasesStorage: formData.databasesStorage,
        cloudInfrastructure: formData.cloudInfrastructure,
        stylingDesign: formData.stylingDesign,
        testingCodeQuality: formData.testingCodeQuality,
        deploymentCICD: formData.deploymentCICD,
        analyticsMonitoring: formData.analyticsMonitoring,
        financialAPIs: formData.financialAPIs,
        aiIntegration: formData.aiIntegration,
        mobileDesktop: formData.mobileDesktop,
        appConfiguration: formData.appConfiguration
      }
    };

    // Submit to webhook
    const submitToWebhook = async () => {
      try {
        const response = await fetch('https://encinitaseyes.app.n8n.cloud/form/0615e0eb-d7cf-4ddb-8ee4-f31e7698be22', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(configData)
        });

        if (response.ok) {
          const result = await response.json();
          console.log('Webhook response:', result);

          // Show success message with summary
          const completedCount = Array.from(formData.completedPages).length + 1;
          const summary = `🎉 Setup Complete!\n\n` +
            `✅ Completed ${completedCount} of ${FORM_PAGES.length} pages\n` +
            `📡 Configuration successfully sent to webhook\n` +
            `🚀 Your project setup data has been processed!\n\n` +
            `Server Response: ${result.message || 'Configuration received successfully'}`;

          alert(summary);
        } else {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
      } catch (error) {
        console.error('Webhook submission failed:', error);

        const errorMessage = error instanceof Error ? error.message : 'Unknown error occurred';
        alert(`❌ Webhook submission failed: ${errorMessage}\n\n` +
              `Please check the browser console for more details.`);
      }
    };

    // Execute webhook submission
    submitToWebhook();

    // Optional: Clear form data after successful completion
    // clearFormData();
  };

  const clearFormData = () => {
    localStorage.removeItem(STORAGE_KEY);
    setFormData({
      currentPage: 1,
      completedPages: new Set<number>(),
      developerInfo: {},
      companyInfo: {},
      defaultPreferences: {},
      versionControl: {},
      developmentEnvironment: {},
      packageManagement: {},
      buildBundling: {},
      ecommercePayments: {},
      fileStorageEmail: {},
      mapsGeolocation: {},
      securityAuth: {},
      communicationAPIs: {},
      dnsConfiguration: {},
      techStack: {},
      stateManagement: {},
      backendAPI: {
        backendFrameworks: [],
        defaultBackendFramework: '',
        apiStyles: [],
        defaultAPIStyle: '',
        apiClients: [],
        defaultAPIClient: ''
      },
      headlessCMS: {},
      searchPlatforms: {},
      databasesStorage: {},
      cloudInfrastructure: {},
      stylingDesign: {},
      testingCodeQuality: {},
      deploymentCICD: {},
      analyticsMonitoring: {},
      financialAPIs: {},
      aiIntegration: {},
      mobileDesktop: {},
      appConfiguration: {}
    });
  };

  const getCurrentPageComponent = () => {
    const pageProps = {
      formData,
      updateFormData,
      nextPage,
      previousPage,
      skipPage
    };

    switch (formData.currentPage) {
      case 1: return <DeveloperInfoPage {...pageProps} />;
      case 2: return <CompanyInfoPage {...pageProps} />;
      case 3: return <AppConfigurationPage {...pageProps} />;
      case 4: return <SecurityAuthPage {...pageProps} />;
      case 5: return <DevelopmentEnvironmentPage {...pageProps} />;
      case 6: return <PackageManagementPage {...pageProps} />;
      case 7: return <BuildBundlingPage {...pageProps} />;
      case 8: return <EcommercePaymentsPage {...pageProps} />;
      case 9: return <FileStorageEmailPage {...pageProps} />;
      case 10: return <MapsGeolocationPage {...pageProps} />;
      case 11: return <CommunicationAPIsPage {...pageProps} />;
      case 12: return <TechStackPage {...pageProps} />;
      case 13: return <StateManagementPage {...pageProps} />;
      case 14: return <BackendAPIPage {...pageProps} />;
      case 15: return <HeadlessCMSPage {...pageProps} />;
      case 16: return <SearchPlatformsPage {...pageProps} />;
      case 17: return <DatabasesStoragePage {...pageProps} />;
      case 18: return <CloudInfrastructurePage {...pageProps} />;
      case 19: return <StylingDesignPage {...pageProps} />;
      case 20: return <TestingCodeQualityPage {...pageProps} />;
      case 21: return <DeploymentCICDPage {...pageProps} />;
      case 22: return <AnalyticsMonitoringPage {...pageProps} />;
      case 23: return <FinancialAPIsPage {...pageProps} />;
      case 24: return <AIIntegrationPage {...pageProps} />;
      case 25: return <MobileDesktopPage {...pageProps} />;
      default: return <div>Page not found</div>;
    }
  };

  const currentPageConfig = FORM_PAGES[formData.currentPage - 1] || FORM_PAGES[0];
  const progress = (formData.completedPages.size / FORM_PAGES.length) * 100;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          <div className="bg-blue-600 text-white p-6">
            <div className="flex justify-between items-center mb-4">
              <h1 className="text-2xl font-bold">Multi-Stage Project Setup</h1>
              <button
                onClick={() => setShowClearConfirm(true)}
                className="text-blue-200 hover:text-white text-sm underline"
              >
                Clear All Data
              </button>
            </div>
            {/* Confirmation Modal */}
            {showClearConfirm && (
              <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-40">
                <div className="bg-white rounded-lg shadow-lg p-8 max-w-md w-full text-gray-900">
                  <h2 className="text-lg font-bold mb-4">Are you sure you want to clear your data?!</h2>
                  <p className="mb-6">This will clear all your form data and you will have to start again with your selections.</p>
                  <div className="flex flex-col gap-3">
                    <button
                      className="w-full px-4 py-2 rounded bg-red-600 text-white font-semibold hover:bg-red-700 transition-colors"
                      onClick={() => {
                        setShowClearConfirm(false);
                        clearFormData();
                      }}
                    >
                      Yes, clear the form data so I can start again.
                    </button>
                    <button
                      className="w-full px-4 py-2 rounded bg-gray-200 text-gray-800 font-semibold hover:bg-gray-300 transition-colors"
                      onClick={() => setShowClearConfirm(false)}
                    >
                      No, do NOT clear the data. Go back to my form selections.
                    </button>
                  </div>
                </div>
              </div>
            )}

            <div className="mb-4">
              <div className="flex justify-between text-sm mb-2">
                <span>Progress: {formData.completedPages.size} of {FORM_PAGES.length} pages completed</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="w-full bg-blue-500 rounded-full h-2">
                <div 
                  className="bg-white h-2 rounded-full transition-all duration-300"
                  style={{ width: `${progress}%` }}
                />
              </div>
            </div>

            <div className="flex items-center gap-4">
              <span className="text-sm opacity-80">Page {formData.currentPage} of {FORM_PAGES.length}</span>
              <h2 className="text-xl font-semibold">{currentPageConfig.title}</h2>
            </div>
            <p className="text-blue-100 mt-2">{currentPageConfig.description}</p>
          </div>

          <div className="border-b bg-gray-50 p-6 pb-12">
            <div ref={scrollContainerRef} className="flex gap-3 overflow-x-auto pb-8">
              {FORM_PAGES.map((page) => (
                <button
                  key={page.id}
                  data-page={page.id}
                  onClick={() => goToPage(page.id)}
                  className={`
                    flex-shrink-0 px-4 py-2 text-sm rounded-full border transition-colors
                    ${formData.currentPage === page.id 
                      ? 'bg-blue-600 text-white border-blue-600' 
                      : formData.completedPages.has(page.id)
                        ? 'bg-green-100 text-green-800 border-green-300 hover:bg-green-200'
                        : 'bg-white text-gray-600 border-gray-300 hover:bg-gray-100'
                    }
                  `}
                >
                  {page.id}. {page.title.replace(/[^\w\s&]/gi, '').trim()}
                </button>
              ))}
            </div>
          </div>

          <div className="p-8 pb-12">
            {getCurrentPageComponent()}
          </div>

          <div className="bg-gray-50 px-8 py-6 pt-8 flex justify-between items-center border-t">
            <button
              onClick={previousPage}
              disabled={formData.currentPage === 1}
              className={`
                px-6 py-3 rounded-md font-medium transition-colors
                ${formData.currentPage === 1
                  ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-600 text-white hover:bg-gray-700'
                }
              `}
            >
              Previous
            </button>

            <div className="flex gap-4">
              {formData.currentPage === 26 ? (
                <>
                  <button
                    onClick={skipPage}
                    className="px-6 py-3 rounded-md font-medium bg-orange-500 text-white hover:bg-orange-600 transition-colors"
                  >
                    Save
                  </button>
                  <button
                    onClick={completeSetup}
                    className="px-6 py-3 rounded-md font-medium bg-green-600 text-white hover:bg-green-700 transition-colors"
                  >
                    SUBMIT
                  </button>
                </>
              ) : (
                <>
                  {currentPageConfig.canSkip && (
                    <button
                      onClick={skipPage}
                      className="px-6 py-3 rounded-md font-medium bg-orange-500 text-white hover:bg-orange-600 transition-colors"
                    >
                      Save & Skip
                    </button>
                  )}

                  {formData.currentPage > 3 && !currentPageConfig.canSkip && (
                    <button
                      onClick={() => {
                        // Mark current page as completed and jump to page 13 (Tech Stack)
                        const updatedCompletedPages = new Set(formData.completedPages);
                        updatedCompletedPages.add(formData.currentPage);
                        updateFormData('completedPages', updatedCompletedPages);
                        goToPage(13); // Jump to Tech Stack page
                      }}
                      className="px-6 py-3 rounded-md font-medium bg-orange-500 text-white hover:bg-orange-600 transition-colors"
                    >
                      Save & Skip
                    </button>
                  )}

                  <button
                    onClick={formData.currentPage === FORM_PAGES.length ? completeSetup : nextPage}
                    className="px-6 py-3 rounded-md font-medium bg-blue-600 text-white hover:bg-blue-700 transition-colors"
                  >
                    {formData.currentPage === FORM_PAGES.length ? 'Complete Setup' : 'Next'}
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
