              checked={(buildData.bundlers || []).length === bundlers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('buildBundling', { bundlers: [...bundlers] });
                } else {
                  updateFormData('buildBundling', { bundlers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {bundlers.map((bundler) => (
            <label key={bundler} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(buildData.bundlers || []).includes(bundler)}
                onChange={(e) => handleBuildCheckboxChange('bundlers', bundler, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{bundler}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Bundler
          </label>
          <select
            value={buildData.defaultBundler || ''}
            onChange={(e) => handleBuildInputChange('defaultBundler', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default bundler</option>
            {bundlers.map((bundler) => (
              <option key={bundler} value={bundler}>{bundler}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Transpilers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Transpilers & Compilers</h3>
        <p className="text-gray-600 mb-4">Select all transpilers you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(buildData.transpilers || []).length === transpilers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('buildBundling', { transpilers: [...transpilers] });
                } else {
                  updateFormData('buildBundling', { transpilers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {transpilers.map((transpiler) => (
            <label key={transpiler} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(buildData.transpilers || []).includes(transpiler)}
                onChange={(e) => handleBuildCheckboxChange('transpilers', transpiler, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{transpiler}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Transpiler
          </label>
          <select
            value={buildData.defaultTranspiler || ''}
            onChange={(e) => handleBuildInputChange('defaultTranspiler', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default transpiler</option>
            {transpilers.map((transpiler) => (
              <option key={transpiler} value={transpiler}>{transpiler}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

    </div>
  );
}
