import React from 'react';
import { MultiStageFormData, AppConfiguration } from '../../types/FormTypes';
import naicsFlat from '../../data/naics_flat.json';
import Select from 'react-select';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function AppConfigurationPage({ formData, updateFormData }: Props) {
  const data = formData.appConfiguration;

  const handleInputChange = (field: keyof AppConfiguration, value: any) => {
    updateFormData('appConfiguration', { [field]: value });
  };

  const handleArrayChange = (field: keyof AppConfiguration, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];

    if (field === 'languagesServed') {
      if (value === 'All of These Languages') {
        if (checked) {
          // When "All of These Languages" is checked, select all languages
          const allLanguages = languagesServed.filter(lang => lang !== 'All of These Languages');
          handleInputChange(field, allLanguages);
        } else {
          // When "All of These Languages" is unchecked, clear all selections
          handleInputChange(field, []);
        }
      } else {
        // For individual language selections
        if (checked) {
          handleInputChange(field, [...currentValues, value]);
        } else {
          handleInputChange(field, currentValues.filter(v => v !== value));
        }
      }
    } else if (field === 'targetAudience') {
      if (value === 'Everyone') {
        if (checked) {
          // When "Everyone" is checked, select all target audiences
          const allAudiences = targetAudience.filter(audience => audience !== 'Everyone');
          handleInputChange(field, allAudiences);
        } else {
          // When "Everyone" is unchecked, clear all selections
          handleInputChange(field, []);
        }
      } else {
        // For individual audience selections
        if (checked) {
          handleInputChange(field, [...currentValues, value]);
        } else {
          handleInputChange(field, currentValues.filter(v => v !== value));
        }
      }
    } else if (field === 'targetAgeRanges') {
      if (value === 'All Ages') {
        if (checked) {
          // When "All Ages" is checked, select all age ranges
          const allAgeRanges = targetAgeRanges.filter(age => age !== 'All Ages');
          handleInputChange(field, allAgeRanges);
        } else {
          // When "All Ages" is unchecked, clear all selections
          handleInputChange(field, []);
        }
      } else {
        // For individual age range selections
        if (checked) {
          handleInputChange(field, [...currentValues, value]);
        } else {
          handleInputChange(field, currentValues.filter(v => v !== value));
        }
      }
    } else if (field === 'targetGender') {
      if (value === 'All Genders') {
        if (checked) {
          // When "All Genders" is checked, select all genders
          const allGenders = targetGender.filter(gender => gender !== 'All Genders');
          handleInputChange(field, allGenders);
        } else {
          // When "All Genders" is unchecked, clear all selections
          handleInputChange(field, []);
        }
      } else {
        // For individual gender selections
        if (checked) {
          handleInputChange(field, [...currentValues, value]);
        } else {
          handleInputChange(field, currentValues.filter(v => v !== value));
        }
      }
    } else if (field === 'techSavviness') {
      if (value === 'All Levels') {
        if (checked) {
          // When "All Levels" is checked, select all technical levels
          const allLevels = techSavviness.filter((level: string) => level !== 'All Levels');
          handleInputChange(field, allLevels);
        } else {
          // When "All Levels" is unchecked, clear all selections
          handleInputChange(field, []);
        }
      } else {
        // For individual technical level selections
        if (checked) {
          handleInputChange(field, [...currentValues, value]);
        } else {
          handleInputChange(field, currentValues.filter(v => v !== value));
        }
      }
    } else if (field === 'educationLevel') {
      if (value === 'All Levels') {
        if (checked) {
          // When "All Levels" is checked, select all education levels
          const allLevels = educationLevel.filter(level => level !== 'All Levels');
          handleInputChange(field, allLevels);
        } else {
          // When "All Levels" is unchecked, clear all selections
          handleInputChange(field, []);
        }
      } else {
        // For individual education level selections
        if (checked) {
          handleInputChange(field, [...currentValues, value]);
        } else {
          handleInputChange(field, currentValues.filter(v => v !== value));
        }
      }
    } else {
      // For other fields, use the original logic
      if (checked) {
        handleInputChange(field, [...currentValues, value]);
      } else {
        handleInputChange(field, currentValues.filter(v => v !== value));
      }
    }
  };

  const targetAudience = [
    'Everyone',
    'Businesses/Enterprise',
    'Content Creators',
    'Developers',
    'E-commerce',
    'Educators',
    'Financial Services',
    'Gaming',
    'General Public',
    'Healthcare Professionals',
    'Non-profit Organizations',
    'Social Media',
    'Students'
  ];

  const targetAgeRanges = [
    'All Ages',
    'Children (0-12)',
    'Teenagers (13-17)',
    'Young Adults (18-24)',
    'Adults (25-34)',
    'Adults (35-44)',
    'Adults (45-54)',
    'Adults (55-64)',
    'Seniors (65+)'
  ];

  const targetGender = [
    'All Genders',
    'Male',
    'Female',
    'Non-binary'
  ];

  const languagesServed = [
    'All of These Languages',
    'Arabic',
    'Chinese',
    'English',
    'French',
    'German',
    'Hindi',
    'Italian',
    'Japanese',
    'Korean',
    'Portuguese',
    'Russian',
    'Spanish'
  ];

  const techSavviness = [
    'All Levels',
    'Beginner',
    'Intermediate',
    'Advanced',
    'Expert'
  ];

  const educationLevel = [
    'All Levels',
    'High School',
    'Some College',
    'Bachelor\'s Degree',
    'Master\'s Degree',
    'PhD/Doctorate',
    'Technical Certification',
    'Self-taught'
  ];

  // Single NAICS dropdown
  const handleNaicsChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    updateFormData('appConfiguration', { sector: e.target.value });
  };

  const occupations = [
    'Software Developer',
    'Designer',
    'Product Manager',
    'Business Analyst',
    'Student',
    'Entrepreneur',
    'Healthcare Professional',
    'Educator',
    'Marketing Professional',
    'Sales Professional',
    'Executive/Manager'
  ];

  const incomeRange = [
    'Under $25,000',
    '$25,000 - $50,000',
    '$50,000 - $75,000',
    '$75,000 - $100,000',
    '$100,000 - $150,000',
    '$150,000 - $200,000',
    'Over $200,000',
    'All Ranges'
  ];

  const companySize = [
    '1-10 employees',
    '11-50 employees',
    '51-200 employees',
    '201-500 employees',
    '501-1000 employees',
    '1000+ employees',
    'All Sizes'
  ];

  const projectBudget = [
    'Under $1,000',
    '$1,000 - $5,000',
    '$5,000 - $10,000',
    '$10,000 - $25,000',
    '$25,000 - $50,000',
    '$50,000 - $100,000',
    '$100,000 - $250,000',
    'Over $250,000',
    'Not specified'
  ];

  const licenseType = [
    'Open Source (MIT)',
    'Open Source (GPL)',
    'Open Source (Apache)',
    'Proprietary',
    'Freemium',
    'Subscription',
    'One-time Purchase',
    'Not specified'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">App Information</h2>
        <p className="text-gray-600">Configure your application settings and target audience</p>
      </div>

      {/* Basic App Information */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Basic App Information</h3>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App Name
            </label>
            <input
              type="text"
              value={data.appName || ''}
              onChange={(e) => handleInputChange('appName', e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              placeholder="Enter your app name - e.g. 'SuperFunky App'"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App Description
            </label>
            <textarea
              value={data.appDescription || ''}
              onChange={(e) => handleInputChange('appDescription', e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              placeholder="Describe your app, its features, and purpose... e.g. 'An online app that obtains an application developer's 'settings' and 'preferences' and creates a folder/directory of usable context specifically made for LLMs and AI Agents to ingest prior to the coding of the project.  This context helps limit false-starts and dependency related failure issues related to normal, yet often-times insufficient planning. SuperFunky reduces tokens used during the coding of the project by having files, scripts, build processes, conceptual planning diagrams, suggested developer extensions, library downloads, user-interaction flows, etc., designed prior to issues being encountered during the coding process which is costly and time-consuming and expensive due to more expensive token usage. The app redusces costs to the developer and makes a finished product better than any normal prompt could make. The app saves the user time and money!'"
              rows={8}
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App Type
            </label>
            <div className="space-y-2">
              {[
                'AI and LLMs',
                'API and Service Monitoring',
                'Booking and Reservation Systems',
                'Cloud Computing',
                'Code Hosting and Collaboration',
                'Content Management Systems (CMS)',
                'Dynamic Web Apps',
                'Educational and E-Learning',
                'E-commerce and Marketplace',
                'Financial Services (FinTech)',
                'Health, Fitness, and Telemedicine',
                'Media Entertainment and Streaming',
                'On-Demand Service',
                'Online Design and Editing',
                'Productivity and Collaboration',
                'Progressive Web Apps (PWAs)',
                'Single Page Applications (SPAs)',
                'Social Networking and Media',
                'Software as a Service (SaaS)',
                'Static Web Apps',
                'Web3 and Decentralized Applications (dApps)'
              ].map(type => (
                <label key={type} className="flex items-center">
                  <input
                    type="radio"
                    name="defaultSiteType"
                    value={type}
                    checked={data.defaultSiteType === type}
                    onChange={(e) => handleInputChange('defaultSiteType', e.target.value)}
                    className="mr-2"
                  />
                  <span className="text-sm">{type}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              App or URL that inspired you
            </label>
            <select
              value={data.inspirationUrl || ''}
              onChange={(e) => handleInputChange('inspirationUrl', e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
            >
              <option value="">Select an inspiration</option>
              <option value="Adidas">Adidas</option>
              <option value="Slack">Slack</option>
              <option value="YouTube">YouTube</option>
            </select>
          </div>
        </div>
      </div>

      {/* Device Types */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Device Types</h3>
        <p className="text-gray-600 mb-4">Device types your apps should work on:</p>

        <div className="space-y-2">
          {['Mobiles and Tablets', 'Desktops and Laptops'].map(device => (
            <label key={device} className="flex items-center">
              <input
                type="checkbox"
                checked={(data.deviceTypes || []).includes(device)}
                onChange={(e) => {
                  const currentValues = (data.deviceTypes || []);
                  if (e.target.checked) {
                    handleInputChange('deviceTypes', [...currentValues, device]);
                  } else {
                    handleInputChange('deviceTypes', currentValues.filter(v => v !== device));
                  }
                }}
                className="mr-2"
              />
              <span className="text-sm">{device}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Target Audience */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Target Audience</h3>
        <p className="text-gray-600 mb-4">Select all target audiences for your app:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {targetAudience.map((audience) => (
            <label key={audience} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={audience === 'Everyone' 
                  ? (data.targetAudience || []).length === targetAudience.length - 1
                  : (data.targetAudience || []).includes(audience)
                }
                onChange={(e) => handleArrayChange('targetAudience', audience, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{audience}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Demographics */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Demographics</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h4 className="font-medium text-gray-800 mb-3">Target Age Ranges</h4>
            <div className="space-y-2">
              {targetAgeRanges.map((range) => (
                <label key={range} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={range === 'All Ages' 
                      ? (data.targetAgeRanges || []).length === targetAgeRanges.length - 1
                      : (data.targetAgeRanges || []).includes(range)
                    }
                    onChange={(e) => handleArrayChange('targetAgeRanges', range, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{range}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <h4 className="font-medium text-gray-800 mb-3">Target Genders</h4>
            <div className="space-y-2">
              {targetGender.map((gender) => (
                <label key={gender} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={gender === 'All Genders' 
                      ? (data.targetGender || []).length === targetGender.length - 1
                      : (data.targetGender || []).includes(gender)
                    }
                    onChange={(e) => handleArrayChange('targetGender', gender, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{gender}</span>
                </label>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Languages Served */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Languages Served</h3>

        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-gray-800 mb-3">Languages Served</h4>
            <div className="space-y-2">
              {languagesServed.map((language) => (
                <label key={language} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={language === 'All of These Languages' 
                      ? (data.languagesServed || []).length === languagesServed.length - 1
                      : (data.languagesServed || []).includes(language)
                    }
                    onChange={(e) => handleArrayChange('languagesServed', language, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{language}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <h4 className="font-medium text-gray-800 mb-3">Geographic Coverage</h4>
            <div className="space-y-3">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={data.locationsServed?.worldwide || false}
                  onChange={(e) => handleInputChange('locationsServed', { 
                    ...data.locationsServed, 
                    worldwide: e.target.checked 
                  })}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">Worldwide</span>
              </label>

              {!data.locationsServed?.worldwide && (
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">City</label>
                    <input
                      type="text"
                      value={data.locationsServed?.city || ''}
                      onChange={(e) => handleInputChange('locationsServed', { 
                        ...data.locationsServed, 
                        city: e.target.value 
                      })}
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                      placeholder="City"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">State/Province</label>
                    <input
                      type="text"
                      value={data.locationsServed?.state || ''}
                      onChange={(e) => handleInputChange('locationsServed', { 
                        ...data.locationsServed, 
                        state: e.target.value 
                      })}
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                      placeholder="State/Province"
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Country</label>
                    <input
                      type="text"
                      value={data.locationsServed?.country || ''}
                      onChange={(e) => handleInputChange('locationsServed', { 
                        ...data.locationsServed, 
                        country: e.target.value 
                      })}
                      className="w-full p-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                      placeholder="Country"
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* User Characteristics */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">User Characteristics</h3>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h4 className="font-medium text-gray-800 mb-3">Technical Savviness</h4>
            <div className="space-y-2">
              {techSavviness.map((level) => (
                <label key={level} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={level === 'All Levels' 
                      ? (data.techSavviness || []).length === techSavviness.length - 1
                      : (data.techSavviness || []).includes(level)
                    }
                    onChange={(e) => handleArrayChange('techSavviness', level, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{level}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <h4 className="font-medium text-gray-800 mb-3">Education Level</h4>
            <div className="space-y-2">
              {educationLevel.map((level) => (
                <label key={level} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={level === 'All Levels' 
                      ? (data.educationLevel || []).length === educationLevel.length - 1
                      : (data.educationLevel || []).includes(level)
                    }
                    onChange={(e) => handleArrayChange('educationLevel', level, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{level}</span>
                </label>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Business Information */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Business Information</h3>

        <div className="space-y-4">
          <div>
            <label className="flex items-center space-x-3 cursor-pointer mb-3">
              <input
                type="radio"
                name="industrySelection"
                value="all"
                checked={!data.sector || data.sector === ''}
                onChange={() => updateFormData('appConfiguration', { sector: '' })}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">All Industries</span>
            </label>

            <div className="flex items-center space-x-3 mb-2">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="radio"
                  name="industrySelection"
                  value="specific"
                  checked={Boolean(data.sector && data.sector !== '')}
                  onChange={() => updateFormData('appConfiguration', { sector: 'selected' })}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">Choose the industry from the dropdown</span>
              </label>
            </div>

            <label className="block text-sm font-medium text-gray-700 mb-2">
              NAICS Industry (6-digit, full path)
            </label>
            <Select
              options={naicsFlat.map((item: any) => ({ value: item.code, label: `${item.label} (${item.code})` }))}
              value={(() => {
                const found = naicsFlat.find((item: any) => item.code === data.sector);
                return found ? { value: data.sector, label: `${found.label} (${data.sector})` } : null;
              })()}
              onChange={(option: any) => updateFormData('appConfiguration', { sector: option ? option.value : '' })}
              isClearable
              classNamePrefix="react-select"
              styles={{
                menu: (provided: any) => ({ ...provided, width: '100%', whiteSpace: 'nowrap', overflowX: 'auto' }),
                option: (provided: any) => ({ ...provided, fontSize: '0.75rem', whiteSpace: 'nowrap' }),
                control: (provided: any) => ({ ...provided, fontSize: '0.75rem', width: '100%' }),
              }}
              filterOption={(option, input) => {
                const label = option.label.toLowerCase();
                const value = option.value.toLowerCase();
                const inputValue = input.toLowerCase();
                return label.includes(inputValue) || value.includes(inputValue);
              }}
              placeholder="Type your 6 digit code or select from the dropdown choices"
            />
          </div>

          <div>
            <h4 className="font-medium text-gray-800 mb-3">Target Occupations</h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(data.occupations || []).length === occupations.length}
                  onChange={(e) => {
                    if (e.target.checked) {
                      updateFormData('appConfiguration', { occupations: [...occupations] });
                    } else {
                      updateFormData('appConfiguration', { occupations: [] });
                    }
                  }}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm font-medium">All Occupations</span>
              </label>

              {[...occupations].sort().map((occupation) => (
                <label key={occupation} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(data.occupations || []).includes(occupation)}
                    onChange={(e) => handleArrayChange('occupations', occupation, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{occupation}</span>
                </label>
              ))}
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Income Range (per year)
              </label>
              <select
                value={data.incomeRange || ''}
                onChange={(e) => handleInputChange('incomeRange', e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              >
                <option value="">Select income range</option>
                {incomeRange.map((range) => (
                  <option key={range} value={range}>{range}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Company Size
              </label>
              <select
                value={data.companySize || ''}
                onChange={(e) => handleInputChange('companySize', e.target.value)}
