import React from 'react';
import { MultiStageFormData, FinancialAPIs } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function FinancialAPIsPage({ formData, updateFormData }: Props) {
  const data = formData.financialAPIs;

  const handleInputChange = (field: keyof FinancialAPIs, value: string | string[]) => {
    updateFormData('financialAPIs', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof FinancialAPIs, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const paymentProviders = [
    '2Checkout',
    'Adyen',
    'Authorize.net',
    'Braintree',
    'Lemon Squeezy',
    'Paddle',
    'PayPal',
    'Square',
    'Stripe',
    'Worldpay'
  ];

  const accountingPlatforms = [
    'Bench',
    'FreeAgent',
    'FreshBooks',
    'GoDaddy Bookkeeping',
    'Kashoo',
    'QuickBooks',
    'Sage',
    'Wave',
    'Xero',
    'Zoho Books'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Financial APIs</h2>
        <p className="text-gray-600">Choose your preferred payment and accounting services</p>
      </div>

      {/* Payment Providers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Payment Providers</h3>
        <p className="text-gray-600 mb-4">Select all payment providers you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {paymentProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.paymentProviders || []).includes(provider)}
                onChange={(e) => handleCheckboxChange('paymentProviders', provider, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Payment Provider
          </label>
          <select
            value={data.defaultPaymentProvider || ''}
            onChange={(e) => handleInputChange('defaultPaymentProvider', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default payment provider</option>
            {paymentProviders.map((provider) => (
              <option key={provider} value={provider}>{provider}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Accounting Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Accounting Platforms</h3>
        <p className="text-gray-600 mb-4">Select all accounting platforms you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          {accountingPlatforms.map((platform) => (
            <label key={platform} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.accountingPlatforms || []).includes(platform)}
                onChange={(e) => handleCheckboxChange('accountingPlatforms', platform, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{platform}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Accounting Platform
          </label>
          <select
            value={data.defaultAccountingPlatform || ''}
            onChange={(e) => handleInputChange('defaultAccountingPlatform', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default accounting platform</option>
            {accountingPlatforms.map((platform) => (
              <option key={platform} value={platform}>{platform}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Service Information */}
      <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
        <h3 className="text-lg font-semibold mb-4 text-blue-900">Financial Services Information</h3>
        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-blue-800">Payment Providers</h4>
            <p className="text-blue-700 text-sm">Services that process online payments, handle transactions, and manage payment gateways for your application. These providers offer APIs for integrating payment processing into your application.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">Accounting Platforms</h4>
            <p className="text-blue-700 text-sm">Software platforms for managing financial records, invoicing, expense tracking, and business accounting. These platforms often provide APIs for integrating financial data and automating accounting processes.</p>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
            <div>
              <h5 className="font-medium text-blue-800">Popular Payment Providers:</h5>
              <ul className="text-blue-700 text-sm space-y-1 mt-2">
                <li>• <strong>Stripe:</strong> Developer-focused payment platform</li>
                <li>• <strong>PayPal:</strong> Global online payment system</li>
                <li>• <strong>Paddle:</strong> Revenue delivery for SaaS</li>
                <li>• <strong>Lemon Squeezy:</strong> Digital product sales</li>
              </ul>
            </div>
            <div>
              <h5 className="font-medium text-blue-800">Popular Accounting Platforms:</h5>
              <ul className="text-blue-700 text-sm space-y-1 mt-2">
                <li>• <strong>QuickBooks:</strong> Small business accounting</li>
                <li>• <strong>Xero:</strong> Cloud-based accounting</li>
                <li>• <strong>Wave:</strong> Free financial software</li>
                <li>• <strong>FreshBooks:</strong> Invoicing and accounting</li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
