import React from 'react';
import { MultiStageFormData, CloudInfrastructure } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CloudInfrastructurePage({ formData, updateFormData }: Props) {
