                <label className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={formData.analyticsMonitoring?.defaultAnalytics === 'Let the AI decide'}
                    onChange={(e) => {
                      const val = e.target.checked ? 'Let the AI decide' : '';
                      updateFormData('analyticsMonitoring', { defaultAnalytics: val });
                      if (formData.analyticsMonitoring?.defaultAnalytics === 'Let the AI decide') {
                        updateFormData('analyticsMonitoring', { defaultAnalytics: val });
                      }
                    }}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-purple-600 font-medium">Let the AI decide</span>
                </label>
              </div>

              <div className="space-y-2">
                {['All are OK', 'Google Analytics', 'Plausible', 'Fathom', 'Mixpanel', 'Hotjar'].map((option) => (
                  <label key={option} className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="checkbox"
                      checked={(formData.analyticsMonitoring?.analyticsTools || []).includes(option)}
                      onChange={(e) => {
                        const currentAnalytics = formData.analyticsMonitoring?.analyticsTools || [];
                        if (e.target.checked) {
                          updateFormData('analyticsMonitoring', { 
                            analyticsTools: [...currentAnalytics, option] 
                          });
                        } else {
                          updateFormData('analyticsMonitoring', { 
                            analyticsTools: currentAnalytics.filter(a => a !== option) 
                          });
                        }
                      }}
                      className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-gray-700 text-sm">{option}</span>
                  </label>
                ))}
              </div>

              <div className="mt-4">
                <label className="block text-sm font-medium text-gray-600 mb-2">Preferred Choice</label>
                <select
                  value={formData.analyticsMonitoring?.defaultAnalytics || ''}
                  onChange={(e) => updateFormData('analyticsMonitoring', { defaultAnalytics: e.target.value })}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                >
                  <option value="">Select your preferred choice</option>
                  {['All are OK', 'Google Analytics', 'Plausible', 'Fathom', 'Mixpanel', 'Hotjar'].map((option) => (
                    <option key={option} value={option}>{option}</option>
                  ))}
                </select>
              </div>
            </div>
          </div>
        </div>

      </div>
    </div>
  );
}
