import React from 'react';
import { MultiStageFormData, StylingDesign } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function StylingDesignPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.stylingDesign;

  const handleInputChange = (field: keyof StylingDesign, value: string | string[]) => {
    updateFormData('stylingDesign', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof StylingDesign, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const cssFrameworks = [
    'Bootstrap',
    'Bulma',
    'DaisyUI',
    'Foundation',
    'Tailwind CSS'
  ];

  const cssInJs = [
    'Emotion',
    'Styled Components'
  ];

  const cssPreprocessors = [
    'PostCSS',
    'Sass (Syntactically Awesome Style Sheets)'
  ];

  const uiComponentLibraries = [
    'Ant Design',
    'Chakra UI',
    'Headless UI',
    'Mantine',
    'Material-UI (MUI)',
    'NextUI',
    'Radix UI',
    'shadcn/ui'
  ];

  const designTools = [
    'Adobe XD',
    'Figma',
    'Sketch'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Styling & Design</h2>
        <p className="text-gray-600">Choose your preferred UI/UX and design preferences</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Design & Prototyping Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Design & Prototyping Tools</h3>
        <p className="text-gray-600 mb-4">Select all design tools you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.designTools || []).length === designTools.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stylingDesign', { designTools: [...designTools] });
                } else {
                  updateFormData('stylingDesign', { designTools: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {designTools.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.designTools || []).includes(tool)}
                onChange={(e) => handleCheckboxChange('designTools', tool, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{tool}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Design Tool
          </label>
          <select
            value={data.defaultDesignTool || ''}
            onChange={(e) => handleInputChange('defaultDesignTool', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default tool</option>
            {designTools.map((tool) => (
              <option key={tool} value={tool}>{tool}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* CSS Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">CSS Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all CSS frameworks you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.cssFrameworks || []).length === cssFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stylingDesign', { cssFrameworks: [...cssFrameworks] });
                } else {
                  updateFormData('stylingDesign', { cssFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cssFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cssFrameworks || []).includes(framework)}
                onChange={(e) => handleCheckboxChange('cssFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default CSS Framework
          </label>
          <select
            value={data.defaultCSSFramework || ''}
            onChange={(e) => handleInputChange('defaultCSSFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default CSS framework</option>
            {cssFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* CSS-in-JS */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">CSS-in-JS Libraries</h3>
        <p className="text-gray-600 mb-4">Select all CSS-in-JS libraries you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.cssInJs || []).length === cssInJs.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stylingDesign', { cssInJs: [...cssInJs] });
                } else {
                  updateFormData('stylingDesign', { cssInJs: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cssInJs.map((library) => (
            <label key={library} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cssInJs || []).includes(library)}
                onChange={(e) => handleCheckboxChange('cssInJs', library, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{library}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default CSS-in-JS Library
          </label>
          <select
            value={data.defaultCSSInJS || ''}
            onChange={(e) => handleInputChange('defaultCSSInJS', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default CSS-in-JS library</option>
            {cssInJs.map((library) => (
              <option key={library} value={library}>{library}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* CSS Preprocessors */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">CSS Preprocessors</h3>
        <p className="text-gray-600 mb-4">Select all CSS preprocessors you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.cssPreprocessors || []).length === cssPreprocessors.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stylingDesign', { cssPreprocessors: [...cssPreprocessors] });
                } else {
                  updateFormData('stylingDesign', { cssPreprocessors: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cssPreprocessors.map((preprocessor) => (
            <label key={preprocessor} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cssPreprocessors || []).includes(preprocessor)}
                onChange={(e) => handleCheckboxChange('cssPreprocessors', preprocessor, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{preprocessor}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default CSS Preprocessor
          </label>
          <select
            value={data.defaultCSSPreprocessor || ''}
            onChange={(e) => handleInputChange('defaultCSSPreprocessor', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default CSS preprocessor</option>
            {cssPreprocessors.map((preprocessor) => (
              <option key={preprocessor} value={preprocessor}>{preprocessor}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* UI Component Libraries */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">UI Component Libraries</h3>
        <p className="text-gray-600 mb-4">Select all UI component libraries you&apos;re comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.uiComponentLibraries || []).length === uiComponentLibraries.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stylingDesign', { uiComponentLibraries: [...uiComponentLibraries] });
                } else {
                  updateFormData('stylingDesign', { uiComponentLibraries: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {uiComponentLibraries.map((library) => (
            <label key={library} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.uiComponentLibraries || []).includes(library)}
                onChange={(e) => handleCheckboxChange('uiComponentLibraries', library, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{library}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default UI Component Library
          </label>
          <select
            value={data.defaultUIComponentLibrary || ''}
            onChange={(e) => handleInputChange('defaultUIComponentLibrary', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
          >
            <option value="">Select default UI component library</option>
            {uiComponentLibraries.map((library) => (
              <option key={library} value={library}>{library}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>


    </div>
  );
}
