import React from 'react';
import { MultiStageFormData, TechStack } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function TechStackPage({ formData, updateFormData }: Props) {
  const data = formData.techStack;

  const handleInputChange = (field: keyof TechStack, value: string | string[]) => {
    updateFormData('techStack', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof TechStack, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const programmingLanguages = [
    'C#',
    'Elixir',
    'Go (Golang)',
    'Java',
    'JavaScript (JS)',
    'Kotlin',
    'PHP',
    'Python',
    'Ruby',
    'Rust',
    'TypeScript (TS)'
  ];

  const runtimes = [
    'Bun',
    'Deno',
    'Node.js'
  ];

  const fullStackFrameworks = [
    'Astro',
    'Next.js',
    'Nuxt',
    'Remix',
    'SolidStart',
    'SvelteKit'
  ];

  const frontendFrameworks = [
    'Angular',
    'Preact',
    'React',
    'SolidJS',
    'Svelte',
    'Vue.js'
  ];

  const backendFrameworks = [
    'ASP.NET Core',
    'Django',
    'Express.js',
    'FastAPI',
    'Fastify',
    'Flask',
    'Laravel',
    'NestJS',
    'Phoenix',
    'Ruby on Rails',
    'Spring Boot'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Tech Stack & Languages</h2>
        <p className="text-gray-600">Choose your preferred programming languages and frameworks</p>
      </div>

      {/* Programming Languages */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Programming Languages</h3>
        <p className="text-gray-600 mb-4">Select all programming languages you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.programmingLanguages || []).length === programmingLanguages.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { programmingLanguages: [...programmingLanguages] });
                } else {
                  updateFormData('techStack', { programmingLanguages: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {programmingLanguages.map((language) => (
            <label key={language} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.programmingLanguages || []).includes(language)}
                onChange={(e) => handleCheckboxChange('programmingLanguages', language, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{language}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Programming Language
          </label>
          <select
            value={data.defaultLanguage || ''}
            onChange={(e) => handleInputChange('defaultLanguage', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default language</option>
            {programmingLanguages.map((language) => (
              <option key={language} value={language}>{language}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Runtimes */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">JavaScript Runtimes</h3>
        <p className="text-gray-600 mb-4">Select all runtimes you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.runtimes || []).length === runtimes.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { runtimes: [...runtimes] });
                } else {
                  updateFormData('techStack', { runtimes: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {runtimes.map((runtime) => (
            <label key={runtime} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.runtimes || []).includes(runtime)}
                onChange={(e) => handleCheckboxChange('runtimes', runtime, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{runtime}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Runtime
          </label>
          <select
            value={data.defaultRuntime || ''}
            onChange={(e) => handleInputChange('defaultRuntime', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default runtime</option>
            {runtimes.map((runtime) => (
              <option key={runtime} value={runtime}>{runtime}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Full-Stack Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Full-Stack & Meta Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all full-stack frameworks you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.fullStackFrameworks || []).length === fullStackFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { fullStackFrameworks: [...fullStackFrameworks] });
                } else {
                  updateFormData('techStack', { fullStackFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {fullStackFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.fullStackFrameworks || []).includes(framework)}
                onChange={(e) => handleCheckboxChange('fullStackFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Full-Stack Framework
          </label>
          <select
            value={data.defaultFullStackFramework || ''}
            onChange={(e) => handleInputChange('defaultFullStackFramework', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default framework</option>
            {fullStackFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Frontend Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Frontend Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all frontend frameworks you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.frontendFrameworks || []).length === frontendFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { frontendFrameworks: [...frontendFrameworks] });
                } else {
                  updateFormData('techStack', { frontendFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {frontendFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.frontendFrameworks || []).includes(framework)}
                onChange={(e) => handleCheckboxChange('frontendFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Frontend Framework
          </label>
          <select
            value={data.defaultFrontend || ''}
            onChange={(e) => handleInputChange('defaultFrontend', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default framework</option>
            {frontendFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>

      {/* Backend Frameworks */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Backend Frameworks</h3>
        <p className="text-gray-600 mb-4">Select all backend frameworks you're comfortable with:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.backendFrameworks || []).length === backendFrameworks.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('techStack', { backendFrameworks: [...backendFrameworks] });
                } else {
                  updateFormData('techStack', { backendFrameworks: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {backendFrameworks.map((framework) => (
            <label key={framework} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.backendFrameworks || []).includes(framework)}
                onChange={(e) => handleCheckboxChange('backendFrameworks', framework, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{framework}</span>
            </label>
          ))}
        </div>

        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default Backend Framework
          </label>
          <select
            value={data.defaultBackend || ''}
            onChange={(e) => handleInputChange('defaultBackend', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default framework</option>
            {backendFrameworks.map((framework) => (
              <option key={framework} value={framework}>{framework}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>
    </div>
  );
}
