import React from 'react';
import { MultiStageFormData, AIIntegration } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function MCPServersPage({ formData, updateFormData, skipPage }: Props) {
  const aiData = formData.aiIntegration;

  const handleAIInputChange = (field: keyof AIIntegration, value: string | string[] | boolean) => {
    updateFormData('aiIntegration', { [field]: value });
  };

  const handleAICheckboxChange = (field: keyof AIIntegration, value: string, checked: boolean) => {
    const currentValues = (aiData[field] as string[]) || [];
    if (checked) {
      handleAIInputChange(field, [...currentValues, value]);
    } else {
      handleAIInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const mcpServers = [
    'Anthropic MCP Server',
    'Custom MCP Server',
    'Database MCP Server',
    'File System MCP Server',
    'Google MCP Server',
    'OpenAI MCP Server'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">MCP Servers</h2>
        <p className="text-gray-600">Choose your preferred MCP servers for AI integration</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* MCP Servers */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">MCP Servers</h3>
        <p className="text-gray-600 mb-4">Select all MCP servers you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.mcpServers || []).includes('Let the AI decide these for me')}
              onChange={(e) => handleAICheckboxChange('mcpServers', 'Let the AI decide these for me', e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">Let the AI decide these for me</span>
          </label>
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(aiData.mcpServers || []).length === mcpServers.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { mcpServers: [...mcpServers] });
                } else {
                  updateFormData('aiIntegration', { mcpServers: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>
          {mcpServers.map((server) => (
            <label key={server} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(aiData.mcpServers || []).includes(server)}
                onChange={(e) => handleAICheckboxChange('mcpServers', server, e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{server}</span>
            </label>
          ))}
        </div>
      </div>

    </div>
  );
}
