import React from 'react';
import { MultiStageFormData, CloudInfrastructure, SecurityAuth, DatabasesStorage, FileStorageEmail } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function CloudInfrastructurePage({ formData, updateFormData, skipPage }: Props) {
  const cloudData = formData.cloudInfrastructure || {};
  const securityData = formData.securityAuth || {};
  const dbData = formData.databasesStorage || {};
  const fileEmailData = formData.fileStorageEmail || {};

  const handleCloudInputChange = (field: keyof CloudInfrastructure, value: string | string[]) => {
    updateFormData('cloudInfrastructure', { [field]: value });
  };

  const handleSecurityInputChange = (field: keyof SecurityAuth, value: string | string[]) => {
    updateFormData('securityAuth', { [field]: value });
  };

  const handleDBCheckboxChange = (field: keyof DatabasesStorage, value: string, checked: boolean) => {
    const currentValues = (dbData[field] as string[]) || [];
    if (checked) {
      updateFormData('databasesStorage', { [field]: [...currentValues, value] });
    } else {
      updateFormData('databasesStorage', { [field]: currentValues.filter(v => v !== value) });
    }
  };

  const handleFileEmailInputChange = (field: keyof FileStorageEmail, value: string) => {
    updateFormData('fileStorageEmail', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof CloudInfrastructure, value: string, checked: boolean) => {
    const currentValues = (cloudData[field] as string[]) || [];
    if (checked) {
      updateFormData('cloudInfrastructure', { [field]: [...currentValues, value] });
    } else {
      updateFormData('cloudInfrastructure', { [field]: currentValues.filter(v => v !== value) });
    }
  };

  const handleSecurityCheckboxChange = (field: keyof SecurityAuth, value: string, checked: boolean) => {
    const currentValues = (securityData[field] as string[]) || [];
    if (checked) {
      updateFormData('securityAuth', { [field]: [...currentValues, value] });
    } else {
      updateFormData('securityAuth', { [field]: currentValues.filter(v => v !== value) });
    }
  };

  // AI Decision handlers for each section
  const handleAICloudCheckboxChange = (field: keyof CloudInfrastructure, value: string, checked: boolean) => {
    if (checked) {
      handleCloudInputChange(field, ['Let the AI decide these for me']);
    } else {
      handleCloudInputChange(field, []);
    }
  };

  const handleAISecurityCheckboxChange = (field: keyof SecurityAuth, value: string, checked: boolean) => {
    if (checked) {
      handleSecurityInputChange(field, ['Let the AI decide these for me']);
    } else {
      handleSecurityInputChange(field, []);
    }
  };

  const handleAIAuthMethodChange = (checked: boolean) => {
    if (checked) {
      handleSecurityInputChange('authMethod', 'Let the AI decide these for me');
    } else {
      handleSecurityInputChange('authMethod', '');
    }
  };

  const handleAIDBCheckboxChange = (field: keyof DatabasesStorage, value: string, checked: boolean) => {
    if (checked) {
      updateFormData('databasesStorage', { [field]: ['Let the AI decide these for me'] });
    } else {
      updateFormData('databasesStorage', { [field]: [] });
    }
  };

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Cloud & Security</h2>
        <p className="text-gray-600">Configure your cloud infrastructure, hosting, and security preferences</p>
      </div>

      {/* Skip Option */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 flex flex-col space-y-3">
        <span className="text-blue-700 text-sm">This section is optional. If it does not apply, you can continue to the next one.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-gray-100 text-gray-700 border border-gray-300 rounded-md hover:bg-gray-200 transition-colors w-full"
        >
          Skip Section
        </button>
      </div>

      {/* Section 1: Core Infrastructure & Hosting */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Core Infrastructure & Hosting</h3>
        <p className="text-gray-600 mb-6">Select your preferred cloud providers and hosting models</p>

        {/* Primary Cloud Ecosystems */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">Primary Cloud Ecosystems</label>

          {/* AI Decision Checkbox with separation */}
          <div className="border-b border-gray-200 pb-4 mb-4">
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(cloudData.cloudHosting || []).includes('Let the AI decide these for me')}
                onChange={(e) => handleAICloudCheckboxChange('cloudHosting', 'Let the AI decide these for me', e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
            </label>
          </div>

          <div className="space-y-2">
            {['AWS (Amazon Web Services)', 'GCP (Google Cloud Platform)', 'Microsoft Azure', 'Cloudflare', 'DigitalOcean', 'Other / No Preference'].map((provider) => (
              <label key={provider} className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(cloudData.cloudHosting || []).includes(provider)}
                  onChange={(e) => handleCheckboxChange('cloudHosting', provider, e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">{provider}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Visual Divider */}
        <hr className="my-8 border-gray-200" />

        {/* Hosting & Deployment Model */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">Hosting & Deployment Model</h3>

          {/* IaaS */}
          <div className="mb-8">
            <h4 className="text-md font-semibold text-blue-600 mb-3">IaaS (Virtual Machines)</h4>

            {/* AI Decision Checkbox with separation */}
            <div className="border-b border-gray-200 pb-4 mb-4">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={cloudData.defaultIaaS === 'Let the AI decide these for me'}
                  onChange={(e) => handleCloudInputChange('defaultIaaS', e.target.checked ? 'Let the AI decide these for me' : '')}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
              </label>
            </div>

            <label className="block text-sm font-medium text-gray-700 mb-2">Preferred IaaS Provider</label>
            <select
              value={cloudData.defaultIaaS || ''}
              onChange={(e) => handleCloudInputChange('defaultIaaS', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            >
              <option value="">All are OK</option>
              <option value="AWS EC2">AWS EC2</option>
              <option value="Google Compute Engine">Google Compute Engine</option>
              <option value="Azure VMs">Azure VMs</option>
              <option value="DigitalOcean Droplets">DigitalOcean Droplets</option>
              <option value="Linode">Linode</option>
              <option value="Vultr">Vultr</option>
            </select>
          </div>

          {/* Visual Divider */}
          <hr className="my-6 border-gray-200" />

          {/* PaaS / Serverless */}
          <div className="mb-8">
            <h4 className="text-md font-semibold text-blue-600 mb-3">PaaS / Serverless Platforms</h4>

            {/* AI Decision Checkbox with separation */}
            <div className="border-b border-gray-200 pb-4 mb-4">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={cloudData.defaultPaaS === 'Let the AI decide these for me'}
                  onChange={(e) => handleCloudInputChange('defaultPaaS', e.target.checked ? 'Let the AI decide these for me' : '')}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
              </label>
            </div>

            <label className="block text-sm font-medium text-gray-700 mb-2">Preferred PaaS Provider</label>
            <select
              value={cloudData.defaultPaaS || ''}
              onChange={(e) => handleCloudInputChange('defaultPaaS', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            >
              <option value="">All are OK</option>
              <option value="Vercel">Vercel</option>
              <option value="Netlify">Netlify</option>
              <option value="Railway">Railway</option>
              <option value="Heroku">Heroku</option>
              <option value="Fly.io">Fly.io</option>
              <option value="Render">Render</option>
              <option value="Platform.sh">Platform.sh</option>
            </select>
          </div>

          {/* Visual Divider */}
          <hr className="my-6 border-gray-200" />

          {/* Container Platforms */}
          <div className="mb-8">
            <h4 className="text-md font-semibold text-blue-600 mb-3">Container Platforms</h4>
            <label className="block text-sm font-medium text-gray-700 mb-2">Preferred Container Platform</label>
            <select
              value={cloudData.defaultContainer || ''}
              onChange={(e) => handleCloudInputChange('defaultContainer', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            >
              <option value="">All are OK</option>
              <option value="Kubernetes (Managed K8s)">Kubernetes (Managed K8s - GKE, EKS, AKS)</option>
              <option value="Serverless Containers">Serverless Containers (Google Cloud Run, AWS Fargate)</option>
              <option value="Docker">Docker</option>
            </select>
          </div>

          {/* Visual Divider */}
          <hr className="my-6 border-gray-200" />

          {/* Functions as a Service */}
          <div className="mb-8">
            <h4 className="text-md font-semibold text-blue-600 mb-3">Functions as a Service (FaaS)</h4>
            <label className="block text-sm font-medium text-gray-700 mb-2">Preferred FaaS Provider</label>
            <select
              value={cloudData.defaultServerless || ''}
              onChange={(e) => handleCloudInputChange('defaultServerless', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            >
              <option value="">All are OK</option>
              <option value="AWS Lambda">AWS Lambda</option>
              <option value="Google Cloud Functions">Google Cloud Functions</option>
              <option value="Cloudflare Workers">Cloudflare Workers</option>
              <option value="Vercel/Netlify Functions">Vercel/Netlify Functions</option>
            </select>
          </div>
        </div>

        {/* Networking */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">Networking</label>

          <div className="space-y-6">
            <div>
              <h4 className="text-md font-semibold text-blue-600 mb-3">DNS Provider</h4>
              <label className="block text-sm font-medium text-gray-700 mb-2">Preferred DNS Provider</label>
              <select
                value={securityData.defaultDNS || ''}
                onChange={(e) => handleSecurityInputChange('defaultDNS', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              >
                <option value="">All are OK</option>
                <option value="Cloudflare">Cloudflare</option>
                <option value="AWS Route 53">AWS Route 53</option>
                <option value="Google Cloud DNS">Google Cloud DNS</option>
                <option value="GoDaddy">GoDaddy</option>
                <option value="Namecheap">Namecheap</option>
              </select>
            </div>

            {/* Visual Divider */}
            <hr className="my-6 border-gray-200" />

            <div>
              <h4 className="text-md font-semibold text-blue-600 mb-3">CDN Provider</h4>
              <label className="block text-sm font-medium text-gray-700 mb-2">Preferred CDN Provider</label>
              <select
                value={securityData.defaultCDN || ''}
                onChange={(e) => handleSecurityInputChange('defaultCDN', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              >
                <option value="">All are OK</option>
                <option value="Cloudflare">Cloudflare</option>
                <option value="AWS CloudFront">AWS CloudFront</option>
                <option value="Fastly">Fastly</option>
                <option value="Google Cloud CDN">Google Cloud CDN</option>
                <option value="Bunny CDN">Bunny CDN</option>
              </select>
            </div>

            {/* Visual Divider */}
            <hr className="my-6 border-gray-200" />

            <div>
              <h4 className="text-md font-semibold text-blue-600 mb-3">SSL Certificate Provider</h4>
              <label className="block text-sm font-medium text-gray-700 mb-2">Preferred SSL Certificate Provider</label>
              <select
                value={securityData.defaultSSL || ''}
                onChange={(e) => handleSecurityInputChange('defaultSSL', e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              >
                <option value="">All are OK</option>
                <option value="Let's Encrypt">Let's Encrypt (via platform)</option>
                <option value="Cloudflare">Cloudflare (Managed)</option>
                <option value="AWS Certificate Manager">AWS Certificate Manager (ACM)</option>
                <option value="Manual Purchase">Manual Purchase (e.g., DigiCert)</option>
              </select>
            </div>
          </div>
        </div>

        {/* Default Hosting Region & Domain Registrar */}
        <div className="space-y-6">
          <div>
            <h4 className="text-md font-semibold text-blue-600 mb-3">Default Hosting Region</h4>
            <label className="block text-sm font-medium text-gray-700 mb-2">Preferred Hosting Region</label>
            <input
              type="text"
              value={cloudData.hostingRegion || ''}
              onChange={(e) => handleCloudInputChange('hostingRegion', e.target.value)}
              placeholder="e.g., us-east-1, us-central"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>

          {/* Visual Divider */}
          <hr className="my-6 border-gray-200" />

          <div>
            <h4 className="text-md font-semibold text-blue-600 mb-3">Domain Registrar</h4>
            <label className="block text-sm font-medium text-gray-700 mb-2">Preferred Domain Registrar</label>
            <input
              type="text"
              value={cloudData.domainRegistrar || ''}
              onChange={(e) => handleCloudInputChange('domainRegistrar', e.target.value)}
              placeholder="e.g., Cloudflare, GoDaddy, Namecheap"
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
        </div>
      </div>

      {/* Section 2: Application Services & Data */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Application Services & Data</h3>
        <p className="text-gray-600 mb-6">Configure databases, storage, and email services</p>

        {/* Databases */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">Databases</label>

          {/* AI Decision Checkbox with separation */}
          <div className="border-b border-gray-200 pb-4 mb-4">
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(dbData.databases || []).includes('Let the AI decide these for me')}
                onChange={(e) => handleAIDBCheckboxChange('databases', 'Let the AI decide these for me', e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
            </label>
          </div>

          <div className="space-y-2">
            {['PostgreSQL', 'MySQL', 'MongoDB', 'Redis', 'SQLite', 'DynamoDB (NoSQL)', 'Elasticsearch', 'Supabase (Postgres)'].map((db) => (
              <label key={db} className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(dbData.databases || []).includes(db)}
                  onChange={(e) => handleDBCheckboxChange('databases', db, e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">{db}</span>
              </label>
            ))}
          </div>
        </div>

        {/* ORM & Query Builders */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">ORM & Query Builders</label>
          <select
            value={dbData.defaultORM || ''}
            onChange={(e) => updateFormData('databasesStorage', { defaultORM: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="Prisma">Prisma</option>
            <option value="Drizzle ORM">Drizzle ORM</option>
            <option value="TypeORM">TypeORM</option>
            <option value="Sequelize">Sequelize</option>
            <option value="Mongoose">Mongoose</option>
          </select>
        </div>

        {/* File/Object Storage */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">File/Object Storage</label>
          <select
            value={fileEmailData.fileStorage || ''}
            onChange={(e) => handleFileEmailInputChange('fileStorage', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="AWS S3">AWS S3</option>
            <option value="Google Cloud Storage">Google Cloud Storage</option>
            <option value="Cloudflare R2">Cloudflare R2</option>
            <option value="Azure Blob Storage">Azure Blob Storage</option>
            <option value="Backblaze B2">Backblaze B2</option>
            <option value="DigitalOcean Spaces">DigitalOcean Spaces</option>
          </select>
        </div>

        {/* Email Service Provider */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Email Service Provider</label>
          <select
            value={fileEmailData.emailProvider || ''}
            onChange={(e) => handleFileEmailInputChange('emailProvider', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="Resend">Resend</option>
            <option value="Postmark">Postmark</option>
            <option value="AWS SES">AWS SES</option>
            <option value="SendGrid">SendGrid</option>
            <option value="Mailgun">Mailgun</option>
          </select>
        </div>
      </div>

      {/* Section 3: Security & Authentication */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Security & Authentication</h3>
        <p className="text-gray-600 mb-6">Configure user identity, login methods, and application security</p>

        {/* Authentication Strategy */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">Authentication Strategy</label>

          {/* AI Decision Checkbox with separation */}
          <div className="border-b border-gray-200 pb-4 mb-4">
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={securityData.authMethod === 'Let the AI decide these for me'}
                onChange={(e) => handleAIAuthMethodChange(e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
            </label>
          </div>

          <div className="space-y-2">
            {['Session-based', 'JWT (JSON Web Tokens)', 'OAuth 2.0 / OpenID Connect'].map((strategy) => (
              <label key={strategy} className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(securityData.authMethod ? [securityData.authMethod] : []).includes(strategy)}
                  onChange={(e) => handleSecurityInputChange('authMethod', e.target.checked ? strategy : '')}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">{strategy}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Identity & Login Providers */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">Identity & Login Providers</label>

          <div className="mb-4">
            <label className="block text-sm font-medium text-gray-600 mb-2">Social Logins</label>

            {/* AI Decision Checkbox with separation */}
            <div className="border-b border-gray-200 pb-4 mb-4">
              <label className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(securityData.socialLogins || []).includes('Let the AI decide these for me')}
                  onChange={(e) => handleAISecurityCheckboxChange('socialLogins', 'Let the AI decide these for me', e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
              </label>
            </div>

            <div className="space-y-2">
              {['Google', 'GitHub', 'Microsoft', 'Apple', 'Discord', 'Facebook'].map((provider) => (
                <label key={provider} className="flex items-center space-x-3 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(securityData.socialLogins || []).includes(provider)}
                                         onChange={(e) => handleSecurityCheckboxChange('socialLogins', provider, e.target.checked)}
                    className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                  />
                  <span className="text-gray-700 text-sm">{provider}</span>
                </label>
              ))}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-600 mb-2">Enterprise SSO</label>
            <select
              value={securityData.enterpriseSSO || ''}
              onChange={(e) => handleSecurityInputChange('enterpriseSSO', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
            >
              <option value="">All are OK</option>
              <option value="SAML 2.0">SAML 2.0</option>
              <option value="Okta">Okta</option>
              <option value="Auth0">Auth0</option>
              <option value="Azure AD">Azure AD</option>
              <option value="Keycloak">Keycloak</option>
            </select>
          </div>
        </div>

        {/* Two-Factor Authentication */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">Two-Factor Authentication (2FA) Method</label>
          <select
            value={securityData.twoFactorAuth || ''}
            onChange={(e) => handleSecurityInputChange('twoFactorAuth', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="Authenticator App (TOTP)">Authenticator App (TOTP)</option>
            <option value="Hardware Security Keys">Hardware Security Keys</option>
            <option value="SMS">SMS</option>
            <option value="Email">Email</option>
            <option value="None Required">None Required</option>
          </select>
        </div>
      </div>

      {/* Section 4: Development & Operations (DevOps) */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Development & Operations (DevOps)</h3>
        <p className="text-gray-600 mb-6">Configure tools for building, testing, deploying, and monitoring</p>

        {/* CI/CD Platform */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">CI/CD Platform</label>
          <select
            value={cloudData.defaultCicdTool || ''}
            onChange={(e) => handleCloudInputChange('defaultCicdTool', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="GitHub Actions">GitHub Actions</option>
            <option value="GitLab CI/CD">GitLab CI/CD</option>
            <option value="CircleCI">CircleCI</option>
            <option value="Jenkins">Jenkins</option>
            <option value="Azure DevOps">Azure DevOps</option>
          </select>
        </div>

        {/* Code Quality & Formatting */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-3">Code Quality & Formatting</label>

          {/* AI Decision Checkbox with separation */}
          <div className="border-b border-gray-200 pb-4 mb-4">
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(cloudData.cicdTools || []).includes('Let the AI decide these for me')}
                onChange={(e) => handleAICloudCheckboxChange('cicdTools', 'Let the AI decide these for me', e.target.checked)}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-purple-600 font-medium">Let the AI decide these for me</span>
            </label>
          </div>

          <div className="space-y-2">
            {['ESLint (Linting)', 'Prettier (Formatting)', 'Ruff (Python Linting/Formatting)', 'Stylelint (CSS Linting)', 'Husky / lint-staged (Git Hooks)'].map((tool) => (
              <label key={tool} className="flex items-center space-x-3 cursor-pointer">
                <input
                  type="checkbox"
                  checked={(cloudData.cicdTools || []).includes(tool)}
                  onChange={(e) => handleCheckboxChange('cicdTools', tool, e.target.checked)}
                  className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
                />
                <span className="text-gray-700 text-sm">{tool}</span>
              </label>
            ))}
          </div>
        </div>

        {/* Testing Frameworks */}
        <div className="mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">Testing Frameworks</label>
          <select
                         value={formData.testingCodeQuality?.defaultTestingFramework || ''}
             onChange={(e) => updateFormData('testingCodeQuality', { defaultTestingFramework: e.target.value })}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="Jest">Jest</option>
            <option value="Vitest">Vitest</option>
            <option value="Playwright">Playwright</option>
            <option value="Cypress">Cypress</option>
            <option value="Pytest">Pytest</option>
            <option value="React Testing Library">React Testing Library</option>
          </select>
        </div>

        {/* Monitoring & Analytics */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-3">Monitoring & Analytics</label>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-600 mb-2">Error & Performance Monitoring</label>
              <select
                                 value={formData.analyticsMonitoring?.defaultPerformanceMonitoring || ''}
                 onChange={(e) => updateFormData('analyticsMonitoring', { defaultPerformanceMonitoring: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              >
                <option value="">All are OK</option>
                <option value="Sentry">Sentry</option>
                <option value="Datadog">Datadog</option>
                <option value="New Relic">New Relic</option>
                <option value="LogRocket">LogRocket</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-600 mb-2">Logging Service</label>
              <select
                                 value={formData.analyticsMonitoring?.defaultLogging || ''}
                 onChange={(e) => updateFormData('analyticsMonitoring', { defaultLogging: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              >
                <option value="">All are OK</option>
                <option value="Datadog">Datadog</option>
                <option value="AWS CloudWatch">AWS CloudWatch</option>
                <option value="Logtail (BetterStack)">Logtail (BetterStack)</option>
                <option value="Platform-native logging">Platform-native logging</option>
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-600 mb-2">User Analytics</label>
              <select
                                 value={formData.analyticsMonitoring?.defaultAnalytics || ''}
                 onChange={(e) => updateFormData('analyticsMonitoring', { defaultAnalytics: e.target.value })}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
              >
                <option value="">All are OK</option>
                <option value="Google Analytics">Google Analytics</option>
                <option value="Plausible">Plausible</option>
                <option value="Fathom">Fathom</option>
                <option value="Mixpanel">Mixpanel</option>
                <option value="Hotjar">Hotjar</option>
              </select>
            </div>
          </div>
        </div>

        {/* Cloud Development Environment */}
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">Cloud Development Environment</label>
          <select
            value={cloudData.defaultCDE || ''}
            onChange={(e) => handleCloudInputChange('defaultCDE', e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
          >
            <option value="">All are OK</option>
            <option value="GitHub Codespaces">GitHub Codespaces</option>
            <option value="Gitpod">Gitpod</option>
            <option value="StackBlitz / CodeSandbox">StackBlitz / CodeSandbox</option>
            <option value="Not Used (Local a must)">Not Used (Local a must)</option>
          </select>
        </div>
      </div>
    </div>
  );
}
