import React from 'react';
import { MultiStageFormData, HeadlessCMS } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function HeadlessCMSPage({ formData, updateFormData }: Props) {
  const data = formData.headlessCMS;

  const handleInputChange = (field: keyof HeadlessCMS, value: string | string[]) => {
    updateFormData('headlessCMS', { [field]: value });
  };

  const cmsPlatforms = [
    'Contentful',
    'Sanity.io',
    'Storyblok',
    'Strapi'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Headless CMS</h2>
        <p className="text-gray-600">Choose your preferred content management systems</p>
      </div>

      {/* CMS Platforms */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">CMS Platforms</h3>
        <p className="text-gray-600 mb-4">Select all CMS platforms you&apos;re comfortable with:</p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.cmsPlatforms || []).length === cmsPlatforms.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('headlessCMS', { cmsPlatforms: [...cmsPlatforms] });
                } else {
                  updateFormData('headlessCMS', { cmsPlatforms: [] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 font-medium">All are OK</span>
          </label>

          {cmsPlatforms.map((cms) => (
            <label key={cms} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="checkbox"
                checked={(data.cmsPlatforms || []).includes(cms)}
                onChange={(e) => handleInputChange('cmsPlatforms', [...(data.cmsPlatforms || []).filter(v => v !== cms), ...(e.target.checked ? [cms] : [])])}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{cms}</span>
            </label>
          ))}
        </div>
        <div className="mt-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Default CMS
          </label>
          <select
            value={data.defaultCMS || ''}
            onChange={(e) => handleInputChange('defaultCMS', e.target.value)}
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
          >
            <option value="">Select default CMS</option>
            {cmsPlatforms.map((cms) => (
              <option key={cms} value={cms}>{cms}</option>
            ))}
            <option value="NO PREFERENCE - all are ok">NO PREFERENCE - all are ok</option>
          </select>
        </div>
      </div>
    </div>
  );
}
