import React from 'react';
import { MultiStageFormData, AIIntegration } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function AIIntegrationPage({ formData, updateFormData }: Props) {
  const data = formData.aiIntegration;

  const handleInputChange = (field: keyof AIIntegration, value: string | string[] | boolean) => {
    updateFormData('aiIntegration', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof AIIntegration, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const llmAgents = [
    'Anthropic Claude',
    'AutoGPT',
    'BabyAGI',
    'Cohere',
    'Google Gemini',
    'Hugging Face',
    'LangChain',
    'Meta Llama',
    'Mistral AI',
    'OpenAI GPT-4'
  ];

  const videoGeneration = [
    'AnimateDiff',
    'Gen-2 (Runway)',
    'Imagen Video (Google)',
    'Make-A-Video (Meta)',
    'Pika Labs',
    'Runway ML',
    'Stable Video Diffusion',
    'Text2Video-Zero'
  ];

  const audioGeneration = [
    'Amazon Polly',
    'Azure Speech Service',
    'Coqui TTS',
    'Descript',
    'ElevenLabs',
    'Google Cloud Text-to-Speech',
    'Murf AI',
    'Play.ht',
    'Resemble AI',
    'Synthesia'
  ];

  const ocr = [
    'ABBYY FineReader',
    'Adobe Acrobat OCR',
    'AWS Textract',
    'Azure Computer Vision',
    'Google Cloud Vision API',
    'OCR.space',
    'PaddleOCR',
    'Tesseract OCR'
  ];

  const mcpServers = [
    'Anthropic MCP Server',
    'Custom MCP Server',
    'Database MCP Server',
    'File System MCP Server',
    'Google MCP Server',
    'OpenAI MCP Server'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">AI & MCPs</h2>
        <p className="text-gray-600">Choose your preferred AI services and tools</p>
      </div>

      {/* LLM Agents */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">LLM Agents & Language Models</h3>
        <p className="text-gray-600 mb-4">Select all LLM agents and language models you want to integrate:</p>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="checkbox"
              checked={(data.llmAgents || []).length === llmAgents.length}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('aiIntegration', { llmAgents: [...llmAgents] });
                } else {
