import React from 'react';
import { MultiStageFormData, StylingDesign, ColorsFonts } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function StylingDesignPage({ formData, updateFormData, skipPage }: Props) {
  const data = formData.stylingDesign;
  const colorsData = formData.colorsFonts || {};

  const handleInputChange = (field: keyof StylingDesign, value: string | string[]) => {
    updateFormData('stylingDesign', { [field]: value });
  };

  const handleCheckboxChange = (field: keyof StylingDesign, value: string, checked: boolean) => {
    const currentValues = (data[field] as string[]) || [];
    if (checked) {
      handleInputChange(field, [...currentValues, value]);
    } else {
      handleInputChange(field, currentValues.filter(v => v !== value));
    }
  };

  const handleColorsInputChange = (field: keyof ColorsFonts, value: string | boolean) => {
    updateFormData('colorsFonts', { [field]: value });
  };

  const handleCustomColorChange = (colorType: 'primaryBackground' | 'secondaryBackground' | 'accent' | 'text' | 'heading', value: string) => {
    const currentCustomColors = colorsData.customColors || {};
    updateFormData('colorsFonts', {
      customColors: {
        ...currentCustomColors,
        [colorType]: value
      }
    });
  };

  const getColorValue = (colorType: 'primaryBackground' | 'secondaryBackground' | 'accent' | 'text' | 'heading') => {
    const customColors = colorsData.customColors || {};
    if (customColors[colorType]) {
      return customColors[colorType];
    }

    // Default colors if no custom colors or theme selected
    const defaultColors = {
      primaryBackground: '#FFFFFF',
      secondaryBackground: '#F8F9FA',
      accent: '#0D6EFD',
      text: '#6C757D',
      heading: '#212529'
    };

    return defaultColors[colorType];
  };

  const cssFrameworks = [
    'Bootstrap',
    'Bulma',
    'DaisyUI',
    'Foundation',
    'Open Props',
    'Tailwind CSS'
  ];

  const styledUILibraries = [
    'Ant Design',
    'Chakra UI',
    'Fluent UI',
    'Mantine',
    'Material-UI (MUI)',
    'NextUI',
    'Vuetify'
  ];

  const headlessUILibraries = [
    'Headless UI',
    'Radix UI'
  ];

  const recipeUILibraries = [
    'Aceternity UI',
    'Catalyst',
    'Draft UI',
    'Keep React',
    'shadcn/ui',
    'shadcn-svelte',
    'shadcn-vue'
  ];

  const designTools = [
    'Adobe XD',
    'Figma',
    'Sketch'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">Styling & Design</h2>
        <p className="text-gray-600">Choose your preferred UI/UX and design preferences</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col space-y-3">
        <span className="text-yellow-700 text-sm">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full"
        >
          Save & Skip
        </button>
      </div>

      {/* Design Approach Selection */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Add Some Screenshots For Color and Design Inspiration</h3>
        <p className="text-gray-600 mb-6">Either upload your screenshots or choose your preferred colors and styles below.</p>

        {/* Radio Button Selection */}
        <div className="mb-6">
          <div className="space-y-3">
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="designApproach"
                value="screenshots"
                checked={data.designApproach === 'screenshots'}
                onChange={(e) => handleInputChange('designApproach', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 font-medium">Upload Screenshots for Design Inspiration</span>
            </label>
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="designApproach"
                value="manual"
                checked={data.designApproach === 'manual'}
                onChange={(e) => handleInputChange('designApproach', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 font-medium">Manually Choose Colors and Styles</span>
            </label>
          </div>
        </div>

        {/* Screenshot Upload Section */}
        {data.designApproach === 'screenshots' && (
          <div className="space-y-4">
            <p className="text-gray-600 text-sm">Upload screenshots of your preferred design inspiration. We'll analyze these to create a matching color palette and design system.</p>

            {/* Home Page Screenshot */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Home Page (Banner/Index Page)
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    updateFormData('stylingDesign', { homePageScreenshot: file });
                  }
                }}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              />
            </div>

            {/* Form Page Screenshot */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Product Form Page
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    updateFormData('stylingDesign', { formPageScreenshot: file });
                  }
                }}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              />
            </div>

            {/* Sign-up Page Screenshot */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Sign-up Page
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    updateFormData('stylingDesign', { signupPageScreenshot: file });
                  }
                }}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              />
            </div>

            {/* Pricing Page Screenshot */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Pricing Page
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    updateFormData('stylingDesign', { pricingPageScreenshot: file });
                  }
                }}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              />
            </div>
          </div>
        )}

        {/* Manual Color Selection */}
        {data.designApproach === 'manual' && (
          <>
            {/* Color Theme Selection */}
            <div className="mb-6">
              <h4 className="text-md font-medium text-gray-800 mb-3">Color Theme</h4>
              <div className="space-y-3">
                {[
                  'Modern & Clean',
                  'Warm & Friendly',
                  'Professional & Corporate',
                  'Bold & Creative',
                  'Minimal & Elegant',
                  'Vibrant & Energetic',
                  'Dark & Sophisticated',
                  'Nature-Inspired',
                  'Tech & Futuristic',
                  'Custom Colors'
                ].map((theme) => (
                  <label key={theme} className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="radio"
                      name="colorTheme"
                      checked={colorsData.selectedTheme === theme}
                      onChange={(e) => handleColorsInputChange('selectedTheme', e.target.value)}
                      value={theme}
                      className="text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-gray-700 text-sm">{theme}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Custom Color Inputs */}
            {colorsData.selectedTheme === 'Custom Colors' && (
              <div className="space-y-4">
                <h4 className="text-md font-medium text-gray-800">Custom Color Palette</h4>

                {/* Primary Background */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Primary Background Color
                  </label>
                  <div className="flex space-x-2">
                    <input
                      type="color"
                      value={getColorValue('primaryBackground')}
                      onChange={(e) => handleCustomColorChange('primaryBackground', e.target.value)}
                      className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                    />
                    <input
                      type="text"
                      value={getColorValue('primaryBackground')}
                      onChange={(e) => handleCustomColorChange('primaryBackground', e.target.value)}
                      className="flex-1 p-2 border border-gray-300 rounded-md text-sm"
                      placeholder="#FFFFFF"
                    />
                  </div>
                </div>

                {/* Secondary Background */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Secondary Background Color
                  </label>
                  <div className="flex space-x-2">
                    <input
                      type="color"
                      value={getColorValue('secondaryBackground')}
                      onChange={(e) => handleCustomColorChange('secondaryBackground', e.target.value)}
                      className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                    />
                    <input
                      type="text"
                      value={getColorValue('secondaryBackground')}
                      onChange={(e) => handleCustomColorChange('secondaryBackground', e.target.value)}
                      className="flex-1 p-2 border border-gray-300 rounded-md text-sm"
                      placeholder="#F8F9FA"
                    />
                  </div>
                </div>

                {/* Accent Color */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Accent Color
                  </label>
                  <div className="flex space-x-2">
                    <input
                      type="color"
                      value={getColorValue('accent')}
                      onChange={(e) => handleCustomColorChange('accent', e.target.value)}
                      className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                    />
                    <input
                      type="text"
                      value={getColorValue('accent')}
                      onChange={(e) => handleCustomColorChange('accent', e.target.value)}
                      className="flex-1 p-2 border border-gray-300 rounded-md text-sm"
                      placeholder="#0D6EFD"
                    />
                  </div>
                </div>

                {/* Text Color */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Text Color
                  </label>
                  <div className="flex space-x-2">
                    <input
                      type="color"
                      value={getColorValue('text')}
                      onChange={(e) => handleCustomColorChange('text', e.target.value)}
                      className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                    />
                    <input
                      type="text"
                      value={getColorValue('text')}
                      onChange={(e) => handleCustomColorChange('text', e.target.value)}
                      className="flex-1 p-2 border border-gray-300 rounded-md text-sm"
                      placeholder="#6C757D"
                    />
                  </div>
                </div>

                {/* Heading Color */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Heading Color
                  </label>
                  <div className="flex space-x-2">
                    <input
                      type="color"
                      value={getColorValue('heading')}
                      onChange={(e) => handleCustomColorChange('heading', e.target.value)}
                      className="w-12 h-10 border border-gray-300 rounded cursor-pointer"
                    />
                    <input
                      type="text"
                      value={getColorValue('heading')}
                      onChange={(e) => handleCustomColorChange('heading', e.target.value)}
                      className="flex-1 p-2 border border-gray-300 rounded-md text-sm"
                      placeholder="#212529"
                    />
                  </div>
                </div>
              </div>
            )}

            {/* Customize Fonts */}
            <div className="mt-6">
              <h4 className="text-md font-medium text-gray-800 mb-3">Font Family</h4>
              <div className="space-y-3">
                {[
                  'Arial',
                  'Helvetica',
                  'Roboto',
                  'Open Sans',
                  'Lato',
                  'Source Sans Pro',
                  'Inter',
                  'Georgia',
                  'Times New Roman',
                  'Courier New'
                ].map((font) => (
                  <label key={font} className="flex items-center space-x-3 cursor-pointer">
                    <input
                      type="radio"
                      name="fontFamily"
                      checked={colorsData.fontFamily === font}
                      onChange={(e) => handleColorsInputChange('fontFamily', e.target.value)}
                      value={font}
                      className="text-blue-600 focus:ring-blue-500"
                    />
                    <span className="text-gray-700 text-sm" style={{ fontFamily: font }}>{font}</span>
                  </label>
                ))}
              </div>
            </div>
          </>
        )}
      </div>

      {/* Design & Prototyping Tools */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Design & Prototyping Tools</h3>
        <p className="text-gray-600 mb-4">Select the design tool you use:</p>

        <div className="grid grid-cols-1 gap-3">
          {/* NONE option */}
          <label className="flex items-center space-x-3 cursor-pointer">
            <input
              type="radio"
              name="designTools"
              checked={(data.designTools || []).includes('NONE') || (data.designTools || []).length === 0}
              onChange={(e) => {
                if (e.target.checked) {
                  updateFormData('stylingDesign', { designTools: ['NONE'] });
                }
              }}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700 text-sm font-medium">NONE</span>
          </label>

          {designTools.map((tool) => (
            <label key={tool} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="designTools"
                checked={(data.designTools || []).includes(tool)}
                onChange={(e) => {
                  if (e.target.checked) {
                    updateFormData('stylingDesign', { designTools: [tool] });
                  }
                }}
                className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 text-sm">{tool}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Design Approach Selection */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Add Some Screenshots For Color and Design Inspiration</h3>
        <p className="text-gray-600 mb-6">Either upload your screenshots or choose your preferred colors and styles below.</p>

        {/* Radio Button Selection */}
        <div className="mb-6">
          <div className="space-y-3">
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="designApproach"
                value="screenshots"
                checked={data.designApproach === 'screenshots'}
                onChange={(e) => handleInputChange('designApproach', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 font-medium">Upload Screenshots for Design Inspiration</span>
            </label>
            <label className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="designApproach"
                value="manual"
                checked={data.designApproach === 'manual'}
                onChange={(e) => handleInputChange('designApproach', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700 font-medium">Manually Choose Colors and Styles</span>
            </label>
          </div>
        </div>

        {/* Screenshot Upload Section */}
        {data.designApproach === 'screenshots' && (
          <div className="space-y-4">
            <p className="text-gray-600 text-sm">Upload screenshots of your preferred design inspiration. We'll analyze these to create a matching color palette and design system.</p>

            {/* Home Page Screenshot */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Home Page (Banner/Index Page)
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    updateFormData('stylingDesign', { homePageScreenshot: file });
                  }
                }}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              />
            </div>

            {/* Form Page Screenshot */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Product Form Page
              </label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const file = e.target.files?.[0];
                  if (file) {
                    updateFormData('stylingDesign', { formPageScreenshot: file });
                  }
                }}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
              />
            </div>

