import React from 'react';
import { MultiStageFormData, AIIntegration } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function AIPage({ formData, updateFormData, skipPage }: Props) {
  const aiData = formData.aiIntegration;

  const handleAIInputChange = (field: keyof AIIntegration, value: string | string[] | boolean) => {
    updateFormData('aiIntegration', { [field]: value });
  };

  const handleAICheckboxChange = (field: keyof AIIntegration, value: string, checked: boolean) => {
    const currentValues = (aiData[field] as string[]) || [];
    if (checked) {
      handleAIInputChange(field, [...currentValues, value]);
    } else {
      handleAIInputChange(field, currentValues.filter(v => v !== value));
    }
  };



  const llmAgents = [
    'Anthropic Claude',
    'AutoGPT',
    'BabyAGI',
    'Cohere',
    'Google Gemini',
    'Hugging Face',
    'LangChain',
    'Meta Llama',
    'Mistral AI',
    'OpenAI GPT-4'
  ];

  const videoGeneration = [
    'AnimateDiff',
    'Gen-2 (Runway)',
    'Imagen Video (Google)',
    'Make-A-Video (Meta)',
    'Pika Labs',
    'Runway ML',
    'Stable Video Diffusion',
    'Text2Video-Zero'
  ];

  const audioGeneration = [
    'Amazon Polly',
    'Azure Speech Service',
    'Coqui TTS',
    'Descript',
    'ElevenLabs',
    'Google Cloud Text-to-Speech',
    'Murf AI',
    'Play.ht',
    'Resemble AI',
    'Synthesia'
  ];

  const ocr = [
    'ABBYY FineReader',
    'Adobe Acrobat OCR',
    'AWS Textract',
    'Azure Computer Vision',
    'Google Cloud Vision API',
    'OCR.space',
    'PaddleOCR',
    'Tesseract OCR'
  ];

  const mappingProviders = [
    'Apple Maps (iOS)',
    'Bing Maps',
    'Carto',
    'Google Maps Platform',
    'HERE Maps',
    'MapTiler',
    'Mapbox',
    'OpenStreetMap',
    'TomTom'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">AI</h2>
        <p className="text-gray-600">Choose your preferred AI and machine learning services</p>
      </div>

      {/* Yellow info box with Save & Skip button */}
      <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 flex flex-col md:flex-row md:items-center md:justify-between">
        <span className="text-yellow-700 text-sm mb-2 md:mb-0">If this section does not apply,.. skip it.</span>
        <button
          onClick={skipPage}
          className="px-4 py-2 bg-orange-500 text-white rounded-md hover:bg-orange-600 transition-colors w-full md:w-auto"
        >
          Save & Skip
        </button>
      </div>

      {/* Mapping Provider */}
      <div className="bg-white p-6 rounded-lg border">
