import React, { useState, useEffect } from 'react';
import { MultiStageFormData, FormPageConfig } from '../types/FormTypes';
import DeveloperInfoPage from './pages/DeveloperInfoPage';
import CompanyInfoPage from './pages/CompanyInfoPage';
import DefaultPreferencesPage from './pages/DefaultPreferencesPage';
import EcommercePaymentsPage from './pages/EcommercePaymentsPage';
import {
  FileStorageEmailPage,
  MapsGeolocationPage,
  SecurityAuthPage,
  CommunicationAPIsPage,
  DnsConfigurationPage,
  TechStackPage,
  CloudInfrastructurePage,
  StylingDesignPage,
  FinancialAPIsPage,
  AIIntegrationPage,
  AppConfigurationPage
} from './pages/PageTemplate';

const FORM_PAGES: FormPageConfig[] = [
  { id: 1, title: '🧑‍💻 Developer Account Information', description: 'Personal developer details', required: true, canSkip: false },
  { id: 2, title: '🏢 Company Information', description: 'Business or organization details', required: true, canSkip: false },
  { id: 3, title: '🧰 Default Preferences', description: 'Development environment defaults', required: true, canSkip: false },
  { id: 4, title: '💳 E-commerce & Payments', description: 'Payment and commerce setup', required: false, canSkip: true },
  { id: 5, title: '📁 File Storage & Email', description: 'Storage and communication services', required: true, canSkip: false },
  { id: 6, title: '🗺️ Maps & Geolocation', description: 'Location-based services', required: false, canSkip: true },
  { id: 7, title: '🔒 Security & Authentication', description: 'Authentication and security preferences', required: true, canSkip: false },
  { id: 8, title: '📡 Communication & APIs', description: 'Communication and integration services', required: false, canSkip: true },
  { id: 9, title: '🌐 DNS, CDN & SSL', description: 'Domain and network configuration', required: true, canSkip: false },
  { id: 10, title: '⚙️ Tech Stack & Languages', description: 'Programming languages and frameworks', required: true, canSkip: false },
  { id: 11, title: '☁️ Cloud & Infrastructure', description: 'Hosting and deployment preferences', required: true, canSkip: false },
  { id: 12, title: '🎨 Styling & Design', description: 'UI/UX and design preferences', required: true, canSkip: false },
  { id: 13, title: '💰 Financial APIs', description: 'Banking and investment services', required: false, canSkip: true },
  { id: 14, title: '🤖 AI Integration & Analytics', description: 'AI services and analytics setup', required: false, canSkip: true },
  { id: 15, title: '📱 App Configuration', description: 'App-specific settings and target audience', required: true, canSkip: false }
];

const STORAGE_KEY = 'multiStageFormData';

export default function MultiStageForm() {
  const [formData, setFormData] = useState<MultiStageFormData>(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        return {
          ...parsed,
          completedPages: new Set(parsed.completedPages || [])
        };
      } catch (error) {
        console.error('Error parsing saved form data:', error);
      }
    }

    return {
      currentPage: 1,
      completedPages: new Set<number>(),
      developerInfo: {},
      companyInfo: {},
      defaultPreferences: {},
      ecommercePayments: {},
      fileStorageEmail: {},
      mapsGeolocation: {},
      securityAuth: {},
      communicationAPIs: {},
      dnsConfiguration: {},
      techStack: {},
      cloudInfrastructure: {},
      stylingDesign: {},
      financialAPIs: {},
      aiIntegration: {},
      appConfiguration: {}
    };
  });

  useEffect(() => {
    const dataToSave = {
      ...formData,
      completedPages: Array.from(formData.completedPages)
    };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(dataToSave));
  }, [formData]);

  const updateFormData = <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => {
    setFormData(prev => ({
      ...prev,
      [section]: { ...(prev[section] as object), ...data }
    }));
  };

  const goToPage = (pageNumber: number) => {
    if (pageNumber >= 1 && pageNumber <= FORM_PAGES.length) {
      setFormData(prev => ({ ...prev, currentPage: pageNumber }));
    }
  };

  const nextPage = () => {
    setFormData(prev => {
      const newCompletedPages = new Set(prev.completedPages);
      newCompletedPages.add(prev.currentPage);
      return {
        ...prev,
        completedPages: newCompletedPages,
        currentPage: Math.min(prev.currentPage + 1, FORM_PAGES.length)
      };
    });
  };

  const previousPage = () => {
    setFormData(prev => ({
      ...prev,
      currentPage: Math.max(prev.currentPage - 1, 1)
    }));
  };

  const skipPage = () => {
    const currentPageConfig = FORM_PAGES[formData.currentPage - 1];
    if (currentPageConfig.canSkip) {
      nextPage();
    }
  };

  const clearFormData = () => {
    localStorage.removeItem(STORAGE_KEY);
    setFormData({
      currentPage: 1,
      completedPages: new Set<number>(),
      developerInfo: {},
      companyInfo: {},
      defaultPreferences: {},
      ecommercePayments: {},
      fileStorageEmail: {},
      mapsGeolocation: {},
      securityAuth: {},
      communicationAPIs: {},
      dnsConfiguration: {},
      techStack: {},
      cloudInfrastructure: {},
      stylingDesign: {},
      financialAPIs: {},
      aiIntegration: {},
      appConfiguration: {}
    });
  };

  const getCurrentPageComponent = () => {
    const pageProps = {
      formData,
      updateFormData,
      nextPage,
      previousPage,
      skipPage
    };

    switch (formData.currentPage) {
      case 1: return <DeveloperInfoPage {...pageProps} />;
      case 2: return <CompanyInfoPage {...pageProps} />;
      case 3: return <DefaultPreferencesPage {...pageProps} />;
      case 4: return <EcommercePaymentsPage {...pageProps} />;
      case 5: return <FileStorageEmailPage {...pageProps} />;
      case 6: return <MapsGeolocationPage {...pageProps} />;
      case 7: return <SecurityAuthPage {...pageProps} />;
      case 8: return <CommunicationAPIsPage {...pageProps} />;
      case 9: return <DnsConfigurationPage {...pageProps} />;
      case 10: return <TechStackPage {...pageProps} />;
      case 11: return <CloudInfrastructurePage {...pageProps} />;
      case 12: return <StylingDesignPage {...pageProps} />;
      case 13: return <FinancialAPIsPage {...pageProps} />;
      case 14: return <AIIntegrationPage {...pageProps} />;
      case 15: return <AppConfigurationPage {...pageProps} />;
      default: return <div>Page not found</div>;
    }
  };

  const currentPageConfig = FORM_PAGES[formData.currentPage - 1];
  const progress = (formData.completedPages.size / FORM_PAGES.length) * 100;

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-4xl mx-auto px-4 py-8">
        <div className="bg-white rounded-lg shadow-lg overflow-hidden">
          <div className="bg-blue-600 text-white p-6">
            <div className="flex justify-between items-center mb-4">
              <h1 className="text-2xl font-bold">Multi-Stage Project Setup</h1>
              <button
                onClick={clearFormData}
                className="text-blue-200 hover:text-white text-sm underline"
              >
                Clear All Data
              </button>
            </div>

            <div className="mb-4">
              <div className="flex justify-between text-sm mb-2">
                <span>Progress: {formData.completedPages.size} of {FORM_PAGES.length} pages completed</span>
                <span>{Math.round(progress)}%</span>
              </div>
              <div className="w-full bg-blue-500 rounded-full h-2">
                <div 
                  className="bg-white h-2 rounded-full transition-all duration-300"
                  style={{ width: `${progress}%` }}
                />
              </div>
            </div>

            <div className="flex items-center gap-4">
              <span className="text-sm opacity-80">Page {formData.currentPage} of {FORM_PAGES.length}</span>
              <h2 className="text-xl font-semibold">{currentPageConfig.title}</h2>
            </div>
            <p className="text-blue-100 mt-2">{currentPageConfig.description}</p>
          </div>

          <div className="border-b bg-gray-50 p-4">
            <div className="flex gap-2 overflow-x-auto">
              {FORM_PAGES.map((page) => (
                <button
                  key={page.id}
                  onClick={() => goToPage(page.id)}
                  className={`
                    flex-shrink-0 px-3 py-1 text-xs rounded-full border transition-colors
                    ${formData.currentPage === page.id 
                      ? 'bg-blue-600 text-white border-blue-600' 
                      : formData.completedPages.has(page.id)
                        ? 'bg-green-100 text-green-800 border-green-300 hover:bg-green-200'
                        : 'bg-white text-gray-600 border-gray-300 hover:bg-gray-100'
                    }
                  `}
                >
                  {page.id}. {page.title.replace(/[^\w\s]/gi, '').trim()}
                </button>
              ))}
            </div>
          </div>

          <div className="p-6">
            {getCurrentPageComponent()}
          </div>

          <div className="bg-gray-50 px-6 py-4 flex justify-between items-center border-t">
            <button
              onClick={previousPage}
              disabled={formData.currentPage === 1}
              className={`
                px-4 py-2 rounded-md font-medium transition-colors
                ${formData.currentPage === 1
                  ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  : 'bg-gray-600 text-white hover:bg-gray-700'
                }
              `}
            >
              Previous
            </button>

            <div className="flex gap-2">
              {currentPageConfig.canSkip && (
                <button
                  onClick={skipPage}
                  className="px-4 py-2 rounded-md font-medium bg-yellow-500 text-white hover:bg-yellow-600 transition-colors"
                >
                  Save & Skip Page
                </button>
              )}

              <button
                onClick={nextPage}
                className="px-4 py-2 rounded-md font-medium bg-blue-600 text-white hover:bg-blue-700 transition-colors"
              >
                {formData.currentPage === FORM_PAGES.length ? 'Complete Setup' : 'Next Page'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
