import React from 'react';
import { MultiStageFormData, FileStorageEmail } from '../../types/FormTypes';

interface Props {
  formData: MultiStageFormData;
  updateFormData: <K extends keyof MultiStageFormData>(
    section: K,
    data: Partial<MultiStageFormData[K]>
  ) => void;
  nextPage: () => void;
  previousPage: () => void;
  skipPage: () => void;
}

export default function FileStorageEmailPage({ formData, updateFormData }: Props) {
  const data = formData.fileStorageEmail;

  const handleInputChange = (field: keyof FileStorageEmail, value: string) => {
    updateFormData('fileStorageEmail', { [field]: value });
  };

  const fileStorageOptions = [
    'Amazon S3 (Simple Storage Service)',
    'Google Cloud Storage',
    'Microsoft Azure Blob Storage',
    'Cloudinary',
    'Firebase Storage',
    'Supabase Storage',
    'DigitalOcean Spaces',
    'MinIO'
  ];

  const emailProviders = [
    'SendGrid',
    'Mailgun',
    'Amazon SES',
    'Postmark',
    'Mailchimp',
    'ConvertKit',
    'Resend',
    'Brevo (formerly Sendinblue)'
  ];

  return (
    <div className="space-y-8">
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900 mb-2">File Storage & Email</h2>
        <p className="text-gray-600">Choose your preferred file storage and email services</p>
      </div>

      {/* File Storage */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">File Storage Services</h3>
        <p className="text-gray-600 mb-4">Select your preferred file storage service:</p>

        <div className="space-y-3">
          {fileStorageOptions.map((service) => (
            <label key={service} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="fileStorage"
                value={service}
                checked={data.fileStorage === service}
                onChange={(e) => handleInputChange('fileStorage', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{service}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Email Provider */}
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold mb-4">Email Service Provider</h3>
        <p className="text-gray-600 mb-4">Select your preferred email service provider:</p>

        <div className="space-y-3">
          {emailProviders.map((provider) => (
            <label key={provider} className="flex items-center space-x-3 cursor-pointer">
              <input
                type="radio"
                name="emailProvider"
                value={provider}
                checked={data.emailProvider === provider}
                onChange={(e) => handleInputChange('emailProvider', e.target.value)}
                className="text-blue-600 focus:ring-blue-500"
              />
              <span className="text-gray-700">{provider}</span>
            </label>
          ))}
        </div>
      </div>

      {/* Service Descriptions */}
      <div className="bg-blue-50 p-6 rounded-lg border border-blue-200">
        <h3 className="text-lg font-semibold mb-4 text-blue-900">Service Information</h3>
        <div className="space-y-4">
          <div>
            <h4 className="font-medium text-blue-800">File Storage Services</h4>
            <p className="text-blue-700 text-sm">Cloud-based storage solutions for storing and serving files, images, and other assets for your application.</p>
          </div>
          <div>
            <h4 className="font-medium text-blue-800">Email Service Providers</h4>
            <p className="text-blue-700 text-sm">Services for sending transactional emails, marketing campaigns, and automated notifications to your users.</p>
          </div>
        </div>
      </div>
    </div>
  );
}
